INSERT INTO `question` VALUES (1, 1, '下列哪种营养素是宏量营养素且其必需氨基酸对于成人有8种，婴儿有9种？', '{\"A\": \"脂肪\", \"B\": \"碳水化合物\", \"C\": \"蛋白质\", \"D\": \"维生素\"}', 1, 'C', '蛋白质的必需氨基酸分类包括成人8种、婴儿9种。', 5, '2025-08-21 22:15:31');
INSERT INTO `question` VALUES (2, 1, '以下哪种脂肪酸是必需脂肪酸？', '{\"A\": \"硬脂酸\", \"B\": \"亚油酸\", \"C\": \"油酸\", \"D\": \"棕榈酸\"}', 1, 'B', '亚油酸与α-亚麻酸为必需脂肪酸。', 5, '2025-08-21 22:15:31');
INSERT INTO `question` VALUES (3, 1, '碳水化合物的血糖生成指数（GI）主要用于什么目的？', '{\"A\": \"衡量食物的热量\", \"B\": \"评估食物的升糖能力\", \"C\": \"测定食物的蛋白质含量\", \"D\": \"判断食物的脂肪含量\"}', 1, 'B', '碳水化合物的血糖生成指数（GI）用于评估食物的升糖能力。', 5, '2025-08-21 22:15:31');
INSERT INTO `question` VALUES (4, 1, '基础代谢率（BMR）的估算方法不包括以下哪一项？', '{\"A\": \"Harris-Benedict公式\", \"B\": \"直接测热法\", \"C\": \"体重指数法\", \"D\": \"Mifflin-St Jeor公式\"}', 1, 'C', '基础代谢率（BMR）的估算常用Harris-Benedict公式或Mifflin-St Jeor公式，直接测热法为实验室方法，体重指数法用于评估肥胖程度。', 5, '2025-08-21 22:15:31');
INSERT INTO `question` VALUES (5, 1, '维生素D的强化剂量上限在2025版GB14880中调整为多少？', '{\"A\": \"5μg/100g\", \"B\": \"10μg/100g\", \"C\": \"15μg/100g\", \"D\": \"20μg/100g\"}', 1, 'C', '2025版GB14880中维生素D强化剂量上限调整为15μg/100g。', 5, '2025-08-21 22:15:31');
INSERT INTO `question` VALUES (6, 1, '蛋白质变性主要影响其什么特性？', '{\"A\": \"氨基酸组成\", \"B\": \"空间结构\", \"C\": \"分子量\", \"D\": \"溶解度\"}', 1, 'B', '蛋白质变性主要影响其空间结构，导致功能改变。', 5, '2025-08-21 22:15:31');
INSERT INTO `question` VALUES (7, 1, '糖酵解过程的关键酶不包括以下哪一项？', '{\"A\": \"己糖激酶\", \"B\": \"磷酸果糖激酶-1\", \"C\": \"丙酮酸激酶\", \"D\": \"柠檬酸合酶\"}', 1, 'D', '柠檬酸合酶是三羧酸循环的关键酶，非糖酵解过程。', 5, '2025-08-21 22:15:31');
INSERT INTO `question` VALUES (8, 1, 'B族维生素如硫胺素、核黄素主要作为哪种物质的辅酶？', '{\"A\": \"NAD+\", \"B\": \"FAD\", \"C\": \"ATP\", \"D\": \"CoA\"}', 2, 'AB', '硫胺素（B1）作为NAD+的辅酶前体，核黄素（B2）作为FAD的辅酶前体，均参与能量代谢。', 5, '2025-08-21 22:15:31');
INSERT INTO `question` VALUES (9, 1, '《中国食物成分表》主要用于什么目的？', '{\"A\": \"评估食物热量\", \"B\": \"查询食物营养成分\", \"C\": \"测定食物水分含量\", \"D\": \"判断食物新鲜度\"}', 1, 'B', '《中国食物成分表》用于查询食物的营养成分。', 5, '2025-08-21 22:15:31');
INSERT INTO `question` VALUES (10, 1, '以下哪种措施可以有效预防食源性疾病如沙门氏菌感染？', '{\"A\": \"高温烹饪\", \"B\": \"生食海鲜\", \"C\": \"使用过期食材\", \"D\": \"不洗手直接处理食物\"}', 1, 'A', '高温烹饪可以有效杀灭沙门氏菌等病原体，预防食源性疾病。', 5, '2025-08-21 22:15:31');
INSERT INTO `question` VALUES (11, 1, '高温油炸对脂肪酸结构的主要影响是什么？', '{\"A\": \"增加不饱和脂肪酸含量\", \"B\": \"生成反式脂肪酸\", \"C\": \"降低脂肪酸饱和度\", \"D\": \"无影响\"}', 1, 'B', '高温油炸会促使不饱和脂肪酸生成反式脂肪酸。', 5, '2025-08-21 22:15:31');
INSERT INTO `question` VALUES (12, 1, '巴氏杀菌对维生素C的保留率有何影响？', '{\"A\": \"完全保留\", \"B\": \"显著提高\", \"C\": \"部分保留\", \"D\": \"完全破坏\"}', 1, 'C', '巴氏杀菌能部分保留维生素C，但会有一定损失。', 5, '2025-08-21 22:15:31');
INSERT INTO `question` VALUES (13, 1, 'BMI的计算公式是什么？', '{\"A\": \"体重（kg）/身高（m）²\", \"B\": \"体重（kg）/身高（cm）²\", \"C\": \"身高（m）/体重（kg）²\", \"D\": \"身高（cm）/体重（kg）²\"}', 1, 'A', 'BMI=体重（kg）/身高（m）²，用于评估肥胖程度。', 5, '2025-08-21 22:15:31');
INSERT INTO `question` VALUES (14, 1, '孕妇每日叶酸补充量的推荐范围是多少？', '{\"A\": \"100-200μg/d\", \"B\": \"200-400μg/d\", \"C\": \"400-800μg/d\", \"D\": \"800-1000μg/d\"}', 1, 'C', '孕妇叶酸补充量推荐为400-800μg/d。', 5, '2025-08-21 22:15:31');
INSERT INTO `question` VALUES (15, 1, '糖尿病患者每餐碳水化合物占比的推荐范围是多少？', '{\"A\": \"10%-20%\", \"B\": \"20%-30%\", \"C\": \"30%-40%\", \"D\": \"40%-50%\"}', 1, 'B', '糖尿病患者每餐碳水化合物占比推荐为20%-30%。', 5, '2025-08-21 22:15:31');
INSERT INTO `question` VALUES (16, 1, '高血压的DASH饮食中，钾与钠的比值应达到多少？', '{\"A\": \"1:1\", \"B\": \"2:1\", \"C\": \"3:1\", \"D\": \"4:1\"}', 1, 'C', '高血压的DASH饮食中，钾与钠的比值应≥3:1。^[16]^', 5, '2025-08-21 22:15:31');
INSERT INTO `question` VALUES (17, 1, '肠内营养制剂选择时，肾病患者应使用哪种配方？', '{\"A\": \"高蛋白配方\", \"B\": \"低蛋白配方\", \"C\": \"高脂肪配方\", \"D\": \"低脂肪配方\"}', 1, 'B', '肾病患者应使用低蛋白配方的肠内营养制剂。^[17]^', 5, '2025-08-21 22:15:31');
INSERT INTO `question` VALUES (18, 1, '特殊医学用途食品（FSMP）中，肿瘤患者专用配方的能量密度是多少？', '{\"A\": \"0.5-1.0kcal/mL\", \"B\": \"1.0-1.5kcal/mL\", \"C\": \"1.5-2.0kcal/mL\", \"D\": \"2.0-2.5kcal/mL\"}', 1, 'C', '肿瘤患者专用配方的能量密度为1.5-2.0kcal/mL。^[18]^', 5, '2025-08-21 22:15:31');
INSERT INTO `question` VALUES (19, 1, '术后康复期患者的蛋白质需求范围是多少？', '{\"A\": \"0.8-1.2g/kg/d\", \"B\": \"1.2-2.0g/kg/d\", \"C\": \"2.0-2.5g/kg/d\", \"D\": \"2.5-3.0g/kg/d\"}', 1, 'B', '术后康复期患者的蛋白质需求为1.2-2.0g/kg/d。^[19]^', 5, '2025-08-21 22:15:31');
INSERT INTO `question` VALUES (20, 1, '耐力项目运动员赛前应采用的碳水化合物补充策略是什么？', '{\"A\": \"低糖饮食\", \"B\": \"高糖饮食\", \"C\": \"无糖饮食\", \"D\": \"随意饮食\"}', 1, 'B', '耐力项目运动员赛前应采用高糖饮食（糖原负荷法）以补充能量。^[20]^', 5, '2025-08-21 22:15:31');
INSERT INTO `question` VALUES (21, 1, '力量训练运动员的蛋白质摄入标准是多少？', '{\"A\": \"0.8-1.2g/kg/d\", \"B\": \"1.2-1.6g/kg/d\", \"C\": \"1.6-2.2g/kg/d\", \"D\": \"2.2-2.8g/kg/d\"}', 1, 'C', '力量训练运动员的蛋白质摄入标准为1.6-2.2g/kg/d。^[21]^', 5, '2025-08-21 22:15:31');
INSERT INTO `question` VALUES (22, 1, '高温作业者每小时应补水多少毫升，并补充多少克钠？', '{\"A\": \"200-500mL，0.5-1g\", \"B\": \"500-1000mL，1-2g\", \"C\": \"1000-1500mL，2-3g\", \"D\": \"1500-2000mL，3-4g\"}', 1, 'B', '高温作业者每小时应补水500-1000mL，并补充钠1-2g。^[22]^', 5, '2025-08-21 22:15:31');
INSERT INTO `question` VALUES (23, 1, '2025版中国居民膳食指南新增内容中，植物性蛋白推荐占比是多少？', '{\"A\": \"30%-35%\", \"B\": \"35%-40%\", \"C\": \"45%-50%\", \"D\": \"50%-55%\"}', 1, 'C', '2025版中国居民膳食指南新增植物性蛋白推荐占比45%-50%。^[23]^', 5, '2025-08-21 22:15:31');
INSERT INTO `question` VALUES (24, 1, 'DRIs中，EAR、RNI、UL分别代表什么？', '{\"A\": \"估计平均需求量、推荐摄入量、可耐受最高摄入量\", \"B\": \"推荐摄入量、估计平均需求量、适宜摄入量\", \"C\": \"可耐受最高摄入量、推荐摄入量、估计平均需求量\", \"D\": \"适宜摄入量、可耐受最高摄入量、估计平均需求量\"}', 1, 'A', 'DRIs中，EAR为估计平均需求量，RNI为推荐摄入量，UL为可耐受最高摄入量。^[24]^', 5, '2025-08-21 22:15:31');
INSERT INTO `question` VALUES (25, 1, '《食品安全法》中关于营养标签的强制标示内容不包括以下哪一项？', '{\"A\": \"能量\", \"B\": \"蛋白质\", \"C\": \"维生素\", \"D\": \"钠\"}', 1, 'C', '《食品安全法》强制标示内容包括能量、蛋白质、脂肪、碳水化合物、钠，维生素非强制标示。^[25]^', 5, '2025-08-21 22:15:31');
INSERT INTO `question` VALUES (26, 1, '24小时膳食回顾法的实施步骤中，不包括以下哪一项？', '{\"A\": \"记录食物种类和数量\", \"B\": \"询问进食时间和地点\", \"C\": \"测定食物生重和熟重\", \"D\": \"分析营养素摄入量\"}', 1, 'C', '24小时膳食回顾法主要记录食物种类和数量、进食时间和地点，后分析营养素摄入量，不涉及测定食物生重和熟重。^[26]^', 5, '2025-08-21 22:15:31');
INSERT INTO `question` VALUES (27, 1, '智能膳食评估工具如AI图像识别主要用于什么目的？', '{\"A\": \"测定食物热量\", \"B\": \"评估营养素摄入\", \"C\": \"记录进食时间\", \"D\": \"判断食物新鲜度\"}', 1, 'B', '智能膳食评估工具如AI图像识别主要用于评估营养素摄入。^[27]^', 5, '2025-08-21 22:15:31');
INSERT INTO `question` VALUES (28, 1, '老年人骨质疏松项目的效果评估指标主要是什么？', '{\"A\": \"血压变化\", \"B\": \"骨密度变化\", \"C\": \"血糖变化\", \"D\": \"血脂变化\"}', 1, 'B', '老年人骨质疏松项目的效果评估指标主要为骨密度变化。^[28]^', 5, '2025-08-21 22:15:31');
INSERT INTO `question` VALUES (29, 1, '营养教育短视频的制作要点中，不包括以下哪一项？', '{\"A\": \"时长≤3分钟\", \"B\": \"突出核心信息\", \"C\": \"使用复杂术语\", \"D\": \"视觉效果吸引人\"}', 1, 'C', '营养教育短视频应时长≤3分钟、突出核心信息、视觉效果吸引人，避免使用复杂术语。^[29]^', 5, '2025-08-21 22:15:31');
INSERT INTO `question` VALUES (30, 1, '植物基饮食对碳排放的主要影响是什么？', '{\"A\": \"增加碳排放\", \"B\": \"无影响\", \"C\": \"减少碳排放\", \"D\": \"依赖饮食类型\"}', 1, 'C', '植物基饮食相比动物性饮食能显著减少碳排放。^[30]^', 5, '2025-08-21 22:15:31');
INSERT INTO `question` VALUES (31, 1, '重金属如铅、汞对营养素吸收的主要拮抗作用是什么？', '{\"A\": \"促进钙吸收\", \"B\": \"抑制铁吸收\", \"C\": \"提高维生素D活性\", \"D\": \"无影响\"}', 1, 'B', '重金属如铅、汞主要抑制铁等营养素的吸收。^[31]^', 5, '2025-08-21 22:15:31');
INSERT INTO `question` VALUES (32, 1, '团餐能量分配中，午餐的能量占比范围是多少？', '{\"A\": \"20%-25%\", \"B\": \"25%-30%\", \"C\": \"30%-40%\", \"D\": \"40%-50%\"}', 1, 'C', '团餐能量分配中，午餐的能量占比范围为30%-40%。^[32]^', 5, '2025-08-21 22:15:31');
INSERT INTO `question` VALUES (33, 1, '成人必需氨基酸的种类为？', '{\"A\": \"7种\", \"B\": \"8种\", \"C\": \"9种\", \"D\": \"10种\"}', 1, 'B', '成人必需氨基酸为8种，婴儿因需要组氨酸，故为9种。', 5, '2025-08-21 22:15:31');
INSERT INTO `question` VALUES (34, 1, '下列属于必需脂肪酸的是？', '{\"A\": \"油酸\", \"B\": \"亚油酸\", \"C\": \"棕榈酸\", \"D\": \"硬脂酸\"}', 1, 'B', '必需脂肪酸包括亚油酸与α-亚麻酸，是人体不能合成需从食物中获取的脂肪酸。', 5, '2025-08-21 22:15:31');
INSERT INTO `question` VALUES (35, 1, '三大产能营养素中，碳水化合物的供能比例为？', '{\"A\": \"10%-15%\", \"B\": \"20%-30%\", \"C\": \"55%-65%\", \"D\": \"70%-80%\"}', 1, 'C', '碳水化合物供能占比55%-65%，脂肪20%-30%，蛋白质10%-15%。', 5, '2025-08-21 22:15:31');
INSERT INTO `question` VALUES (36, 1, '2025版GB14880中，维生素D强化剂量上限调整为？', '{\"A\": \"5μg/100g\", \"B\": \"10μg/100g\", \"C\": \"15μg/100g\", \"D\": \"20μg/100g\"}', 1, 'C', '2025版GB14880将维生素D强化剂量上限增至15μg/100g。', 5, '2025-08-21 22:15:31');
INSERT INTO `question` VALUES (37, 1, '蛋白质变性后不会发生的变化是？', '{\"A\": \"空间结构破坏\", \"B\": \"生物活性丧失\", \"C\": \"肽键断裂\", \"D\": \"溶解度降低\"}', 1, 'C', '蛋白质变性是空间结构破坏，肽键未断裂，会导致生物活性丧失、溶解度降低等。', 5, '2025-08-21 22:15:31');
INSERT INTO `question` VALUES (38, 1, '糖酵解的关键酶包括？', '{\"A\": \"己糖激酶\", \"B\": \"柠檬酸合酶\", \"C\": \"丙酮酸脱氢酶\", \"D\": \"异柠檬酸脱氢酶\"}', 1, 'A', '糖酵解的关键酶有己糖激酶、磷酸果糖激酶-1、丙酮酸激酶；柠檬酸合酶等是三羧酸循环的关键酶。', 5, '2025-08-21 22:15:31');
INSERT INTO `question` VALUES (39, 1, 'BMI的计算公式为？', '{\"A\": \"体重(kg)/身高(m)\", \"B\": \"体重(kg)/身高(m)²\", \"C\": \"身高(m)/体重(kg)\", \"D\": \"身高(m)²/体重(kg)\"}', 1, 'B', 'BMI（身体质量指数）=体重(kg)÷身高(m)的平方，是评估营养状况的常用工具。', 5, '2025-08-21 22:15:31');
INSERT INTO `question` VALUES (40, 1, '孕妇每日叶酸的补充量应为？', '{\"A\": \"100-200μg\", \"B\": \"200-300μg\", \"C\": \"400-800μg\", \"D\": \"1000-1200μg\"}', 1, 'C', '孕妇需补充叶酸400-800μg/d，以预防胎儿神经管畸形。', 5, '2025-08-21 22:15:31');
INSERT INTO `question` VALUES (41, 1, '糖尿病患者每餐碳水化合物供能占比宜为？', '{\"A\": \"10%-20%\", \"B\": \"20%-30%\", \"C\": \"30%-40%\", \"D\": \"40%-50%\"}', 1, 'B', '糖尿病管理中，每餐碳水化合物供能占比建议为20%-30%，以稳定血糖。', 5, '2025-08-21 22:15:31');
INSERT INTO `question` VALUES (42, 1, '高血压患者采用DASH饮食时，钾/钠比值应≥？', '{\"A\": \"1:1\", \"B\": \"2:1\", \"C\": \"3:1\", \"D\": \"4:1\"}', 1, 'C', 'DASH饮食强调高钾低钠，钾/钠比值≥3:1，有助于降低血压。', 5, '2025-08-21 22:15:31');
INSERT INTO `question` VALUES (43, 1, '肾病患者肠内营养制剂宜选择？', '{\"A\": \"高蛋白配方\", \"B\": \"低蛋白配方\", \"C\": \"高糖配方\", \"D\": \"高脂配方\"}', 1, 'B', '肾病患者需限制蛋白质摄入以减轻肾脏负担，故选择低蛋白配方肠内营养制剂。', 5, '2025-08-21 22:15:31');
