INSERT INTO `question` VALUES (46, 1, '力量训练运动员的蛋白质摄入标准为？', '{\"A\": \"0.8-1.0g/kg/d\", \"B\": \"1.2-1.5g/kg/d\", \"C\": \"1.6-2.2g/kg/d\", \"D\": \"2.5-3.0g/kg/d\"}', 1, 'C', '力量训练运动员需较高蛋白质促进肌肉合成，摄入标准为1.6-2.2g/kg/d。', 5, '2025-08-21 22:15:31');
INSERT INTO `question` VALUES (47, 1, '高温作业者每小时补水量应为？', '{\"A\": \"100-300mL\", \"B\": \"300-500mL\", \"C\": \"500-1000mL\", \"D\": \"1000-1500mL\"}', 1, 'C', '高温作业者出汗多，每小时需补水500-1000mL，同时补充1-2g钠。', 5, '2025-08-21 22:15:31');
INSERT INTO `question` VALUES (48, 1, '2025版中国居民膳食指南新增的内容包括？', '{\"A\": \"植物性蛋白推荐占比45%-50%\", \"B\": \"每日12种食物搭配\", \"C\": \"少吃加工肉类\", \"D\": \"增加全谷物摄入\"}', 2, 'AB', '2025版膳食指南新增植物性蛋白占比45%-50%、每日12种食物搭配等内容。', 5, '2025-08-21 22:15:31');
INSERT INTO `question` VALUES (49, 1, '女性铁的RNI（推荐摄入量）为？', '{\"A\": \"10mg/d\", \"B\": \"15mg/d\", \"C\": \"20mg/d\", \"D\": \"25mg/d\"}', 1, 'C', 'DRIs中，女性铁的推荐摄入量（RNI）为20mg/d，高于男性。', 5, '2025-08-21 22:15:31');
INSERT INTO `question` VALUES (50, 1, '常用的膳食调查方法有？', '{\"A\": \"24小时膳食回顾法\", \"B\": \"称重法\", \"C\": \"记账法\", \"D\": \"化学分析法\"}', 2, 'ABCD', '膳食调查方法包括24小时回顾法、称重法、记账法、化学分析法等，各有适用场景。', 5, '2025-08-21 22:15:31');
INSERT INTO `question` VALUES (51, 1, '社区干预中，智能膳食评估工具包括？', '{\"A\": \"AI图像识别\", \"B\": \"手机APP记录\", \"C\": \"食物频率问卷\", \"D\": \"人体成分分析仪\"}', 1, 'A', 'AI图像识别是智能膳食评估工具的一种，可通过图像分析食物种类和量。', 5, '2025-08-21 22:15:31');
INSERT INTO `question` VALUES (52, 1, '团餐能量分配中，早餐占比为？', '{\"A\": \"10%-15%\", \"B\": \"25%-30%\", \"C\": \"30%-40%\", \"D\": \"40%-50%\"}', 1, 'B', '团餐能量分配建议：早餐25%-30%，午餐30%-40%，晚餐30%-35%。', 5, '2025-08-21 22:15:31');
INSERT INTO `question` VALUES (53, 1, '下列烹饪方式中，对营养素保留最有利的是？', '{\"A\": \"油炸\", \"B\": \"烧烤\", \"C\": \"蒸\", \"D\": \"红烧\"}', 1, 'C', '蒸、煮、炖等方式温度较低且少用油，能较好保留营养素；油炸、烧烤易导致营养素破坏。', 5, '2025-08-21 22:15:31');
INSERT INTO `question` VALUES (54, 1, 'HACCP体系中的关键控制点（CCP）包括？', '{\"A\": \"冷链食品的温度监控\", \"B\": \"食材采购渠道\", \"C\": \"厨师健康状况\", \"D\": \"餐具清洗流程\"}', 1, 'A', '冷链食品的温度监控是HACCP体系中的关键控制点，直接影响食品安全性。', 5, '2025-08-21 22:15:31');
INSERT INTO `question` VALUES (55, 1, '低嘌呤饮食适用于哪种疾病患者？', '{\"A\": \"高血压\", \"B\": \"痛风\", \"C\": \"糖尿病\", \"D\": \"骨质疏松\"}', 1, 'B', '痛风与嘌呤代谢紊乱有关，低嘌呤饮食可减少尿酸生成，适合痛风患者。', 5, '2025-08-21 22:15:31');
INSERT INTO `question` VALUES (56, 1, '学生餐中，蛋白质供能比应为？', '{\"A\": \"8%-10%\", \"B\": \"12%-15%\", \"C\": \"15%-20%\", \"D\": \"20%-25%\"}', 1, 'B', '学生餐营养均衡标准中，蛋白质供能比建议为12%-15%，满足生长发育需求。', 5, '2025-08-21 22:15:31');
INSERT INTO `question` VALUES (57, 1, '注册营养师的职业守则包括？', '{\"A\": \"诚实守信\", \"B\": \"保护客户隐私\", \"C\": \"避免利益冲突\", \"D\": \"夸大营养补剂效果\"}', 2, 'ABC', '注册营养师职业守则包括诚实守信、保护隐私、避免利益冲突等，禁止夸大宣传。', 5, '2025-08-21 22:15:31');
INSERT INTO `question` VALUES (58, 1, '精准营养研究的内容包括？', '{\"A\": \"基因-营养互作\", \"B\": \"个性化营养干预\", \"C\": \"群体膳食指南制定\", \"D\": \"食品加工工艺优化\"}', 2, 'AB', '精准营养关注基因-营养互作（如MTHFR基因与叶酸代谢）及个性化干预方案设计。', 5, '2025-08-21 22:15:31');
INSERT INTO `question` VALUES (59, 1, '肠道菌群代谢产物中，对代谢性疾病有调控作用的是？', '{\"A\": \"短链脂肪酸\", \"B\": \"胆固醇\", \"C\": \"葡萄糖\", \"D\": \"甘油三酯\"}', 1, 'A', '肠道菌群产生的短链脂肪酸（如丁酸）可调控代谢性疾病，如肥胖、糖尿病等。', 5, '2025-08-21 22:15:31');
INSERT INTO `question` VALUES (60, 1, '脂溶性维生素包括？', '{\"A\": \"维生素A\", \"B\": \"维生素B1\", \"C\": \"维生素D\", \"D\": \"维生素K\"}', 2, 'ACD', '脂溶性维生素包括维生素A、D、E、K；维生素B族为水溶性维生素。', 5, '2025-08-21 22:15:31');
INSERT INTO `question` VALUES (61, 1, '食源性疾病的常见致病菌有？', '{\"A\": \"沙门氏菌\", \"B\": \"李斯特菌\", \"C\": \"乳酸菌\", \"D\": \"双歧杆菌\"}', 2, 'AB', '沙门氏菌、李斯特菌是常见食源性致病菌，乳酸菌、双歧杆菌为有益菌。', 5, '2025-08-21 22:15:31');
INSERT INTO `question` VALUES (62, 2, '下列哪种脂肪酸是必需脂肪酸？', '{\"A\": \"硬脂酸\", \"B\": \"亚油酸\", \"C\": \"油酸\", \"D\": \"棕榈酸\"}', 1, 'B', '亚油酸与α-亚麻酸为必需脂肪酸。', 5, '2025-08-21 22:35:39');
INSERT INTO `question` VALUES (63, 2, '计算基础代谢率（BMR）可使用以下哪种公式？', '{\"A\": \"Fleming公式\", \"B\": \"Harris-Benedict公式\", \"C\": \"Mifflin-St Jeor公式\", \"D\": \"Owen公式\"}', 1, 'B', '基础代谢率（BMR）的估算方法可使用Harris-Benedict公式。', 5, '2025-08-21 22:35:39');
