INSERT INTO `question` VALUES (66, 2, '以下哪种食物成分表常用于营养评估？', '{\"A\": \"美国食物成分表\", \"B\": \"中国食物成分表\", \"C\": \"日本食物成分表\", \"D\": \"欧洲食物成分表\"}', 1, 'B', '食物成分表的使用（如《中国食物成分表》）。', 5, '2025-08-21 22:35:39');
INSERT INTO `question` VALUES (67, 2, '高温油炸对脂肪酸结构的主要影响是什么？', '{\"A\": \"生成反式脂肪酸\", \"B\": \"增加不饱和脂肪酸含量\", \"C\": \"减少饱和脂肪酸含量\", \"D\": \"无影响\"}', 1, 'A', '高温油炸对脂肪酸结构的影响（反式脂肪酸生成）。', 5, '2025-08-21 22:35:39');
INSERT INTO `question` VALUES (68, 2, 'BMI的计算公式是什么？', '{\"A\": \"体重（kg）/身高（m）\", \"B\": \"体重（kg）/身高²（m²）\", \"C\": \"身高（m）/体重（kg）\", \"D\": \"身高²（m²）/体重（kg）\"}', 1, 'B', 'BMI为体重（kg）除以身高²（m²）。', 5, '2025-08-21 22:35:39');
INSERT INTO `question` VALUES (69, 2, '孕妇每日叶酸补充量的推荐范围是多少？', '{\"A\": \"100-200μg/d\", \"B\": \"200-400μg/d\", \"C\": \"400-800μg/d\", \"D\": \"800-1000μg/d\"}', 1, 'C', '孕妇叶酸补充量（400-800μg/d）。', 5, '2025-08-21 22:35:39');
INSERT INTO `question` VALUES (70, 2, '糖尿病患者的碳水化合物分配每餐应占多少比例？', '{\"A\": \"10%-15%\", \"B\": \"15%-20%\", \"C\": \"20%-30%\", \"D\": \"30%-40%\"}', 1, 'C', '糖尿病的碳水化合物分配（每餐占比20%-30%）。', 5, '2025-08-21 22:35:39');
INSERT INTO `question` VALUES (71, 2, '高血压DASH饮食中钾与钠的比值应达到多少？', '{\"A\": \"1:1\", \"B\": \"2:1\", \"C\": \"3:1\", \"D\": \"4:1\"}', 1, 'C', '高血压的DASH饮食（钾/钠比值≥3:1）。', 5, '2025-08-21 22:35:39');
INSERT INTO `question` VALUES (72, 2, '耐力项目运动员赛前常用的碳水化合物补充策略是什么？', '{\"A\": \"糖原负荷法\", \"B\": \"低糖饮食法\", \"C\": \"高脂饮食法\", \"D\": \"高蛋白饮食法\"}', 1, 'A', '耐力项目的碳水化合物补充策略（赛前糖原负荷法）。', 5, '2025-08-21 22:35:39');
INSERT INTO `question` VALUES (73, 2, '2025版中国居民膳食指南中植物性蛋白推荐占比是多少？', '{\"A\": \"20%-25%\", \"B\": \"30%-35%\", \"C\": \"45%-50%\", \"D\": \"55%-60%\"}', 1, 'C', '2025版新增内容（如植物性蛋白推荐占比45%-50%）。', 5, '2025-08-21 22:35:39');
