INSERT INTO `question` VALUES (75, 3, '下列属于水溶性维生素的是？', '{\"A\": \"维生素A\", \"B\": \"维生素D\", \"C\": \"维生素K\", \"D\": \"维生素C\"}', 1, 'D', '水溶性维生素包括B族维生素和维生素C，维生素A、D、K属于脂溶性维生素。', 5, '2025-08-21 22:36:09');
INSERT INTO `question` VALUES (76, 3, '下列哪种矿物质是血红蛋白的组成成分？', '{\"A\": \"钙\", \"B\": \"铁\", \"C\": \"锌\", \"D\": \"碘\"}', 1, 'B', '铁是血红蛋白的组成成分，参与氧气的运输。', 5, '2025-08-21 22:36:09');
INSERT INTO `question` VALUES (77, 3, '下列哪种维生素缺乏会导致夜盲症？', '{\"A\": \"维生素A\", \"B\": \"维生素B1\", \"C\": \"维生素C\", \"D\": \"维生素D\"}', 1, 'A', '维生素A缺乏会导致夜盲症，因其是视网膜中视紫红质的组成成分。', 5, '2025-08-21 22:36:09');
INSERT INTO `question` VALUES (78, 3, '下列哪种矿物质缺乏会导致佝偻病？', '{\"A\": \"钙\", \"B\": \"铁\", \"C\": \"锌\", \"D\": \"碘\"}', 1, 'A', '钙缺乏会导致佝偻病（儿童）和骨质疏松症（成人）。', 5, '2025-08-21 22:36:09');
INSERT INTO `question` VALUES (79, 3, '下列哪种维生素缺乏会导致坏血病？', '{\"A\": \"维生素A\", \"B\": \"维生素B1\", \"C\": \"维生素C\", \"D\": \"维生素D\"}', 1, 'C', '维生素C缺乏会导致坏血病，因其参与胶原蛋白的合成。', 5, '2025-08-21 22:36:09');
INSERT INTO `question` VALUES (80, 3, '下列哪种矿物质缺乏会导致甲状腺肿大？', '{\"A\": \"钙\", \"B\": \"铁\", \"C\": \"锌\", \"D\": \"碘\"}', 1, 'D', '碘缺乏会导致甲状腺肿大，因其是甲状腺激素的组成成分。', 5, '2025-08-21 22:36:09');
INSERT INTO `question` VALUES (81, 3, '下列哪种维生素缺乏会导致脚气病？', '{\"A\": \"维生素A\", \"B\": \"维生素B1\", \"C\": \"维生素C\", \"D\": \"维生素D\"}', 1, 'B', '维生素B1（硫胺素）缺乏会导致脚气病，影响能量代谢。', 5, '2025-08-21 22:36:09');
INSERT INTO `question` VALUES (82, 3, '下列哪种矿物质缺乏会导致贫血？', '{\"A\": \"钙\", \"B\": \"铁\", \"C\": \"锌\", \"D\": \"碘\"}', 1, 'B', '铁缺乏会导致缺铁性贫血，因其是血红蛋白的组成成分。', 5, '2025-08-21 22:36:09');
INSERT INTO `question` VALUES (83, 3, '下列哪种维生素缺乏会导致口角炎？', '{\"A\": \"维生素A\", \"B\": \"维生素B2\", \"C\": \"维生素C\", \"D\": \"维生素D\"}', 1, 'B', '维生素B2（核黄素）缺乏会导致口角炎、唇炎等。', 5, '2025-08-21 22:36:09');
INSERT INTO `question` VALUES (84, 3, '下列哪种矿物质缺乏会导致克山病？', '{\"A\": \"钙\", \"B\": \"铁\", \"C\": \"锌\", \"D\": \"硒\"}', 1, 'D', '硒缺乏会导致克山病（地方性心肌病），因其参与抗氧化酶的合成。', 5, '2025-08-21 22:36:09');
INSERT INTO `question` VALUES (85, 3, '下列哪种维生素缺乏会导致巨幼红细胞性贫血？', '{\"A\": \"维生素A\", \"B\": \"维生素B12\", \"C\": \"维生素C\", \"D\": \"维生素D\"}', 1, 'B', '维生素B12或叶酸缺乏会导致巨幼红细胞性贫血，因其参与DNA合成。', 5, '2025-08-21 22:36:09');
INSERT INTO `question` VALUES (86, 3, '下列哪种矿物质缺乏会导致骨质疏松症？', '{\"A\": \"钙\", \"B\": \"铁\", \"C\": \"锌\", \"D\": \"碘\"}', 1, 'A', '钙缺乏会导致骨质疏松症，尤其老年人因骨吸收大于骨形成。', 5, '2025-08-21 22:36:09');
INSERT INTO `question` VALUES (87, 3, '下列哪种维生素缺乏会导致干眼症？', '{\"A\": \"维生素A\", \"B\": \"维生素B1\", \"C\": \"维生素C\", \"D\": \"维生素D\"}', 1, 'A', '维生素A缺乏会导致干眼症，严重时可致夜盲症或失明。', 5, '2025-08-21 22:36:09');
INSERT INTO `question` VALUES (88, 3, '下列哪种矿物质缺乏会导致肌肉痉挛？', '{\"A\": \"钙\", \"B\": \"铁\", \"C\": \"锌\", \"D\": \"碘\"}', 1, 'A', '钙缺乏会导致神经肌肉兴奋性增高，引发肌肉痉挛。', 5, '2025-08-21 22:36:09');
INSERT INTO `question` VALUES (89, 3, '下列哪种维生素缺乏会导致糙皮病？', '{\"A\": \"维生素A\", \"B\": \"维生素B3\", \"C\": \"维生素C\", \"D\": \"维生素D\"}', 1, 'B', '维生素B3（烟酸）缺乏会导致糙皮病，表现为皮炎、腹泻、痴呆。', 5, '2025-08-21 22:36:09');
INSERT INTO `question` VALUES (90, 4, '1. 以下哪种营养素在肠外营养中不需要常规补充？', '{\"A\": \"葡萄糖\", \"B\": \"脂肪\", \"C\": \"氨基酸\", \"D\": \"维生素K\"}', 1, 'D', '肠外营养中通常需要补充葡萄糖、脂肪、氨基酸等宏量营养素，以及电解质、维生素和微量元素。维生素K一般可通过肠道菌群合成或食物摄入，在肠外营养中不需常规补充，除非有特殊情况。', 5, '2025-08-21 22:36:17');
INSERT INTO `question` VALUES (91, 4, '2. 肠内营养支持的禁忌症包括以下哪些？', '{\"A\": \"肠梗阻\", \"B\": \"严重腹泻\", \"C\": \"短肠综合征早期\", \"D\": \"胃肠道功能基本正常\"}', 2, 'ABC', '肠内营养支持的禁忌症包括肠梗阻、严重腹泻、短肠综合征早期等胃肠道功能障碍或不能耐受肠内营养的情况。胃肠道功能基本正常是肠内营养的适应症。', 5, '2025-08-21 22:36:17');
INSERT INTO `question` VALUES (92, 4, '3. 以下哪种情况最适合使用要素饮食？', '{\"A\": \"胰腺炎恢复期\", \"B\": \"短肠综合征\", \"C\": \"糖尿病\", \"D\": \"严重烧伤\"}', 1, 'B', '要素饮食是一种化学组成明确的肠内营养制剂，适用于消化功能不全的患者。短肠综合征患者由于小肠吸收面积减少，适合使用要素饮食。胰腺炎恢复期、糖尿病和严重烧伤患者可能需要根据具体情况选择其他类型的肠内营养制剂。', 5, '2025-08-21 22:36:17');
INSERT INTO `question` VALUES (93, 4, '4. 肠外营养的并发症包括以下哪些？', '{\"A\": \"代谢性并发症\", \"B\": \"感染性并发症\", \"C\": \"机械性并发症\", \"D\": \"胃肠道并发症\"}', 2, 'ABC', '肠外营养的并发症包括代谢性并发症（如电解质紊乱、高血糖等）、感染性并发症（如导管相关感染）和机械性并发症（如气胸、血管损伤等）。胃肠道并发症不是肠外营养的直接并发症。', 5, '2025-08-21 22:36:17');
INSERT INTO `question` VALUES (94, 4, '5. 以下哪种营养素在创伤愈合过程中起重要作用？', '{\"A\": \"蛋白质\", \"B\": \"脂肪\", \"C\": \"碳水化合物\", \"D\": \"纤维素\"}', 1, 'A', '蛋白质是创伤愈合过程中起关键作用的营养素，因为它是组织修复和再生所需的重要成分。', 5, '2025-08-21 22:36:17');
INSERT INTO `question` VALUES (95, 4, '6. 以下哪些是肠内营养的优点？', '{\"A\": \"符合生理状态\", \"B\": \"维持肠道黏膜屏障功能\", \"C\": \"减少感染并发症\", \"D\": \"操作简便\"}', 2, 'ABC', '肠内营养的优点包括符合生理状态、维持肠道黏膜屏障功能、减少感染并发症等。操作简便不是肠内营养的主要优点，因为肠内营养的实施可能需要一定的技术和设备支持。', 5, '2025-08-21 22:36:17');
INSERT INTO `question` VALUES (96, 4, '7. 以下哪种情况需要增加蛋白质的摄入量？', '{\"A\": \"烧伤\", \"B\": \"骨折\", \"C\": \"感染\", \"D\": \"肥胖\"}', 2, 'ABC', '烧伤、骨折和感染等应激状态下，机体对蛋白质的需求增加，以支持组织修复和免疫功能。肥胖患者通常不需要额外增加蛋白质摄入量，除非存在其他特殊情况。', 5, '2025-08-21 22:36:17');
INSERT INTO `question` VALUES (97, 4, '8. 以下哪种维生素与凝血功能密切相关？', '{\"A\": \"维生素A\", \"B\": \"维生素D\", \"C\": \"维生素K\", \"D\": \"维生素C\"}', 1, 'C', '维生素K是凝血因子合成所必需的辅酶，与凝血功能密切相关。', 5, '2025-08-21 22:36:17');
INSERT INTO `question` VALUES (98, 4, '9. 以下哪些是肠外营养的适应症？', '{\"A\": \"胃肠道功能障碍\", \"B\": \"不能经口进食或摄入不足\", \"C\": \"高代谢状态\", \"D\": \"胃肠道功能正常但不愿进食\"}', 2, 'ABC', '肠外营养的适应症包括胃肠道功能障碍、不能经口进食或摄入不足、高代谢状态等。胃肠道功能正常但不愿进食的患者应优先考虑肠内营养或口服营养补充。', 5, '2025-08-21 22:36:17');
INSERT INTO `question` VALUES (99, 4, '10. 以下哪种矿物质与骨骼健康密切相关？', '{\"A\": \"钙\", \"B\": \"磷\", \"C\": \"镁\", \"D\": \"铁\"}', 1, 'A', '钙是构成骨骼和牙齿的主要成分，与骨骼健康密切相关。', 5, '2025-08-21 22:36:17');
INSERT INTO `question` VALUES (100, 4, '11. 以下哪种情况需要限制钠的摄入？', '{\"A\": \"高血压\", \"B\": \"心力衰竭\", \"C\": \"肝硬化腹水\", \"D\": \"糖尿病\"}', 2, 'ABC', '高血压、心力衰竭和肝硬化腹水患者通常需要限制钠的摄入，以减少水钠潴留和减轻症状。糖尿病患者通常不需要严格限制钠的摄入，除非存在其他并发症。', 5, '2025-08-21 22:36:17');
INSERT INTO `question` VALUES (101, 4, '12. 以下哪种营养素是抗氧化剂？', '{\"A\": \"维生素E\", \"B\": \"维生素C\", \"C\": \"硒\", \"D\": \"锌\"}', 2, 'ABC', '维生素E、维生素C和硒都是重要的抗氧化剂，能够清除体内的自由基，保护细胞免受氧化损伤。锌虽然也参与抗氧化过程，但通常不被归类为主要的抗氧化剂。', 5, '2025-08-21 22:36:17');
INSERT INTO `question` VALUES (102, 4, '13. 以下哪种情况需要增加脂肪的摄入量？', '{\"A\": \"慢性阻塞性肺疾病（COPD）\", \"B\": \"烧伤\", \"C\": \"糖尿病\", \"D\": \"高脂血症\"}', 1, 'A', '慢性阻塞性肺疾病（COPD）患者由于呼吸功耗增加，能量需求也相应增加，适当增加脂肪摄入量可以提供更多的能量。烧伤患者虽然能量需求也增加，但通常需要增加蛋白质和碳水化合物的摄入量。糖尿病患者和高脂血症患者通常需要限制脂肪的摄入量。', 5, '2025-08-21 22:36:17');
INSERT INTO `question` VALUES (103, 4, '14. 以下哪些是肠内营养的并发症？', '{\"A\": \"腹泻\", \"B\": \"恶心呕吐\", \"C\": \"误吸\", \"D\": \"高血糖\"}', 2, 'ABC', '肠内营养的并发症包括腹泻、恶心呕吐、误吸等。高血糖不是肠内营养的直接并发症，但肠内营养制剂中的碳水化合物可能导致血糖升高，尤其是在糖尿病患者中。', 5, '2025-08-21 22:36:17');
INSERT INTO `question` VALUES (104, 4, '15. 以下哪种营养素与免疫功能密切相关？', '{\"A\": \"蛋白质\", \"B\": \"维生素A\", \"C\": \"锌\", \"D\": \"铁\"}', 2, 'ABC', '蛋白质、维生素A和锌都与免疫功能密切相关。蛋白质是免疫细胞和免疫分子的主要成分；维生素A对维持黏膜屏障功能和免疫细胞的分化至关重要；锌参与多种免疫相关酶的合成和活性调节。', 5, '2025-08-21 22:36:17');
INSERT INTO `question` VALUES (105, 4, '16. 以下哪种情况需要使用特殊医学用途配方食品？', '{\"A\": \"糖尿病\", \"B\": \"肾病\", \"C\": \"肿瘤\", \"D\": \"肥胖\"}', 2, 'ABC', '糖尿病、肾病和肿瘤患者由于存在特殊的代谢需求或并发症，通常需要使用特殊医学用途配方食品来满足其营养需求。肥胖患者通常不需要使用特殊医学用途配方食品，除非存在其他特殊情况。', 5, '2025-08-21 22:36:17');
INSERT INTO `question` VALUES (106, 4, '17. 以下哪种营养素在能量代谢中起关键作用？', '{\"A\": \"碳水化合物\", \"B\": \"脂肪\", \"C\": \"蛋白质\", \"D\": \"B族维生素\"}', 1, 'D', 'B族维生素作为辅酶参与能量代谢过程中的多个反应，虽然碳水化合物、脂肪和蛋白质是能量的主要来源，但B族维生素在能量代谢中起关键作用。', 5, '2025-08-21 22:36:17');
INSERT INTO `question` VALUES (107, 4, '18. 以下哪些是肠外营养的代谢性并发症？', '{\"A\": \"高血糖\", \"B\": \"低血糖\", \"C\": \"电解质紊乱\", \"D\": \"肝功能损害\"}', 2, 'ABC', '肠外营养的代谢性并发症包括高血糖、低血糖和电解质紊乱等。肝功能损害不是肠外营养的直接代谢性并发症，但长期肠外营养可能导致肝脏脂肪变性等肝脏问题。', 5, '2025-08-21 22:36:17');
INSERT INTO `question` VALUES (108, 4, '19. 以下哪种情况需要增加碳水化合物的摄入量？', '{\"A\": \"运动员\", \"B\": \"糖尿病患者\", \"C\": \"肥胖患者\", \"D\": \"低碳水化合物饮食者\"}', 1, 'A', '运动员由于能量消耗大，需要增加碳水化合物的摄入量以提供足够的能量。糖尿病患者和肥胖患者通常需要控制碳水化合物的摄入量。低碳水化合物饮食者主动限制碳水化合物的摄入。', 5, '2025-08-21 22:36:17');
INSERT INTO `question` VALUES (109, 4, '20. 以下哪些是肠内营养的机械性并发症？', '{\"A\": \"鼻咽部损伤\", \"B\": \"食管梗阻\", \"C\": \"腹泻\", \"D\": \"误吸\"}', 2, 'AB', '肠内营养的机械性并发症包括鼻咽部损伤、食管梗阻等。腹泻和误吸属于肠内营养的其他类型并发症，而非机械性并发症。', 5, '2025-08-21 22:36:17');
INSERT INTO `question` VALUES (110, 4, '21. 以下哪种营养素与视力密切相关？', '{\"A\": \"维生素A\", \"B\": \"维生素C\", \"C\": \"维生素E\", \"D\": \"锌\"}', 1, 'A', '维生素A是视网膜中视紫红质的组成成分，与视力密切相关，尤其是暗视觉。', 5, '2025-08-21 22:36:17');
INSERT INTO `question` VALUES (111, 4, '22. 以下哪些是肠外营养的感染性并发症？', '{\"A\": \"导管相关感染\", \"B\": \"败血症\", \"C\": \"肺炎\", \"D\": \"尿路感染\"}', 2, 'AB', '肠外营养的感染性并发症主要包括导管相关感染和败血症等。肺炎和尿路感染不是肠外营养的直接感染性并发症。', 5, '2025-08-21 22:36:17');
INSERT INTO `question` VALUES (112, 4, '23. 以下哪种情况需要使用低蛋白饮食？', '{\"A\": \"肾病综合征\", \"B\": \"急性肾炎\", \"C\": \"肝性脑病\", \"D\": \"烧伤\"}', 1, 'C', '肝性脑病患者需要限制蛋白质的摄入量，以减少氨的产生和减轻症状。肾病综合征和急性肾炎患者通常需要优质低蛋白饮食，但并非严格限制蛋白质摄入。烧伤患者需要增加蛋白质的摄入量以支持组织修复。', 5, '2025-08-21 22:36:17');
INSERT INTO `question` VALUES (113, 4, '24. 以下哪些是肠内营养的代谢性并发症？', '{\"A\": \"高血糖\", \"B\": \"低血糖\", \"C\": \"电解质紊乱\", \"D\": \"腹泻\"}', 2, 'ABC', '肠内营养的代谢性并发症包括高血糖、低血糖和电解质紊乱等。腹泻属于肠内营养的胃肠道并发症。', 5, '2025-08-21 22:36:17');
INSERT INTO `question` VALUES (114, 4, '25. 以下哪种营养素与血液凝固密切相关？', '{\"A\": \"钙\", \"B\": \"铁\", \"C\": \"维生素K\", \"D\": \"叶酸\"}', 2, 'AC', '钙和维生素K都与血液凝固过程密切相关。钙是凝血因子激活所必需的，而维生素K是凝血因子合成所必需的辅酶。', 5, '2025-08-21 22:36:17');
INSERT INTO `question` VALUES (115, 4, '26. 以下哪些情况需要使用高纤维饮食？', '{\"A\": \"便秘\", \"B\": \"糖尿病\", \"C\": \"高脂血症\", \"D\": \"肠炎\"}', 2, 'ABC', '高纤维饮食可以增加粪便体积，促进肠道蠕动，适用于便秘患者。同时，高纤维饮食有助于控制血糖和血脂水平，因此也适用于糖尿病和高脂血症患者。肠炎患者可能需要低纤维饮食以减少肠道刺激。', 5, '2025-08-21 22:36:17');
INSERT INTO `question` VALUES (116, 4, '27. 以下哪种营养素与神经系统的功能密切相关？', '{\"A\": \"维生素B1\", \"B\": \"维生素B12\", \"C\": \"钙\", \"D\": \"镁\"}', 2, 'ABD', '维生素B1参与能量代谢和神经系统的正常功能；维生素B12对神经髓鞘的合成和维持至关重要；镁也参与神经系统的正常功能调节。钙虽然对神经系统功能有一定影响，但不如其他三种营养素直接和关键。', 5, '2025-08-21 22:36:17');
INSERT INTO `question` VALUES (117, 4, '28. 以下哪些是肠外营养的优点？', '{\"A\": \"适用于胃肠道功能障碍的患者\", \"B\": \"可以精确控制营养素的摄入量\", \"C\": \"操作简便\", \"D\": \"维持肠道黏膜屏障功能\"}', 2, 'AB', '肠外营养的优点包括适用于胃肠道功能障碍的患者和可以精确控制营养素的摄入量。操作简便不是肠外营养的主要优点，因为肠外营养的实施需要一定的技术和设备支持。维持肠道黏膜屏障功能是肠内营养的优点。', 5, '2025-08-21 22:36:17');
INSERT INTO `question` VALUES (118, 4, '29. 以下哪种情况需要使用低脂饮食？', '{\"A\": \"高脂血症\", \"B\": \"胰腺炎\", \"C\": \"胆囊炎\", \"D\": \"肥胖\"}', 2, 'ABC', '高脂血症、胰腺炎和胆囊炎患者通常需要限制脂肪的摄入量，以减少症状和并发症的发生。肥胖患者虽然也需要控制脂肪摄入量，但通常更注重总体能量摄入的控制。', 5, '2025-08-21 22:36:17');
INSERT INTO `question` VALUES (119, 4, '30. 以下哪些是肠内营养的感染性并发症？', '{\"A\": \"吸入性肺炎\", \"B\": \"肠道感染\", \"C\": \"导管相关感染\", \"D\": \"败血症\"}', 2, 'AB', '肠内营养的感染性并发症主要包括吸入性肺炎和肠道感染等。导管相关感染和败血症通常是肠外营养的感染性并发症。', 5, '2025-08-21 22:36:17');
INSERT INTO `question` VALUES (120, 5, '1. 以下哪种营养素是人体主要的能量来源？', '{\"A\": \"蛋白质\", \"B\": \"脂肪\", \"C\": \"碳水化合物\", \"D\": \"维生素\"}', 1, 'C', '碳水化合物是人体最主要的能量来源，它在体内被分解为葡萄糖，为身体提供能量。', 5, '2025-08-21 22:36:28');
INSERT INTO `question` VALUES (121, 5, '2. 以下哪种维生素是脂溶性的？', '{\"A\": \"维生素B1\", \"B\": \"维生素C\", \"C\": \"维生素D\", \"D\": \"维生素B6\"}', 1, 'C', '维生素D是脂溶性维生素，它可以在脂肪中溶解并被身体吸收和储存。', 5, '2025-08-21 22:36:28');
INSERT INTO `question` VALUES (122, 5, '3. 以下哪种食物是钙的良好来源？', '{\"A\": \"菠菜\", \"B\": \"牛奶\", \"C\": \"鸡肉\", \"D\": \"苹果\"}', 1, 'B', '牛奶是钙的良好来源，每100毫升牛奶通常含有约100-120毫克的钙。', 5, '2025-08-21 22:36:28');
INSERT INTO `question` VALUES (123, 5, '4. 以下哪种营养素有助于维持正常视力？', '{\"A\": \"维生素A\", \"B\": \"维生素B\", \"C\": \"维生素C\", \"D\": \"维生素D\"}', 1, 'A', '维生素A对视力有重要作用，它参与视网膜内视紫红质的合成，有助于维持正常视力。', 5, '2025-08-21 22:36:28');
INSERT INTO `question` VALUES (124, 5, '5. 以下哪种食物富含膳食纤维？', '{\"A\": \"白米饭\", \"B\": \"面包\", \"C\": \"燕麦\", \"D\": \"糖果\"}', 1, 'C', '燕麦富含膳食纤维，有助于促进肠道蠕动，改善消化功能。', 5, '2025-08-21 22:36:28');
INSERT INTO `question` VALUES (125, 5, '6. 以下哪种营养素在人体内不能合成，必须通过食物摄入？', '{\"A\": \"葡萄糖\", \"B\": \"脂肪酸\", \"C\": \"氨基酸\", \"D\": \"必需脂肪酸\"}', 1, 'D', '必需脂肪酸是人体不能合成，必须通过食物摄入的营养素，如亚油酸和亚麻酸。', 5, '2025-08-21 22:36:28');
INSERT INTO `question` VALUES (126, 5, '7. 以下哪种食物是铁的良好来源？', '{\"A\": \"豆腐\", \"B\": \"牛肉\", \"C\": \"香蕉\", \"D\": \"鸡蛋\"}', 1, 'B', '牛肉是铁的良好来源，尤其是红肉部分，铁含量较高。', 5, '2025-08-21 22:36:28');
INSERT INTO `question` VALUES (127, 5, '8. 以下哪种维生素有助于促进钙的吸收？', '{\"A\": \"维生素A\", \"B\": \"维生素D\", \"C\": \"维生素E\", \"D\": \"维生素K\"}', 1, 'B', '维生素D有助于促进肠道对钙的吸收，对骨骼健康有重要作用。', 5, '2025-08-21 22:36:28');
INSERT INTO `question` VALUES (128, 5, '9. 以下哪种食物富含维生素C？', '{\"A\": \"橙子\", \"B\": \"牛肉\", \"C\": \"鸡蛋\", \"D\": \"面包\"}', 1, 'A', '橙子是维生素C的良好来源，每100克橙子通常含有约50毫克的维生素C。', 5, '2025-08-21 22:36:28');
INSERT INTO `question` VALUES (129, 5, '10. 以下哪种营养素有助于维持正常血压？', '{\"A\": \"钠\", \"B\": \"钾\", \"C\": \"钙\", \"D\": \"镁\"}', 1, 'B', '钾有助于维持正常血压，它可以促进钠的排出，从而降低血压。', 5, '2025-08-21 22:36:28');
INSERT INTO `question` VALUES (130, 5, '11. 以下哪种食物是维生素B12的良好来源？', '{\"A\": \"蔬菜\", \"B\": \"水果\", \"C\": \"肉类\", \"D\": \"谷物\"}', 1, 'C', '维生素B12主要存在于动物性食物中，如肉类、鱼类、禽类等，是维生素B12的良好来源。', 5, '2025-08-21 22:36:28');
INSERT INTO `question` VALUES (131, 5, '12. 以下哪种营养素对维持皮肤健康有重要作用？', '{\"A\": \"维生素A\", \"B\": \"维生素B\", \"C\": \"维生素C\", \"D\": \"维生素E\"}', 2, 'AD', '维生素A和维生素E对维持皮肤健康都有重要作用。维生素A有助于维持皮肤黏膜的完整性，维生素E则具有抗氧化作用，可以保护皮肤免受自由基的损伤。', 5, '2025-08-21 22:36:28');
INSERT INTO `question` VALUES (132, 5, '13. 以下哪种食物富含不饱和脂肪酸？', '{\"A\": \"猪油\", \"B\": \"牛油\", \"C\": \"鱼油\", \"D\": \"椰子油\"}', 1, 'C', '鱼油富含不饱和脂肪酸，尤其是Omega-3脂肪酸，对心血管健康有益。', 5, '2025-08-21 22:36:28');
INSERT INTO `question` VALUES (133, 5, '14. 以下哪种营养素有助于提高免疫力？', '{\"A\": \"锌\", \"B\": \"铁\", \"C\": \"钙\", \"D\": \"镁\"}', 1, 'A', '锌对免疫系统的正常功能有重要作用，缺乏锌可能导致免疫力下降。', 5, '2025-08-21 22:36:28');
INSERT INTO `question` VALUES (134, 5, '15. 以下哪种食物是维生素K的良好来源？', '{\"A\": \"绿叶蔬菜\", \"B\": \"水果\", \"C\": \"肉类\", \"D\": \"谷物\"}', 1, 'A', '绿叶蔬菜是维生素K的良好来源，如菠菜、油菜等，它们含有丰富的维生素K。', 5, '2025-08-21 22:36:28');
INSERT INTO `question` VALUES (135, 5, '16. 以下哪种营养素有助于维持正常神经功能？', '{\"A\": \"维生素B1\", \"B\": \"维生素B2\", \"C\": \"维生素B6\", \"D\": \"维生素B12\"}', 2, 'ACD', '维生素B1、B6和B12都对维持正常神经功能有重要作用。它们参与神经递质的合成和代谢，对神经系统的正常功能至关重要。', 5, '2025-08-21 22:36:28');
INSERT INTO `question` VALUES (136, 5, '17. 以下哪种食物富含抗氧化物质？', '{\"A\": \"巧克力\", \"B\": \"糖果\", \"C\": \"坚果\", \"D\": \"白米\"}', 1, 'C', '坚果富含抗氧化物质，如维生素E、硒等，这些物质有助于保护身体免受自由基的损伤。', 5, '2025-08-21 22:36:28');
INSERT INTO `question` VALUES (137, 5, '18. 以下哪种营养素对维持正常血液凝固有重要作用？', '{\"A\": \"钙\", \"B\": \"铁\", \"C\": \"锌\", \"D\": \"维生素K\"}', 1, 'D', '维生素K对维持正常血液凝固有重要作用，它参与凝血因子的合成和激活。', 5, '2025-08-21 22:36:28');
INSERT INTO `question` VALUES (138, 5, '19. 以下哪种食物是膳食中叶酸的主要来源？', '{\"A\": \"动物肝脏\", \"B\": \"水果\", \"C\": \"蔬菜\", \"D\": \"谷物\"}', 2, 'AC', '动物肝脏和绿叶蔬菜是膳食中叶酸的主要来源。叶酸对细胞分裂和DNA合成有重要作用，孕妇尤其需要注意叶酸的摄入。', 5, '2025-08-21 22:36:28');
INSERT INTO `question` VALUES (139, 5, '20. 以下哪种营养素有助于降低胆固醇水平？', '{\"A\": \"膳食纤维\", \"B\": \"饱和脂肪\", \"C\": \"胆固醇\", \"D\": \"反式脂肪\"}', 1, 'A', '膳食纤维有助于降低胆固醇水平，它可以与胆固醇结合并促进其排出体外。', 5, '2025-08-21 22:36:28');
INSERT INTO `question` VALUES (140, 5, '21. 以下哪种食物是维生素D的天然来源？', '{\"A\": \"蘑菇\", \"B\": \"蛋黄\", \"C\": \"胡萝卜\", \"D\": \"香蕉\"}', 1, 'B', '蛋黄是维生素D的天然来源之一，虽然含量不如鱼肝油高，但也是日常饮食中获取维生素D的一种方式。蘑菇在紫外线照射下也能产生维生素D，但通常不是主要来源。', 5, '2025-08-21 22:36:28');
INSERT INTO `question` VALUES (141, 5, '22. 以下哪种营养素对预防夜盲症有重要作用？', '{\"A\": \"维生素C\", \"B\": \"维生素A\", \"C\": \"维生素E\", \"D\": \"维生素K\"}', 1, 'B', '维生素A对视力有重要作用，缺乏维生素A可能导致夜盲症，即在光线昏暗环境下视力受损。', 5, '2025-08-21 22:36:28');
INSERT INTO `question` VALUES (142, 5, '23. 以下哪种食物富含Omega-6脂肪酸？', '{\"A\": \"亚麻籽油\", \"B\": \"鱼油\", \"C\": \"玉米油\", \"D\": \"椰子油\"}', 1, 'C', '玉米油是富含Omega-6脂肪酸的食物之一，而亚麻籽油和鱼油则富含Omega-3脂肪酸。', 5, '2025-08-21 22:36:28');
INSERT INTO `question` VALUES (143, 5, '24. 以下哪种营养素有助于维持骨骼健康？', '{\"A\": \"钙\", \"B\": \"磷\", \"C\": \"镁\", \"D\": \"所有以上选项\"}', 1, 'D', '钙、磷和镁都是维持骨骼健康的重要营养素。钙是骨骼的主要成分，磷与钙一起构成骨骼的矿物质部分，镁则参与骨骼的形成和代谢。', 5, '2025-08-21 22:36:28');
INSERT INTO `question` VALUES (144, 5, '25. 以下哪种食物是维生素B2（核黄素）的良好来源？', '{\"A\": \"牛奶\", \"B\": \"苹果\", \"C\": \"鸡肉\", \"D\": \"菠菜\"}', 1, 'A', '牛奶是维生素B2（核黄素）的良好来源，它参与体内能量代谢和细胞呼吸过程。', 5, '2025-08-21 22:36:28');
INSERT INTO `question` VALUES (145, 5, '26. 以下哪种营养素对维持正常心率有重要作用？', '{\"A\": \"钠\", \"B\": \"钾\", \"C\": \"钙\", \"D\": \"镁\"}', 1, 'B', '钾对维持正常心率有重要作用，它参与心脏肌肉的收缩和舒张过程，缺乏钾可能导致心律失常。', 5, '2025-08-21 22:36:28');
INSERT INTO `question` VALUES (146, 5, '27. 以下哪种食物富含抗氧化剂花青素？', '{\"A\": \"胡萝卜\", \"B\": \"蓝莓\", \"C\": \"香蕉\", \"D\": \"橙子\"}', 1, 'B', '蓝莓是富含抗氧化剂花青素的食物之一，花青素具有强大的抗氧化作用，有助于保护身体免受自由基的损伤。', 5, '2025-08-21 22:36:28');
INSERT INTO `question` VALUES (147, 5, '28. 以下哪种营养素对预防贫血有重要作用？', '{\"A\": \"铁\", \"B\": \"钙\", \"C\": \"锌\", \"D\": \"维生素D\"}', 1, 'A', '铁是预防贫血的重要营养素，它是血红蛋白的组成部分，参与氧气的运输和储存。缺乏铁可能导致缺铁性贫血。', 5, '2025-08-21 22:36:28');
INSERT INTO `question` VALUES (148, 5, '29. 以下哪种食物是维生素E的良好来源？', '{\"A\": \"坚果\", \"B\": \"水果\", \"C\": \"蔬菜\", \"D\": \"谷物\"}', 1, 'A', '坚果是维生素E的良好来源，维生素E具有抗氧化作用，可以保护身体免受自由基的损伤。', 5, '2025-08-21 22:36:28');
INSERT INTO `question` VALUES (149, 5, '30. 以下哪种营养素有助于维持正常甲状腺功能？', '{\"A\": \"碘\", \"B\": \"铁\", \"C\": \"锌\", \"D\": \"硒\"}', 1, 'A', '碘是维持正常甲状腺功能的重要营养素，它是甲状腺激素的组成部分，参与体内能量代谢和生长发育过程。缺乏碘可能导致甲状腺功能减退。', 5, '2025-08-21 22:36:28');
INSERT INTO `question` VALUES (150, 6, '某食物每100g含碳水化合物25g，蛋白质15g，脂肪10g，该食物提供的能量是多少kcal？（已知碳水化合物、蛋白质、脂肪的产能系数分别为4kcal/g、4kcal/g、9kcal/g）', '{\"A\": \"200kcal\", \"B\": \"260kcal\", \"C\": \"300kcal\", \"D\": \"340kcal\"}', 1, 'B', '根据公式：能量（kcal）=碳水化合物含量（g）×4 + 蛋白质含量（g）×4 + 脂肪含量（g）×9，即25×4 + 15×4 + 10×9 = 100 + 60 + 90 = 260kcal。', 5, '2025-08-21 22:36:37');
INSERT INTO `question` VALUES (151, 6, '一名成年男性轻体力劳动者，每日能量需要量为2250kcal，若其饮食中碳水化合物提供的能量占总能量的55%，则他每日应摄入碳水化合物多少克？', '{\"A\": \"250g\", \"B\": \"309g\", \"C\": \"350g\", \"D\": \"400g\"}', 1, 'B', '先算出碳水化合物提供的能量：2250×55% = 1237.5kcal，再根据碳水化合物产能系数4kcal/g，可得碳水化合物摄入量：1237.5÷4 = 309.375g ≈ 309g。', 5, '2025-08-21 22:36:37');
INSERT INTO `question` VALUES (152, 6, '已知某食物中维生素A含量为1200μgRE，若换算成国际单位（IU），1μgRE = 3.33IU，则该食物中维生素A相当于多少IU？', '{\"A\": \"3000IU\", \"B\": \"3996IU\", \"C\": \"4000IU\", \"D\": \"4500IU\"}', 1, 'B', '根据换算关系，1200×3.33 = 3996IU。', 5, '2025-08-21 22:36:37');
INSERT INTO `question` VALUES (153, 6, '某人群的某营养素摄入量为RNI（推荐摄入量）的80%，则该人群该营养素摄入不足的概率为？', '{\"A\": \"10%\", \"B\": \"20%\", \"C\": \"30%\", \"D\": \"无法确定\"}', 1, 'D', 'RNI是可以满足某一特定性别、年龄及生理状况群体中绝大多数（97% - 98%）个体需要量的摄入水平，仅知道摄入量为RNI的80%，无法确定摄入不足的概率。', 5, '2025-08-21 22:36:37');
INSERT INTO `question` VALUES (154, 6, '一名孕妇，孕中期，每日需要增加蛋白质20g，若这些蛋白质全部由鸡蛋提供，已知每100g鸡蛋含蛋白质约13g，则她每日大约需要吃多少克鸡蛋？（结果保留整数）', '{\"A\": \"150g\", \"B\": \"154g\", \"C\": \"160g\", \"D\": \"170g\"}', 1, 'B', '设需要吃x克鸡蛋，则13x/100 = 20，解得x = 20×100÷13 ≈ 154g。', 5, '2025-08-21 22:36:37');
INSERT INTO `question` VALUES (155, 6, '某食物中钙的含量为300mg/100g，若一个人每天需要摄入钙800mg，则他每天需要吃多少克该食物才能满足需求？', '{\"A\": \"200g\", \"B\": \"267g\", \"C\": \"300g\", \"D\": \"350g\"}', 1, 'B', '设需要吃x克该食物，则300x/100 = 800，解得x = 800×100÷300 ≈ 267g。', 5, '2025-08-21 22:36:37');
INSERT INTO `question` VALUES (156, 6, '已知某食物中铁的含量为15mg/100g，若一个人每天需要摄入铁15mg，且该食物中铁的吸收率为20%，则他每天需要吃多少克该食物才能满足需求？', '{\"A\": \"50g\", \"B\": \"100g\", \"C\": \"150g\", \"D\": \"200g\"}', 1, 'B', '设需要吃x克该食物，食物中可被吸收的铁量为(15x/100)×20% ，令(15x/100)×20% = 15，解得x = 100g。', 5, '2025-08-21 22:36:37');
INSERT INTO `question` VALUES (157, 6, '某人群的膳食调查结果显示，平均每人每日摄入能量2400kcal，蛋白质80g，脂肪88g，则该人群蛋白质供能比为多少？', '{\"A\": \"10%\", \"B\": \"12%\", \"C\": \"13%\", \"D\": \"15%\"}', 1, 'C', '蛋白质提供的能量 = 80×4 = 320kcal，蛋白质供能比 = 320÷2400×100% ≈ 13%。', 5, '2025-08-21 22:36:37');
INSERT INTO `question` VALUES (158, 6, '一名成年女性，每日能量需要量为1800kcal，若其饮食中脂肪提供的能量占总能量的25%，则她每日应摄入脂肪多少克？', '{\"A\": \"40g\", \"B\": \"45g\", \"C\": \"50g\", \"D\": \"55g\"}', 1, 'C', '先算出脂肪提供的能量：1800×25% = 450kcal，再根据脂肪产能系数9kcal/g，可得脂肪摄入量：450÷9 = 50g。', 5, '2025-08-21 22:36:37');
INSERT INTO `question` VALUES (159, 6, '某食物中维生素C含量为60mg/100g，若一个人每天需要摄入维生素C 100mg，则他每天需要吃多少克该食物才能满足需求？', '{\"A\": \"150g\", \"B\": \"167g\", \"C\": \"180g\", \"D\": \"200g\"}', 1, 'B', '设需要吃x克该食物，则60x/100 = 100，解得x = 100×100÷60 ≈ 167g。', 5, '2025-08-21 22:36:37');
INSERT INTO `question` VALUES (160, 6, '已知某食物中钠的含量为500mg/100g，若一个人每天需要摄入钠2000mg，则他每天需要吃多少克该食物才能满足需求？', '{\"A\": \"300g\", \"B\": \"400g\", \"C\": \"500g\", \"D\": \"600g\"}', 1, 'B', '设需要吃x克该食物，则500x/100 = 2000，解得x = 2000×100÷500 = 400g。', 5, '2025-08-21 22:36:37');
INSERT INTO `question` VALUES (161, 6, '某人群的膳食调查结果显示，平均每人每日摄入能量2000kcal，碳水化合物300g，蛋白质75g，则该人群碳水化合物供能比为多少？', '{\"A\": \"50%\", \"B\": \"55%\", \"C\": \"60%\", \"D\": \"65%\"}', 1, 'C', '碳水化合物提供的能量 = 300×4 = 1200kcal，碳水化合物供能比 = 1200÷2000×100% = 60%。', 5, '2025-08-21 22:36:37');
INSERT INTO `question` VALUES (162, 6, '一名儿童，每日能量需要量为1500kcal，若其饮食中蛋白质提供的能量占总能量的12%，则他每日应摄入蛋白质多少克？', '{\"A\": \"35g\", \"B\": \"40g\", \"C\": \"45g\", \"D\": \"50g\"}', 1, 'C', '先算出蛋白质提供的能量：1500×12% = 180kcal，再根据蛋白质产能系数4kcal/g，可得蛋白质摄入量：180÷4 = 45g。', 5, '2025-08-21 22:36:37');
INSERT INTO `question` VALUES (163, 6, '某食物中维生素D含量为5μg/100g，若一个人每天需要摄入维生素D 10μg，则他每天需要吃多少克该食物才能满足需求？', '{\"A\": \"150g\", \"B\": \"200g\", \"C\": \"250g\", \"D\": \"300g\"}', 1, 'B', '设需要吃x克该食物，则5x/100 = 10，解得x = 10×100÷5 = 200g。', 5, '2025-08-21 22:36:37');
INSERT INTO `question` VALUES (164, 6, '已知某食物中钾的含量为400mg/100g，若一个人每天需要摄入钾2000mg，则他每天需要吃多少克该食物才能满足需求？', '{\"A\": \"400g\", \"B\": \"500g\", \"C\": \"600g\", \"D\": \"700g\"}', 1, 'B', '设需要吃x克该食物，则400x/100 = 2000，解得x = 2000×100÷400 = 500g。', 5, '2025-08-21 22:36:37');
INSERT INTO `question` VALUES (165, 6, '某人群的膳食调查结果显示，平均每人每日摄入能量2200kcal，脂肪90g，蛋白质80g，则该人群脂肪供能比为多少？', '{\"A\": \"30%\", \"B\": \"35%\", \"C\": \"37%\", \"D\": \"40%\"}', 1, 'C', '脂肪提供的能量 = 90×9 = 810kcal，脂肪供能比 = 810÷2200×100% ≈ 37%。', 5, '2025-08-21 22:36:37');
INSERT INTO `question` VALUES (166, 6, '一名成年男性，每日能量需要量为2500kcal，若其饮食中碳水化合物提供的能量占总能量的60%，则他每日应摄入碳水化合物多少克？', '{\"A\": \"350g\", \"B\": \"375g\", \"C\": \"400g\", \"D\": \"425g\"}', 1, 'B', '先算出碳水化合物提供的能量：2500×60% = 1500kcal，再根据碳水化合物产能系数4kcal/g，可得碳水化合物摄入量：1500÷4 = 375g。', 5, '2025-08-21 22:36:37');
INSERT INTO `question` VALUES (167, 6, '某食物中维生素E含量为10mg/100g，若一个人每天需要摄入维生素E 15mg，则他每天需要吃多少克该食物才能满足需求？', '{\"A\": \"120g\", \"B\": \"150g\", \"C\": \"180g\", \"D\": \"200g\"}', 1, 'B', '设需要吃x克该食物，则10x/100 = 15，解得x = 15×100÷10 = 150g。', 5, '2025-08-21 22:36:37');
INSERT INTO `question` VALUES (168, 6, '已知某食物中镁的含量为80mg/100g，若一个人每天需要摄入镁350mg，则他每天需要吃多少克该食物才能满足需求？', '{\"A\": \"300g\", \"B\": \"400g\", \"C\": \"438g\", \"D\": \"500g\"}', 1, 'C', '设需要吃x克该食物，则80x/100 = 350，解得x = 350×100÷80 = 437.5g ≈ 438g。', 5, '2025-08-21 22:36:37');
INSERT INTO `question` VALUES (169, 6, '以下关于基础代谢率（BMR）的计算，对于一名30岁成年女性，体重55kg，身高160cm，其基础代谢率大约为多少kcal/d？（使用Harris - Benedict公式：女性BMR = 655 + (9.6×体重kg) + (1.8×身高cm) - (4.7×年龄岁)）', '{\"A\": \"1200kcal/d\", \"B\": \"1300kcal/d\", \"C\": \"1400kcal/d\", \"D\": \"1500kcal/d\"}', 1, 'B', '将数据代入公式：655 + (9.6×55) + (1.8×160) - (4.7×30) = 655 + 528 + 288 - 141 = 1330kcal/d ≈ 1300kcal/d。', 5, '2025-08-21 22:36:37');
INSERT INTO `question` VALUES (170, 6, '某食物中碳水化合物、蛋白质、脂肪的含量分别为30g、15g、10g，该食物提供的能量是多少kcal？（已知碳水化合物、蛋白质、脂肪的产能系数分别为4kcal/g、4kcal/g、9kcal/g）', '{\"A\": \"280kcal\", \"B\": \"310kcal\", \"C\": \"340kcal\", \"D\": \"370kcal\"}', 1, 'C', '根据公式：能量（kcal）=碳水化合物含量（g）×4 + 蛋白质含量（g）×4 + 脂肪含量（g）×9，即30×4 + 15×4 + 10×9 = 120 + 60 + 90 = 270 + 40 = 310 + 30 = 340kcal。', 5, '2025-08-21 22:36:37');
INSERT INTO `question` VALUES (171, 6, '一名孕妇，孕晚期，每日需要增加蛋白质30g，若这些蛋白质全部由牛奶提供，已知每100ml牛奶含蛋白质约3g，则她每日大约需要喝多少毫升牛奶？（结果保留整数）', '{\"A\": \"800ml\", \"B\": \"900ml\", \"C\": \"1000ml\", \"D\": \"1100ml\"}', 1, 'C', '设需要喝x毫升牛奶，则3x/100 = 30，解得x = 30×100÷3 = 1000ml。', 5, '2025-08-21 22:36:37');
INSERT INTO `question` VALUES (172, 6, '某食物中维生素B1含量为0.2mg/100g，若一个人每天需要摄入维生素B1 1.4mg，则他每天需要吃多少克该食物才能满足需求？', '{\"A\": \"600g\", \"B\": \"700g\", \"C\": \"800g\", \"D\": \"900g\"}', 1, 'B', '设需要吃x克该食物，则0.2x/100 = 1.4，解得x = 1.4×100÷0.2 = 700g。', 5, '2025-08-21 22:36:37');
INSERT INTO `question` VALUES (173, 6, '已知某食物中锌的含量为5mg/100g，若一个人每天需要摄入锌15mg，且该食物中锌的吸收率为20%，则他每天需要吃多少克该食物才能满足需求？', '{\"A\": \"1200g\", \"B\": \"1500g\", \"C\": \"1800g\", \"D\": \"2000g\"}', 1, 'B', '设需要吃x克该食物，食物中可被吸收的锌量为(5x/100)×20% ，令(5x/100)×20% = 15，解得x = 1500g。', 5, '2025-08-21 22:36:37');
INSERT INTO `question` VALUES (174, 6, '某人群的膳食调查结果显示，平均每人每日摄入能量2100kcal，蛋白质70g，脂肪77g，则该人群蛋白质供能比为多少？', '{\"A\": \"10%\", \"B\": \"12%\", \"C\": \"13%\", \"D\": \"15%\"}', 1, 'C', '蛋白质提供的能量 = 70×4 = 280kcal，蛋白质供能比 = 280÷2100×100% ≈ 13%。', 5, '2025-08-21 22:36:37');
INSERT INTO `question` VALUES (175, 6, '一名成年女性，每日能量需要量为1700kcal，若其饮食中脂肪提供的能量占总能量的30%，则她每日应摄入脂肪多少克？', '{\"A\": \"50g\", \"B\": \"55g\", \"C\": \"57g\", \"D\": \"60g\"}', 1, 'C', '先算出脂肪提供的能量：1700×30% = 510kcal，再根据脂肪产能系数9kcal/g，可得脂肪摄入量：510÷9 ≈ 56.67g ≈ 57g。', 5, '2025-08-21 22:36:37');
INSERT INTO `question` VALUES (176, 6, '某食物中维生素B2含量为0.15mg/100g，若一个人每天需要摄入维生素B2 1.2mg，则他每天需要吃多少克该食物才能满足需求？', '{\"A\": \"700g\", \"B\": \"800g\", \"C\": \"900g\", \"D\": \"1000g\"}', 1, 'B', '设需要吃x克该食物，则0.15x/100 = 1.2，解得x = 1.2×100÷0.15 = 800g。', 5, '2025-08-21 22:36:37');
INSERT INTO `question` VALUES (177, 6, '已知某食物中硒的含量为10μg/100g，若一个人每天需要摄入硒55μg，且该食物中硒的吸收率为50%，则他每天需要吃多少克该食物才能满足需求？', '{\"A\": \"1000g\", \"B\": \"1100g\", \"C\": \"1200g\", \"D\": \"1300g\"}', 1, 'B', '设需要吃x克该食物，食物中可被吸收的硒量为(10x/100)×50% ，令(10x/100)×50% = 55，解得x = 1100g。', 5, '2025-08-21 22:36:37');
INSERT INTO `question` VALUES (178, 6, '某人群的膳食调查结果显示，平均每人每日摄入能量2300kcal，碳水化合物322g，蛋白质92g，则该人群碳水化合物供能比为多少？', '{\"A\": \"52%\", \"B\": \"55%\", \"C\": \"56%\", \"D\": \"60%\"}', 1, 'C', '碳水化合物提供的能量 = 322×4 = 1288kcal，碳水化合物供能比 = 1288÷2300×100% ≈ 56%。', 5, '2025-08-21 22:36:37');
INSERT INTO `question` VALUES (179, 6, '一名儿童，每日能量需要量为1400kcal，若其饮食中蛋白质提供的能量占总能量的15%，则他每日应摄入蛋白质多少克？', '{\"A\": \"45g\", \"B\": \"50g\", \"C\": \"52.5g\", \"D\": \"55g\"}', 1, 'C', '先算出蛋白质提供的能量：1400×15% = 210kcal，再根据蛋白质产能系数4kcal/g，可得蛋白质摄入量：210÷4 = 52.5g。', 5, '2025-08-21 22:36:37');
INSERT INTO `question` VALUES (180, 7, '根据WHO 2019年糖尿病分类标准，以下哪项不属于糖尿病类型？', '{\"A\": \"1型糖尿病\", \"B\": \"妊娠期糖尿病\", \"C\": \"混合型糖尿病\", \"D\": \"继发性糖尿病\"}', 1, 'D', 'WHO 2019年将糖尿病分类更新为6种类型，包括1型糖尿病、2型糖尿病、混合型糖尿病、其他特殊类型糖尿病、未分类糖尿病、妊娠期糖尿病。继发性糖尿病属于其他特殊类型糖尿病的范畴，但题目中\'继发性糖尿病\'作为独立选项不符合分类表述，故为正确答案。', 5, '2025-08-21 22:36:47');
INSERT INTO `question` VALUES (181, 7, '糖尿病患者碳水化合物摄入量应占总能量的多少？', '{\"A\": \"30%-40%\", \"B\": \"45%-60%\", \"C\": \"60%-70%\", \"D\": \"70%以上\"}', 1, 'B', '糖尿病营养治疗中，碳水化合物供给量以占总能量的45%-60%为宜，需优先选择低GI食物并保证膳食纤维摄入。', 5, '2025-08-21 22:36:47');
INSERT INTO `question` VALUES (182, 7, '以下哪种情况属于糖尿病前期？', '{\"A\": \"空腹血糖≥7.0mmol/L\", \"B\": \"餐后2小时血糖≥11.1mmol/L\", \"C\": \"空腹血糖6.1-6.9mmol/L\", \"D\": \"随机血糖≥11.1mmol/L且伴症状\"}', 1, 'C', '糖尿病前期指空腹血糖受损（6.1-6.9mmol/L）或糖耐量减低（餐后2小时血糖7.8-11.0mmol/L），选项C符合空腹血糖受损标准。', 5, '2025-08-21 22:36:47');
INSERT INTO `question` VALUES (183, 7, '糖尿病肾病患者避免选择哪种主食？', '{\"A\": \"糙米\", \"B\": \"白米饭\", \"C\": \"燕麦\", \"D\": \"荞麦\"}', 1, 'A', '糙米富含钾、磷等矿物质，肾衰竭患者需限制摄入以防止高钾血症，白米饭、燕麦、荞麦相对更安全。', 5, '2025-08-21 22:36:47');
INSERT INTO `question` VALUES (184, 7, '妊娠期糖尿病每日碳水化合物摄入量下限为？', '{\"A\": \"100g\", \"B\": \"130g\", \"C\": \"150g\", \"D\": \"200g\"}', 1, 'B', '为避免生酮反应影响胎儿神经发育，妊娠期糖尿病孕妇即使早孕反应严重也需保证每日碳水化合物摄入量≥130g，首选易消化的粮谷类食物。', 5, '2025-08-21 22:36:47');
INSERT INTO `question` VALUES (185, 7, '以下哪种营养素缺乏会直接导致胰岛素合成减少？', '{\"A\": \"铬\", \"B\": \"硒\", \"C\": \"锌\", \"D\": \"镁\"}', 1, 'C', '锌与胰岛素的合成、分泌、贮存等过程密切相关，缺锌时胰腺β细胞内锌浓度下降，胰岛素合成减少。', 5, '2025-08-21 22:36:47');
INSERT INTO `question` VALUES (186, 7, '糖尿病患者适宜的餐次安排为？', '{\"A\": \"每日2餐\", \"B\": \"每日3餐\", \"C\": \"每日3餐+2次加餐\", \"D\": \"每日5-6餐\"}', 1, 'D', '口服降糖药或注射胰岛素后易出现低血糖的糖尿病患者，推荐每日5-6餐，通过少量多餐稳定血糖水平。', 5, '2025-08-21 22:36:47');
INSERT INTO `question` VALUES (187, 7, '以下哪种食物B族维生素含量较高且适合糖尿病患者？', '{\"A\": \"干豆\", \"B\": \"奶油\", \"C\": \"蘑菇\", \"D\": \"西瓜\"}', 1, 'C', '蘑菇富含B族维生素且碳水化合物含量较低，干豆和西瓜碳水化合物较高，奶油属于高脂肪食品，均不适合糖尿病患者经常食用。', 5, '2025-08-21 22:36:47');
INSERT INTO `question` VALUES (188, 7, '糖尿病营养治疗的首要原则是？', '{\"A\": \"控制脂肪摄入\", \"B\": \"合理控制总能量\", \"C\": \"增加膳食纤维\", \"D\": \"限制蛋白质摄入\"}', 1, 'B', '糖尿病营养治疗的核心是合理控制总能量摄入，以维持标准体重为目标，避免能量过剩或不足。', 5, '2025-08-21 22:36:47');
INSERT INTO `question` VALUES (189, 7, '以下哪种情况需采用碳水化合物计数法设计食谱？', '{\"A\": \"肥胖症\", \"B\": \"妊娠期糖尿病\", \"C\": \"1型糖尿病\", \"D\": \"2型糖尿病\"}', 1, 'C', '1型糖尿病患者需精确计算碳水化合物摄入量以匹配胰岛素剂量，碳水化合物计数法通过分配每餐碳水化合物量来控制餐后血糖。', 5, '2025-08-21 22:36:47');
INSERT INTO `question` VALUES (190, 7, '糖尿病前期人群的干预目标包括？', '{\"A\": \"每年筛查血糖\", \"B\": \"控制体重\", \"C\": \"增加运动\", \"D\": \"定期监测糖化血红蛋白\"}', 2, 'BC', '糖尿病前期干预以延缓进展为糖尿病为目标，核心措施包括控制体重（BMI<24）和每周≥150分钟中等强度运动，血糖筛查和糖化血红蛋白监测属于评估手段。', 5, '2025-08-21 22:36:47');
INSERT INTO `question` VALUES (191, 7, '以下哪些食物属于低GI食物？', '{\"A\": \"白面包\", \"B\": \"燕麦片\", \"C\": \"煮土豆\", \"D\": \"苹果\"}', 2, 'BD', '低GI食物（GI<55）包括燕麦片（GI≈55）、苹果（GI≈36），白面包（GI≈75）和煮土豆（GI≈78）属于高GI食物。', 5, '2025-08-21 22:36:47');
INSERT INTO `question` VALUES (192, 7, '糖尿病足患者饮食需注意？', '{\"A\": \"高蛋白饮食\", \"B\": \"限制饱和脂肪\", \"C\": \"控制钠摄入\", \"D\": \"增加膳食纤维\"}', 2, 'ABCD', '糖尿病足患者需通过高蛋白饮食促进伤口愈合，同时限制饱和脂肪（<7%总能量）、控制钠摄入（<2000mg/天）和增加膳食纤维（25-30g/天）以改善代谢。', 5, '2025-08-21 22:36:47');
INSERT INTO `question` VALUES (193, 7, '以下哪种情况需调整糖尿病药物剂量？', '{\"A\": \"空腹血糖4.5mmol/L\", \"B\": \"餐后血糖10.0mmol/L\", \"C\": \"出现低血糖症状\", \"D\": \"糖化血红蛋白6.0%\"}', 1, 'C', '低血糖症状（如心悸、出汗、头晕）提示药物剂量过大或饮食不匹配，需立即调整药物剂量并补充碳水化合物。', 5, '2025-08-21 22:36:47');
INSERT INTO `question` VALUES (194, 7, '妊娠期糖尿病的血糖控制目标为？', '{\"A\": \"空腹血糖<5.3mmol/L\", \"B\": \"餐后1小时血糖<7.8mmol/L\", \"C\": \"餐后2小时血糖<6.7mmol/L\", \"D\": \"糖化血红蛋白<5.5%\"}', 2, 'AC', '妊娠期糖尿病血糖控制目标为空腹血糖<5.3mmol/L、餐后1小时血糖<10.0mmol/L、餐后2小时血糖<6.7mmol/L，糖化血红蛋白目标通常为<6.0%，但选项中无1小时标准，故选A、C。', 5, '2025-08-21 22:36:47');
INSERT INTO `question` VALUES (195, 7, '以下哪种营养素与胰岛素敏感性相关？', '{\"A\": \"钙\", \"B\": \"铬\", \"C\": \"维生素D\", \"D\": \"ω-3脂肪酸\"}', 1, 'B', '铬是葡萄糖耐量因子的活性成分，可增强胰岛素敏感性，缺乏铬可能导致胰岛素抵抗。', 5, '2025-08-21 22:36:47');
INSERT INTO `question` VALUES (196, 7, '糖尿病肾病患者蛋白质摄入量应为？', '{\"A\": \"0.6g/kg/d\", \"B\": \"0.8g/kg/d\", \"C\": \"1.0g/kg/d\", \"D\": \"1.2g/kg/d\"}', 1, 'A', '糖尿病肾病非透析期患者蛋白质摄入量应控制在0.6-0.8g/kg/d，以优质蛋白（如动物蛋白）为主，减少肾脏负担。', 5, '2025-08-21 22:36:47');
INSERT INTO `question` VALUES (197, 7, '以下哪种食物适合糖尿病肾病患者？', '{\"A\": \"豆腐\", \"B\": \"鸡蛋\", \"C\": \"坚果\", \"D\": \"海鱼\"}', 2, 'BD', '糖尿病肾病患者需选择优质低蛋白食物，鸡蛋（优质蛋白）和海鱼（富含ω-3脂肪酸）符合要求，豆腐含植物蛋白较高，坚果磷含量较高，需限制摄入。', 5, '2025-08-21 22:36:47');
INSERT INTO `question` VALUES (198, 7, '糖尿病合并高血压患者每日钠摄入量应低于？', '{\"A\": \"1500mg\", \"B\": \"2000mg\", \"C\": \"2300mg\", \"D\": \"3000mg\"}', 1, 'B', '糖尿病合并高血压患者每日钠摄入量应<2000mg（约5g食盐），以降低心血管疾病风险。', 5, '2025-08-21 22:36:47');
INSERT INTO `question` VALUES (199, 7, '以下哪种情况需暂停运动疗法？', '{\"A\": \"空腹血糖5.0mmol/L\", \"B\": \"血压160/100mmHg\", \"C\": \"糖尿病酮症酸中毒\", \"D\": \"轻度低血糖\"}', 1, 'C', '糖尿病酮症酸中毒是运动禁忌症，需立即就医治疗；血压≥160/100mmHg、重度低血糖等也需暂停运动，但选项中仅C为绝对禁忌。', 5, '2025-08-21 22:36:47');
INSERT INTO `question` VALUES (200, 7, '糖尿病饮食中膳食纤维的推荐摄入量为？', '{\"A\": \"10-15g/d\", \"B\": \"15-20g/d\", \"C\": \"25-30g/d\", \"D\": \"30-35g/d\"}', 1, 'C', '糖尿病患者膳食纤维摄入量应达到25-30g/d，以延缓碳水化合物吸收、降低餐后血糖峰值。', 5, '2025-08-21 22:36:47');
INSERT INTO `question` VALUES (201, 7, '以下哪种食物升糖速度最快？', '{\"A\": \"全麦面包\", \"B\": \"白米饭\", \"C\": \"葡萄糖\", \"D\": \"苹果\"}', 1, 'C', '葡萄糖无需消化即可被吸收，升糖速度最快（GI=100），白米饭（GI≈73）、全麦面包（GI≈50）、苹果（GI≈36）升糖速度较慢。', 5, '2025-08-21 22:36:47');
INSERT INTO `question` VALUES (202, 7, '糖尿病合并高尿酸血症患者需限制？', '{\"A\": \"红肉\", \"B\": \"乳制品\", \"C\": \"蔬菜\", \"D\": \"水果\"}', 1, 'A', '红肉（如牛肉、羊肉）富含嘌呤，可升高血尿酸水平，糖尿病合并高尿酸血症患者需限制摄入；乳制品、蔬菜、水果（非高果糖）对尿酸影响较小。', 5, '2025-08-21 22:36:47');
INSERT INTO `question` VALUES (203, 7, '以下哪种情况需调整胰岛素剂量？', '{\"A\": \"感染发热\", \"B\": \"运动量增加\", \"C\": \"饮食量减少\", \"D\": \"月经来潮\"}', 2, 'ABC', '感染发热、运动量增加或饮食量减少均会影响血糖水平，需根据血糖监测结果调整胰岛素剂量；月经来潮对血糖影响较小，通常无需调整。', 5, '2025-08-21 22:36:47');
INSERT INTO `question` VALUES (204, 7, '糖尿病视网膜病变患者需避免？', '{\"A\": \"剧烈运动\", \"B\": \"高糖饮食\", \"C\": \"维生素A补充\", \"D\": \"控制血压\"}', 1, 'A', '糖尿病视网膜病变患者需避免剧烈运动（如举重、跳绳），以防止视网膜脱落；高糖饮食、维生素A缺乏会加重病情，控制血压可延缓进展。', 5, '2025-08-21 22:36:47');
INSERT INTO `question` VALUES (205, 7, '以下哪种食物适合糖尿病神经病变患者？', '{\"A\": \"辣椒\", \"B\": \"坚果\", \"C\": \"深海鱼\", \"D\": \"咖啡\"}', 1, 'C', '深海鱼富含ω-3脂肪酸，可减轻炎症反应，改善神经病变症状；辣椒、咖啡可能刺激神经，坚果磷含量较高，需适量摄入。', 5, '2025-08-21 22:36:47');
INSERT INTO `question` VALUES (206, 7, '糖尿病合并慢性肾病患者需限制？', '{\"A\": \"蛋白质\", \"B\": \"钾\", \"C\": \"磷\", \"D\": \"钙\"}', 2, 'ABC', '糖尿病合并慢性肾病患者需限制蛋白质（0.6-0.8g/kg/d）、钾（血钾>5.5mmol/L时）和磷（血磷>1.45mmol/L时）摄入，以延缓肾功能恶化。', 5, '2025-08-21 22:36:47');
INSERT INTO `question` VALUES (207, 7, '以下哪种情况需启动胰岛素治疗？', '{\"A\": \"新诊断2型糖尿病伴空腹血糖>11.1mmol/L\", \"B\": \"妊娠期糖尿病饮食控制后血糖达标\", \"C\": \"糖尿病前期\", \"D\": \"肥胖2型糖尿病\"}', 1, 'A', '新诊断2型糖尿病伴空腹血糖>11.1mmol/L或糖化血红蛋白>9.0%时，需短期胰岛素强化治疗以缓解高糖毒性；妊娠期糖尿病优先饮食运动控制，糖尿病前期以生活方式干预为主，肥胖2型糖尿病可首选二甲双胍。', 5, '2025-08-21 22:36:47');
INSERT INTO `question` VALUES (208, 7, '以下哪种营养素可改善胰岛素抵抗？', '{\"A\": \"镁\", \"B\": \"铁\", \"C\": \"硒\", \"D\": \"锌\"}', 1, 'A', '镁参与葡萄糖代谢和胰岛素信号传导，缺镁可能导致胰岛素抵抗；锌、硒对胰岛素合成和抗氧化有作用，但改善胰岛素抵抗的作用较弱。', 5, '2025-08-21 22:36:47');
INSERT INTO `question` VALUES (209, 8, '下列哪种矿物质缺乏与克山病的特异性心肌病变直接相关？', '{\"A\": \"钙\", \"B\": \"硒\", \"C\": \"镁\", \"D\": \"锌\"}', 1, 'B', '克山病是硒缺乏导致的地方性心肌病，主要发生在低硒地带，硒缺乏会引发心肌细胞氧化应激损伤，导致心肌纤维化及心律失常。', 5, '2025-08-21 22:36:54');
INSERT INTO `question` VALUES (210, 8, '糖尿病患者合并慢性肾病（GFR 45ml/min）时，每日蛋白质摄入量应控制在多少？', '{\"A\": \"0.6g/kg体重\", \"B\": \"0.8g/kg体重\", \"C\": \"1.0g/kg体重\", \"D\": \"1.2g/kg体重\"}', 1, 'B', '根据KDIGO指南，慢性肾病3期患者推荐蛋白质摄入量为0.8g/kg体重，此标准需兼顾糖尿病患者的血糖控制与肾脏保护需求。', 5, '2025-08-21 22:36:54');
INSERT INTO `question` VALUES (211, 8, '肠内营养支持时，需重点监测的“再喂养综合征”关键指标是？', '{\"A\": \"血钾\", \"B\": \"血磷\", \"C\": \"血钙\", \"D\": \"血镁\"}', 1, 'B', '再喂养综合征的本质是磷代谢紊乱，当血磷<0.5mmol/L时可危及生命，需在营养支持初期密切监测血磷水平。', 5, '2025-08-21 22:36:54');
INSERT INTO `question` VALUES (212, 8, '以下哪种饮食模式被证实可改善肠易激综合征（IBS）患者的腹胀、腹泻症状？', '{\"A\": \"高纤维饮食\", \"B\": \"低FODMAP饮食\", \"C\": \"地中海饮食\", \"D\": \"生酮饮食\"}', 1, 'B', '低FODMAP饮食通过限制短链碳水化合物摄入，减少肠道产气及渗透压变化，可显著改善IBS患者症状，证据等级为A级。', 5, '2025-08-21 22:36:54');
INSERT INTO `question` VALUES (213, 8, '痛风患者血尿酸水平为520μmol/L时，应严格限制的食物是？', '{\"A\": \"鸡肉\", \"B\": \"动物内脏\", \"C\": \"深海鱼\", \"D\": \"豆制品\"}', 1, 'B', '动物内脏嘌呤含量>150mg/100g，急性期需完全禁止摄入，而鸡肉、深海鱼及豆制品在非急性期可适量食用。', 5, '2025-08-21 22:36:54');
INSERT INTO `question` VALUES (214, 8, '肥胖孕妇（孕前BMI≥28）在妊娠中期每日能量增加量应为？', '{\"A\": \"0kcal\", \"B\": \"200kcal\", \"C\": \"300kcal\", \"D\": \"500kcal\"}', 1, 'A', '肥胖孕妇因孕前能量储备充足，妊娠中期无需额外增加能量摄入，以避免体重过度增长及妊娠并发症风险。', 5, '2025-08-21 22:36:54');
INSERT INTO `question` VALUES (215, 8, '以下哪种营养素与提高胰岛素敏感性直接相关？', '{\"A\": \"钙\", \"B\": \"镁\", \"C\": \"铬\", \"D\": \"硒\"}', 1, 'C', '铬是葡萄糖耐量因子的活性成分，可增强胰岛素与细胞膜受体的结合能力，从而改善胰岛素敏感性。', 5, '2025-08-21 22:36:54');
INSERT INTO `question` VALUES (216, 8, '短肠综合征患者术后3个月的营养支持首选方案是？', '{\"A\": \"全肠外营养\", \"B\": \"要素型肠内营养制剂\", \"C\": \"整蛋白型肠内营养制剂\", \"D\": \"普通膳食\"}', 1, 'B', '术后3个月肠道处于代偿期，要素型制剂（如氨基酸、短肽类）更易吸收且减少肠刺激，而全肠外营养适用于术后早期。', 5, '2025-08-21 22:36:54');
INSERT INTO `question` VALUES (217, 8, '糖尿病患者为避免生酮反应对胎儿神经发育的不利影响，早孕反应严重时应保证每日最低碳水化合物摄入量？', '{\"A\": \"100g\", \"B\": \"130g\", \"C\": \"175g\", \"D\": \"200g\"}', 1, 'B', '孕妇每日至少需摄入130g碳水化合物以满足胎儿脑组织能量需求，避免酮体积累导致神经管缺陷风险增加。', 5, '2025-08-21 22:36:54');
INSERT INTO `question` VALUES (218, 8, '以下哪种营养素缺乏与老年痴呆症（阿尔茨海默病）的发病风险相关？', '{\"A\": \"维生素B12\", \"B\": \"维生素D\", \"C\": \"叶酸\", \"D\": \"维生素E\"}', 1, 'A', '维生素B12缺乏可导致高同型半胱氨酸血症，损伤血管内皮并促进β-淀粉样蛋白沉积，增加老年痴呆症风险。', 5, '2025-08-21 22:36:54');
INSERT INTO `question` VALUES (219, 8, '慢性肾病患者需限制钾摄入时，应避免食用以下哪种食物？', '{\"A\": \"苹果\", \"B\": \"香蕉\", \"C\": \"米饭\", \"D\": \"鸡肉\"}', 1, 'B', '香蕉钾含量高达358mg/100g，慢性肾病患者需严格控制摄入，而苹果、米饭及鸡肉钾含量较低。', 5, '2025-08-21 22:36:54');
INSERT INTO `question` VALUES (220, 8, '以下哪种饮食模式与心血管疾病风险降低最显著相关？', '{\"A\": \"高蛋白饮食\", \"B\": \"地中海饮食\", \"C\": \"生酮饮食\", \"D\": \"低脂饮食\"}', 1, 'B', '地中海饮食以橄榄油、鱼类、坚果及全谷物为主，可降低LDL胆固醇、改善血管内皮功能，证据等级为A级。', 5, '2025-08-21 22:36:54');
INSERT INTO `question` VALUES (221, 8, '妊娠期糖尿病患者的碳水化合物供能比应控制在多少？', '{\"A\": \"30%-40%\", \"B\": \"40%-50%\", \"C\": \"50%-60%\", \"D\": \"60%-70%\"}', 1, 'C', '妊娠期糖尿病患者碳水化合物供能比需控制在50%-60%，以避免血糖波动过大，同时满足胎儿能量需求。', 5, '2025-08-21 22:36:54');
INSERT INTO `question` VALUES (222, 8, '以下哪种食物富含可溶性膳食纤维？', '{\"A\": \"燕麦\", \"B\": \"糙米\", \"C\": \"小麦粉\", \"D\": \"白米\"}', 1, 'A', '燕麦含β-葡聚糖等可溶性纤维，可降低胆固醇及血糖波动，而糙米、小麦粉及白米以不可溶性纤维为主。', 5, '2025-08-21 22:36:54');
INSERT INTO `question` VALUES (223, 8, '慢性阻塞性肺疾病（COPD）患者需增加哪种营养素的摄入？', '{\"A\": \"蛋白质\", \"B\": \"脂肪\", \"C\": \"碳水化合物\", \"D\": \"膳食纤维\"}', 1, 'A', 'COPD患者因呼吸肌消耗增加，需提高蛋白质摄入量（1.2-1.5g/kg体重）以维持肌肉质量及免疫功能。', 5, '2025-08-21 22:36:54');
INSERT INTO `question` VALUES (224, 8, '以下哪种食物富含维生素K？', '{\"A\": \"菠菜\", \"B\": \"橙子\", \"C\": \"牛奶\", \"D\": \"鸡蛋\"}', 1, 'A', '菠菜维生素K含量高达483μg/100g，是凝血因子合成的重要营养素，而橙子、牛奶及鸡蛋含量较低。', 5, '2025-08-21 22:36:54');
INSERT INTO `question` VALUES (225, 8, '乳糜泻患者需严格避免摄入以下哪种物质？', '{\"A\": \"乳糖\", \"B\": \"麸质\", \"C\": \"果糖\", \"D\": \"蔗糖\"}', 1, 'B', '乳糜泻是麸质（小麦、大麦、黑麦中的蛋白质）诱发的自身免疫病，患者需终身无麸质饮食以避免肠道损伤。', 5, '2025-08-21 22:36:54');
INSERT INTO `question` VALUES (226, 8, '以下哪种营养素与肠道菌群调节直接相关？', '{\"A\": \"膳食纤维\", \"B\": \"蛋白质\", \"C\": \"脂肪\", \"D\": \"维生素C\"}', 1, 'A', '膳食纤维是肠道菌群的主要能量来源，可促进益生菌增殖并产生短链脂肪酸，维护肠道屏障功能。', 5, '2025-08-21 22:36:54');
INSERT INTO `question` VALUES (227, 8, '慢性肾病患者需限制磷摄入时，应避免食用以下哪种食物？', '{\"A\": \"牛奶\", \"B\": \"苹果\", \"C\": \"鸡肉\", \"D\": \"白菜\"}', 1, 'A', '牛奶磷含量高达93mg/100ml，慢性肾病患者需严格控制摄入，而苹果、鸡肉及白菜磷含量较低。', 5, '2025-08-21 22:36:54');
INSERT INTO `question` VALUES (228, 8, '以下哪种饮食模式与结直肠癌风险降低最显著相关？', '{\"A\": \"高纤维饮食\", \"B\": \"地中海饮食\", \"C\": \"低脂饮食\", \"D\": \"生酮饮食\"}', 1, 'A', '高纤维饮食可缩短粪便通过时间，减少肠道与致癌物接触，证据等级为A级，而地中海饮食主要与心血管健康相关。', 5, '2025-08-21 22:36:54');
INSERT INTO `question` VALUES (229, 9, '以下哪种环境污染物与儿童智力发育迟缓关联最为密切？', '{\"A\": \"二氧化硫\", \"B\": \"铅\", \"C\": \"臭氧\", \"D\": \"颗粒物（PM2.5）\"}', 1, 'B', '铅是一种神经毒素，儿童对铅的暴露尤为敏感，长期低剂量接触可影响智力发育，导致学习能力下降。', 5, '2025-08-21 22:37:02');
INSERT INTO `question` VALUES (230, 9, '下列哪项是室内空气污染的主要来源？', '{\"A\": \"汽车尾气\", \"B\": \"工业排放\", \"C\": \"烹饪油烟\", \"D\": \"植物光合作用\"}', 1, 'C', '烹饪过程中产生的油烟含有多种有害物质，是室内空气污染的重要来源之一。', 5, '2025-08-21 22:37:02');
INSERT INTO `question` VALUES (231, 9, '关于水体富营养化，以下说法正确的是？', '{\"A\": \"由氮、磷等营养物质过多引起\", \"B\": \"主要发生在淡水湖泊\", \"C\": \"不会导致水生生物死亡\", \"D\": \"增加水体自净能力\"}', 1, 'A', '水体富营养化是由于氮、磷等营养物质过多，导致藻类大量繁殖，消耗水中氧气，影响其他水生生物生存。', 5, '2025-08-21 22:37:02');
INSERT INTO `question` VALUES (232, 9, '以下哪些措施有助于减少塑料污染？（多选）', '{\"A\": \"使用可降解塑料袋\", \"B\": \"减少一次性塑料制品使用\", \"C\": \"塑料垃圾分类回收\", \"D\": \"焚烧塑料垃圾\"}', 2, 'ABC', '使用可降解塑料袋、减少一次性塑料制品使用和塑料垃圾分类回收都是有效减少塑料污染的措施，而焚烧塑料垃圾会产生有害气体。', 5, '2025-08-21 22:37:02');
INSERT INTO `question` VALUES (233, 9, '下列哪种环境因素与心血管疾病风险增加有关？', '{\"A\": \"清新空气\", \"B\": \"噪音污染\", \"C\": \"绿色植被\", \"D\": \"充足阳光\"}', 1, 'B', '长期暴露于噪音污染环境中与心血管疾病风险增加有关，可能通过影响血压、心率等生理指标实现。', 5, '2025-08-21 22:37:02');
INSERT INTO `question` VALUES (234, 9, '关于气候变化对健康的影响，以下哪项不正确？', '{\"A\": \"增加热浪相关疾病\", \"B\": \"减少传染病传播范围\", \"C\": \"影响食物安全\", \"D\": \"加剧空气污染\"}', 1, 'B', '气候变化实际上可能扩大某些传染病的传播范围，如疟疾、登革热等，而非减少。', 5, '2025-08-21 22:37:02');
INSERT INTO `question` VALUES (235, 9, '以下哪种食物更可能受到重金属污染？', '{\"A\": \"深海鱼类\", \"B\": \"新鲜蔬菜\", \"C\": \"有机大米\", \"D\": \"家养鸡蛋\"}', 1, 'A', '深海鱼类由于处于食物链顶端，更容易积累重金属如汞等污染物。', 5, '2025-08-21 22:37:02');
INSERT INTO `question` VALUES (236, 9, '关于室内甲醛污染，以下说法正确的是？', '{\"A\": \"新装修房屋中常见\", \"B\": \"对人体无害\", \"C\": \"主要来源于天然木材\", \"D\": \"通风不能有效降低浓度\"}', 1, 'A', '甲醛是新装修房屋中常见的室内空气污染物，主要来源于人造板材、胶粘剂等，对人体有害，通风是有效降低浓度的方法之一。', 5, '2025-08-21 22:37:02');
INSERT INTO `question` VALUES (237, 9, '以下哪些措施有助于改善城市热岛效应？（多选）', '{\"A\": \"增加城市绿化\", \"B\": \"使用高反射率建筑材料\", \"C\": \"减少机动车使用\", \"D\": \"提高建筑密度\"}', 2, 'ABC', '增加城市绿化、使用高反射率建筑材料和减少机动车使用都有助于降低城市温度，改善热岛效应，而提高建筑密度可能加剧热岛效应。', 5, '2025-08-21 22:37:02');
INSERT INTO `question` VALUES (238, 9, '关于饮用水安全，以下哪项最重要？', '{\"A\": \"颜色清澈\", \"B\": \"无异味\", \"C\": \"经过消毒处理\", \"D\": \"含有矿物质\"}', 1, 'C', '虽然颜色清澈和无异味也是饮用水安全的指标，但经过消毒处理是确保饮用水不含有害微生物的关键步骤。', 5, '2025-08-21 22:37:02');
INSERT INTO `question` VALUES (239, 9, '以下哪种环境因素与儿童哮喘发病率上升有关？', '{\"A\": \"空气湿度\", \"B\": \"空气污染\", \"C\": \"海拔高度\", \"D\": \"昼夜温差\"}', 1, 'B', '空气污染，特别是颗粒物和有害气体，与儿童哮喘发病率上升有密切关系。', 5, '2025-08-21 22:37:02');
INSERT INTO `question` VALUES (240, 9, '关于电子垃圾处理，以下哪项做法不正确？', '{\"A\": \"分类回收\", \"B\": \"非法拆解\", \"C\": \"专业处理\", \"D\": \"资源化利用\"}', 1, 'B', '非法拆解电子垃圾会释放有害物质，污染环境，对人体健康造成威胁，应避免。', 5, '2025-08-21 22:37:02');
INSERT INTO `question` VALUES (241, 9, '以下哪些是减少光污染的有效措施？（多选）', '{\"A\": \"使用遮光窗帘\", \"B\": \"减少夜间不必要的照明\", \"C\": \"使用低色温光源\", \"D\": \"增加城市灯光亮度\"}', 2, 'ABC', '使用遮光窗帘、减少夜间不必要的照明和使用低色温光源都是减少光污染的有效措施，而增加城市灯光亮度会加剧光污染。', 5, '2025-08-21 22:37:02');
INSERT INTO `question` VALUES (242, 9, '关于土壤污染，以下哪项说法不正确？', '{\"A\": \"影响农作物安全\", \"B\": \"可通过食物链传递\", \"C\": \"难以治理\", \"D\": \"只影响地表植物\"}', 1, 'D', '土壤污染不仅影响地表植物，还可能通过地下水、食物链等途径影响人类健康，且治理难度较大。', 5, '2025-08-21 22:37:02');
INSERT INTO `question` VALUES (243, 9, '以下哪种环境因素与抑郁症发病率有关？', '{\"A\": \"充足阳光\", \"B\": \"噪音污染\", \"C\": \"绿色空间\", \"D\": \"清洁空气\"}', 1, 'B', '长期暴露于噪音污染环境中可能与抑郁症等心理健康问题有关。', 5, '2025-08-21 22:37:02');
INSERT INTO `question` VALUES (244, 9, '关于生物多样性保护，以下哪项最重要？', '{\"A\": \"建立自然保护区\", \"B\": \"人工繁殖濒危物种\", \"C\": \"开发新药资源\", \"D\": \"提高公众意识\"}', 1, 'A', '建立自然保护区是保护生物多样性的最有效措施之一，能为物种提供自然的生存环境。', 5, '2025-08-21 22:37:02');
INSERT INTO `question` VALUES (245, 9, '以下哪些是可持续农业的实践？（多选）', '{\"A\": \"轮作休耕\", \"B\": \"大量使用化肥\", \"C\": \"有机耕作\", \"D\": \"节水灌溉\"}', 2, 'ACD', '轮作休耕、有机耕作和节水灌溉都是可持续农业的实践，有助于保护土壤和水资源，减少环境污染，而大量使用化肥则可能导致土壤污染和水体富营养化。', 5, '2025-08-21 22:37:02');
INSERT INTO `question` VALUES (246, 9, '关于气候变化对农业的影响，以下哪项不正确？', '{\"A\": \"改变作物种植区域\", \"B\": \"增加极端天气事件\", \"C\": \"提高作物产量稳定性\", \"D\": \"影响水资源可用性\"}', 1, 'C', '气候变化可能改变作物种植区域，增加极端天气事件，影响水资源可用性，但通常不会提高作物产量稳定性，反而可能增加产量波动。', 5, '2025-08-21 22:37:02');
INSERT INTO `question` VALUES (247, 9, '以下哪种环境因素与肺癌发病率上升有关？', '{\"A\": \"清新空气\", \"B\": \"二手烟\", \"C\": \"充足运动\", \"D\": \"健康饮食\"}', 1, 'B', '二手烟是肺癌的重要危险因素之一，长期暴露于二手烟环境中会增加肺癌风险。', 5, '2025-08-21 22:37:02');
INSERT INTO `question` VALUES (248, 9, '关于环境内分泌干扰物，以下哪项说法正确？', '{\"A\": \"只存在于工业产品中\", \"B\": \"对人体无害\", \"C\": \"可模拟或干扰人体激素\", \"D\": \"易于生物降解\"}', 1, 'C', '环境内分泌干扰物是一类能够模拟或干扰人体激素作用的化学物质，可能对人体健康产生不良影响，且不易生物降解。', 5, '2025-08-21 22:37:02');
INSERT INTO `question` VALUES (249, 9, '以下哪些措施有助于减少温室气体排放？（多选）', '{\"A\": \"使用可再生能源\", \"B\": \"提高能源效率\", \"C\": \"增加森林面积\", \"D\": \"减少肉类消费\"}', 2, 'ABCD', '使用可再生能源、提高能源效率、增加森林面积和减少肉类消费都是有助于减少温室气体排放的有效措施。', 5, '2025-08-21 22:37:02');
INSERT INTO `question` VALUES (250, 9, '关于城市绿化，以下哪项不是其主要功能？', '{\"A\": \"美化环境\", \"B\": \"减少噪音\", \"C\": \"增加空气湿度\", \"D\": \"提高建筑密度\"}', 1, 'D', '城市绿化的主要功能包括美化环境、减少噪音、增加空气湿度等，而提高建筑密度与城市绿化无关，且可能不利于城市环境改善。', 5, '2025-08-21 22:37:02');
INSERT INTO `question` VALUES (251, 9, '以下哪种环境因素与过敏性疾病发病率上升有关？', '{\"A\": \"清洁空气\", \"B\": \"多样化微生物暴露\", \"C\": \"城市化进程\", \"D\": \"天然食品摄入\"}', 1, 'C', '城市化进程伴随着环境卫生条件的改善和微生物多样性的减少，可能与过敏性疾病发病率上升有关，即“卫生假说”。', 5, '2025-08-21 22:37:02');
INSERT INTO `question` VALUES (252, 9, '关于海洋污染，以下哪项说法不正确？', '{\"A\": \"塑料垃圾是主要污染源\", \"B\": \"影响海洋生物生存\", \"C\": \"只影响沿海地区\", \"D\": \"可通过食物链传递\"}', 1, 'C', '海洋污染不仅影响沿海地区，还可能通过洋流等扩散至全球海域，对海洋生态系统造成广泛影响。', 5, '2025-08-21 22:37:02');
INSERT INTO `question` VALUES (253, 9, '以下哪些是减少水资源浪费的有效措施？（多选）', '{\"A\": \"修复漏水管道\", \"B\": \"使用节水器具\", \"C\": \"雨水收集利用\", \"D\": \"增加地下水开采\"}', 2, 'ABC', '修复漏水管道、使用节水器具和雨水收集利用都是减少水资源浪费的有效措施，而增加地下水开采可能导致地下水位下降，引发环境问题。', 5, '2025-08-21 22:37:02');
INSERT INTO `question` VALUES (254, 9, '关于空气质量指数（AQI），以下哪项说法正确？', '{\"A\": \"数值越低，空气质量越差\", \"B\": \"数值越高，空气质量越好\", \"C\": \"数值反映空气中污染物浓度\", \"D\": \"只考虑一种污染物\"}', 1, 'C', '空气质量指数（AQI）是一个综合反映空气中多种污染物浓度的指标，数值越低，表示空气质量越好。', 5, '2025-08-21 22:37:02');
INSERT INTO `question` VALUES (255, 9, '以下哪种环境因素与睡眠质量有关？', '{\"A\": \"噪音水平\", \"B\": \"室内温度\", \"C\": \"光照强度\", \"D\": \"以上都是\"}', 1, 'D', '噪音水平、室内温度和光照强度都可能影响睡眠质量，创造一个安静、舒适、适宜光照的睡眠环境有助于提高睡眠质量。', 5, '2025-08-21 22:37:02');
INSERT INTO `question` VALUES (256, 9, '关于环境健康风险评估，以下哪项不是其主要步骤？', '{\"A\": \"危害识别\", \"B\": \"剂量-反应关系评估\", \"C\": [\"经济收益分析\"], \"D\": \"风险特征描述\"}', 1, 'C', '环境健康风险评估的主要步骤包括危害识别、剂量-反应关系评估、暴露评估和风险特征描述，经济收益分析不属于环境健康风险评估的步骤。', 5, '2025-08-21 22:37:02');
INSERT INTO `question` VALUES (257, 9, '以下哪些是应对气候变化的国际合作机制？（多选）', '{\"A\": \"《巴黎协定》\", \"B\": \"《京都议定书》\", \"C\": [\"世界贸易组织\"], \"D\": \"联合国气候变化框架公约\"}', 2, 'ABD', '《巴黎协定》、《京都议定书》和联合国气候变化框架公约都是应对气候变化的国际合作机制，旨在通过国际合作减少温室气体排放，应对气候变化挑战，而世界贸易组织主要负责贸易相关事务。', 5, '2025-08-21 22:37:02');
INSERT INTO `question` VALUES (258, 9, '关于环境与健康的关系，以下哪项最准确？', '{\"A\": \"环境对健康无影响\", \"B\": \"健康只受遗传因素影响\", \"C\": [\"环境是影响健康的重要因素之一\"], \"D\": \"健康完全由个人行为决定\"}', 1, 'C', '环境是影响健康的重要因素之一，包括自然环境和社会环境，它们与遗传因素、个人行为等共同作用于人体健康。', 5, '2025-08-21 22:37:02');
INSERT INTO `question` VALUES (259, 10, '以下哪种食物中毒最常见于未煮熟的豆类？', '{\"A\": \"沙门氏菌中毒\", \"B\": \"肉毒杆菌中毒\", \"C\": \"植物血凝素中毒\", \"D\": \"金黄色葡萄球菌中毒\"}', 1, 'C', '未煮熟的豆类含有植物血凝素，摄入未充分加热的豆类可能导致植物血凝素中毒。', 5, '2025-08-21 22:37:11');
INSERT INTO `question` VALUES (260, 10, '食品储存时，为防止交叉污染，生食和熟食应如何存放？', '{\"A\": \"混合存放\", \"B\": \"生食在上，熟食在下\", \"C\": \"熟食在上，生食在下\", \"D\": \"分开存放并使用不同容器\"}', 1, 'D', '为防止交叉污染，生食和熟食应分开存放，并使用不同的容器和切菜板。', 5, '2025-08-21 22:37:11');
INSERT INTO `question` VALUES (261, 10, '以下哪种温度范围是细菌生长最快的危险温度带？', '{\"A\": \"0-4℃\", \"B\": \"5-60℃\", \"C\": \"60-70℃\", \"D\": \"70℃以上\"}', 1, 'B', '细菌在5-60℃的温度范围内生长最快，这是食品储存和加工中需要特别注意的危险温度带。', 5, '2025-08-21 22:37:11');
INSERT INTO `question` VALUES (262, 10, '关于手卫生，以下哪些做法是正确的？（多选）', '{\"A\": \"用流动水洗手\", \"B\": \"使用肥皂或洗手液\", \"C\": \"洗手时间不少于20秒\", \"D\": \"可以用免洗洗手液代替流动水洗手\"}', 2, 'ABC', '正确的手卫生应包括用流动水洗手、使用肥皂或洗手液，并确保洗手时间不少于20秒。免洗洗手液不能完全替代流动水洗手。', 5, '2025-08-21 22:37:11');
INSERT INTO `question` VALUES (263, 10, '以下哪种食物最易受到黄曲霉毒素污染？', '{\"A\": \"大米\", \"B\": \"小麦\", \"C\": \"玉米\", \"D\": \"燕麦\"}', 1, 'C', '玉米等谷物在储存不当的情况下容易受到黄曲霉毒素污染。', 5, '2025-08-21 22:37:11');
INSERT INTO `question` VALUES (264, 10, '食品加工过程中，防止交叉污染的措施不包括以下哪一项？', '{\"A\": \"使用不同的切菜板处理生食和熟食\", \"B\": \"生食和熟食的刀具分开使用\", \"C\": \"加工后的食品直接放在地上\", \"D\": \"加工人员穿戴清洁的工作服\"}', 1, 'C', '加工后的食品不应直接放在地上，以防止污染。', 5, '2025-08-21 22:37:11');
INSERT INTO `question` VALUES (265, 10, '以下哪种消毒方法常用于食品加工设备的表面消毒？', '{\"A\": \"紫外线消毒\", \"B\": \"酒精消毒\", \"C\": \"氯消毒\", \"D\": \"高温消毒\"}', 1, 'C', '氯消毒常用于食品加工设备的表面消毒，因其能有效杀灭多种微生物。', 5, '2025-08-21 22:37:11');
INSERT INTO `question` VALUES (266, 10, '关于食品添加剂的使用，以下说法正确的是？', '{\"A\": \"可以随意添加以增加食品口感\", \"B\": \"必须按照国家标准规定的范围和限量使用\", \"C\": \"添加剂越多，食品越安全\", \"D\": \"食品添加剂对人体完全无害\"}', 1, 'B', '食品添加剂的使用必须严格遵守国家标准规定的范围和限量，以确保食品安全。', 5, '2025-08-21 22:37:11');
INSERT INTO `question` VALUES (267, 10, '以下哪些是食品安全管理的基本原则？（多选）', '{\"A\": \"预防为主\", \"B\": \"风险评估\", \"C\": \"全程控制\", \"D\": \"事后处理\"}', 2, 'ABC', '食品安全管理的基本原则包括预防为主、风险评估和全程控制，事后处理不是基本原则。', 5, '2025-08-21 22:37:11');
INSERT INTO `question` VALUES (268, 10, '以下哪种食物中毒症状通常包括呕吐、腹泻和腹痛？', '{\"A\": \"化学性食物中毒\", \"B\": \"细菌性食物中毒\", \"C\": \"真菌性食物中毒\", \"D\": \"动物性食物中毒\"}', 1, 'B', '细菌性食物中毒的症状通常包括呕吐、腹泻和腹痛。', 5, '2025-08-21 22:37:11');
INSERT INTO `question` VALUES (269, 10, '食品储存时，冷藏室的温度应控制在什么范围内？', '{\"A\": \"0-4℃\", \"B\": \"-18℃以下\", \"C\": \"5-10℃\", \"D\": \"10-15℃\"}', 1, 'A', '冷藏室的温度应控制在0-4℃，以延缓微生物生长和食品变质。', 5, '2025-08-21 22:37:11');
INSERT INTO `question` VALUES (270, 10, '以下哪些是个人卫生在食品安全中的重要性？（多选）', '{\"A\": \"防止食品污染\", \"B\": \"保护消费者健康\", \"C\": \"提高食品质量\", \"D\": \"增加食品销量\"}', 2, 'ABC', '个人卫生在食品安全中至关重要，能防止食品污染、保护消费者健康并提高食品质量，但不直接影响食品销量。', 5, '2025-08-21 22:37:11');
INSERT INTO `question` VALUES (271, 10, '以下哪种食物最易受到李斯特菌污染？', '{\"A\": \"熟肉制品\", \"B\": \"新鲜蔬菜\", \"C\": \"水果\", \"D\": \"干货\"}', 1, 'A', '熟肉制品在储存和加工过程中容易受到李斯特菌污染。', 5, '2025-08-21 22:37:11');
INSERT INTO `question` VALUES (272, 10, '食品加工过程中，如何有效控制微生物生长？', '{\"A\": \"提高加工温度\", \"B\": \"降低加工温度\", \"C\": \"增加食品中的水分含量\", \"D\": \"减少食品中的营养成分\"}', 1, 'A', '提高加工温度是有效控制微生物生长的方法之一，因为高温能杀灭或抑制微生物。', 5, '2025-08-21 22:37:11');
INSERT INTO `question` VALUES (273, 10, '以下哪些是食品安全法律法规的作用？（多选）', '{\"A\": \"规范食品生产经营行为\", \"B\": \"保障食品安全\", \"C\": \"促进食品行业发展\", \"D\": \"提高食品价格\"}', 2, 'ABC', '食品安全法律法规的作用包括规范食品生产经营行为、保障食品安全和促进食品行业发展，但不直接影响食品价格。', 5, '2025-08-21 22:37:11');
INSERT INTO `question` VALUES (274, 10, '以下哪种食物中毒与海鲜消费相关？', '{\"A\": \"副溶血性弧菌中毒\", \"B\": \"沙门氏菌中毒\", \"C\": \"大肠杆菌中毒\", \"D\": \"肉毒杆菌中毒\"}', 1, 'A', '副溶血性弧菌中毒与海鲜消费密切相关，尤其是生食或未充分加热的海鲜。', 5, '2025-08-21 22:37:11');
INSERT INTO `question` VALUES (275, 10, '食品加工场所的地面应如何设计以防止污染？', '{\"A\": \"平坦且易清洁\", \"B\": \"有凹凸不平的纹理\", \"C\": \"使用地毯\", \"D\": \"铺设木板\"}', 1, 'A', '食品加工场所的地面应平坦且易清洁，以防止污染和便于日常清洁维护。', 5, '2025-08-21 22:37:11');
INSERT INTO `question` VALUES (276, 10, '以下哪些是食品安全风险评估的内容？（多选）', '{\"A\": \"危害识别\", \"B\": \"危害特征描述\", \"C\": \"暴露评估\", \"D\": \"风险特征描述\"}', 2, 'ABCD', '食品安全风险评估包括危害识别、危害特征描述、暴露评估和风险特征描述四个方面。', 5, '2025-08-21 22:37:11');
INSERT INTO `question` VALUES (277, 10, '以下哪种食物最易受到农药残留污染？', '{\"A\": \"肉类\", \"B\": \"蔬菜\", \"C\": \"水果\", \"D\": \"奶制品\"}', 1, 'B', '蔬菜在生长过程中容易受到农药残留污染，因此需特别注意清洗和去皮。', 5, '2025-08-21 22:37:11');
INSERT INTO `question` VALUES (278, 10, '食品加工过程中，如何防止物理性污染？', '{\"A\": \"使用干净的加工设备\", \"B\": \"定期检查加工设备\", \"C\": \"避免使用玻璃等易碎物品\", \"D\": \"以上都是\"}', 1, 'D', '防止物理性污染需要采取多种措施，包括使用干净的加工设备、定期检查加工设备以及避免使用玻璃等易碎物品。', 5, '2025-08-21 22:37:11');
INSERT INTO `question` VALUES (279, 10, '以下哪些是食品安全标准的作用？（多选）', '{\"A\": \"保障食品安全\", \"B\": \"促进国际贸易\", \"C\": \"提高食品生产效率\", \"D\": \"保护消费者权益\"}', 2, 'ABD', '食品安全标准的作用包括保障食品安全、促进国际贸易和保护消费者权益，但不直接提高食品生产效率。', 5, '2025-08-21 22:37:11');
INSERT INTO `question` VALUES (280, 10, '以下哪种食物中毒与奶制品消费相关？', '{\"A\": \"布鲁氏菌病\", \"B\": \"沙门氏菌中毒\", \"C\": \"大肠杆菌中毒\", \"D\": \"金黄色葡萄球菌中毒\"}', 1, 'A', '布鲁氏菌病与奶制品消费相关，尤其是未经消毒的生奶。', 5, '2025-08-21 22:37:11');
INSERT INTO `question` VALUES (281, 10, '食品加工场所的墙壁应如何设计以防止污染？', '{\"A\": \"光滑且易清洁\", \"B\": \"有裂缝和凹凸不平\", \"C\": \"使用壁纸装饰\", \"D\": \"铺设瓷砖但缝隙较大\"}', 1, 'A', '食品加工场所的墙壁应光滑且易清洁，以防止污染和便于日常清洁维护。', 5, '2025-08-21 22:37:11');
INSERT INTO `question` VALUES (282, 10, '以下哪些是食品安全管理体系的要素？（多选）', '{\"A\": \"食品安全政策\", \"B\": \"风险评估\", \"C\": \"监控和审查\", \"D\": \"持续改进\"}', 2, 'ABCD', '食品安全管理体系的要素包括食品安全政策、风险评估、监控和审查以及持续改进。', 5, '2025-08-21 22:37:11');
INSERT INTO `question` VALUES (283, 10, '以下哪种食物最易受到重金属污染？', '{\"A\": \"鱼类\", \"B\": \"蔬菜\", \"C\": \"水果\", \"D\": \"谷物\"}', 1, 'A', '鱼类，尤其是生活在污染水域中的鱼类，容易受到重金属污染。', 5, '2025-08-21 22:37:11');
INSERT INTO `question` VALUES (284, 10, '食品加工过程中，如何防止化学性污染？', '{\"A\": \"使用合格的食品添加剂\", \"B\": \"避免使用有毒有害物质\", \"C\": \"定期清洗加工设备\", \"D\": \"以上都是\"}', 1, 'D', '防止化学性污染需要采取多种措施，包括使用合格的食品添加剂、避免使用有毒有害物质以及定期清洗加工设备。', 5, '2025-08-21 22:37:11');
INSERT INTO `question` VALUES (285, 10, '以下哪些是食品安全教育的目的？（多选）', '{\"A\": \"提高公众食品安全意识\", \"B\": \"促进食品安全行为改变\", \"C\": \"减少食品安全事故\", \"D\": \"增加食品销量\"}', 2, 'ABC', '食品安全教育的目的包括提高公众食品安全意识、促进食品安全行为改变和减少食品安全事故，但不直接影响食品销量。', 5, '2025-08-21 22:37:11');
INSERT INTO `question` VALUES (286, 10, '以下哪种食物中毒与蛋类消费相关？', '{\"A\": \"沙门氏菌中毒\", \"B\": \"副溶血性弧菌中毒\", \"C\": \"大肠杆菌中毒\", \"D\": \"肉毒杆菌中毒\"}', 1, 'A', '沙门氏菌中毒与蛋类消费密切相关，尤其是未充分加热的蛋类。', 5, '2025-08-21 22:37:11');
INSERT INTO `question` VALUES (287, 10, '食品加工场所的排水系统应如何设计以防止污染？', '{\"A\": \"有防鼠、防虫设施\", \"B\": \"排水顺畅且不易堵塞\", \"C\": \"使用开放式排水沟\", \"D\": \"排水口直接连接外界水体\"}', 1, 'B', '食品加工场所的排水系统应设计为排水顺畅且不易堵塞，同时应有防鼠、防虫设施，但不应使用开放式排水沟或直接连接外界水体。', 5, '2025-08-21 22:37:11');
INSERT INTO `question` VALUES (288, 10, '以下哪些是食品安全监管的职责？（多选）', '{\"A\": \"制定食品安全标准\", \"B\": \"监督食品安全法律法规的执行\", \"C\": \"处理食品安全事故\", \"D\": \"提高食品价格\"}', 2, 'ABC', '食品安全监管的职责包括制定食品安全标准、监督食品安全法律法规的执行以及处理食品安全事故，但不直接影响食品价格。', 5, '2025-08-21 22:37:11');
INSERT INTO `question` VALUES (289, 11, '以下哪种人群需要特别关注维生素D的补充以预防佝偻病？', '{\"A\": \"孕妇\", \"B\": \"老年人\", \"C\": \"婴幼儿\", \"D\": \"运动员\"}', 1, 'C', '婴幼儿处于骨骼快速发育阶段，维生素D缺乏易导致佝偻病。中国居民膳食指南明确指出，婴幼儿需每日补充400IU维生素D以预防该疾病。', 5, '2025-08-21 22:37:18');
INSERT INTO `question` VALUES (290, 11, '针对糖尿病患者的饮食管理，以下哪些措施是正确的？（多选）', '{\"A\": \"控制总能量摄入\", \"B\": \"增加精制糖摄入\", \"C\": \"定时定量进餐\", \"D\": \"减少膳食纤维摄入\"}', 2, 'AC', '糖尿病患者需通过控制总能量摄入维持血糖稳定，定时定量进餐有助于避免血糖波动。增加精制糖和减少膳食纤维摄入均不利于血糖管理。', 5, '2025-08-21 22:37:18');
INSERT INTO `question` VALUES (291, 11, '以下哪种食物是高血压患者钠摄入的主要来源？', '{\"A\": \"新鲜蔬菜\", \"B\": \"全谷物\", \"C\": \"加工肉类\", \"D\": \"水果\"}', 1, 'C', '加工肉类（如腊肉、香肠）含盐量高，是高血压患者钠摄入的主要来源。中国营养学会建议，高血压患者每日钠摄入量应低于2.5克。', 5, '2025-08-21 22:37:18');
INSERT INTO `question` VALUES (292, 11, '肥胖人群的膳食纤维推荐摄入量是多少？', '{\"A\": \"15克/天\", \"B\": \"20克/天\", \"C\": \"25克/天\", \"D\": \"30克/天\"}', 1, 'D', '肥胖人群需通过增加膳食纤维摄入（30克/天）增强饱腹感，减少能量摄入。膳食纤维还可促进肠道蠕动，预防便秘。', 5, '2025-08-21 22:37:18');
INSERT INTO `question` VALUES (293, 11, '孕妇叶酸补充的最佳时间窗口是？（多选）', '{\"A\": \"孕前1个月\", \"B\": \"孕早期3个月\", \"C\": \"孕中期3个月\", \"D\": \"孕晚期3个月\"}', 2, 'AB', '叶酸可预防胎儿神经管畸形，补充时间应覆盖孕前3个月至孕早期3个月。此阶段是胎儿神经管发育的关键期。', 5, '2025-08-21 22:37:18');
INSERT INTO `question` VALUES (294, 11, '以下哪种营养素对运动员肌肉修复至关重要？', '{\"A\": \"碳水化合物\", \"B\": \"脂肪\", \"C\": \"蛋白质\", \"D\": \"维生素C\"}', 1, 'C', '蛋白质是肌肉修复的原料，运动员蛋白质推荐摄入量为1.5-2.0克/公斤体重/天。例如，一名70公斤的运动员每日需摄入105-140克蛋白质。', 5, '2025-08-21 22:37:18');
INSERT INTO `question` VALUES (295, 11, '老年人钙的推荐摄入量是多少？', '{\"A\": \"800毫克/天\", \"B\": \"1000毫克/天\", \"C\": \"1200毫克/天\", \"D\": \"1500毫克/天\"}', 1, 'C', '老年人因骨量流失加速，需通过摄入1200毫克/天钙预防骨质疏松。奶制品、豆制品和深绿色蔬菜是优质钙来源。', 5, '2025-08-21 22:37:18');
INSERT INTO `question` VALUES (296, 11, '以下哪些措施可改善孕妇缺铁性贫血？（多选）', '{\"A\": \"增加动物肝脏摄入\", \"B\": \"补充维生素C\", \"C\": \"减少红肉摄入\", \"D\": \"避免同时摄入钙剂\"}', 2, 'ABD', '动物肝脏富含血红素铁，维生素C可促进铁吸收，钙剂会抑制铁吸收。红肉是铁的重要来源，减少摄入会加重贫血。', 5, '2025-08-21 22:37:18');
INSERT INTO `question` VALUES (297, 11, '慢性肾病患者需严格限制哪种营养素摄入？', '{\"A\": \"蛋白质\", \"B\": \"膳食纤维\", \"C\": \"维生素D\", \"D\": \"钾\"}', 1, 'A', '慢性肾病患者需限制蛋白质摄入（0.6-0.8克/公斤体重/天）以减轻肾脏负担。高蛋白饮食会加速肾功能恶化。', 5, '2025-08-21 22:37:18');
INSERT INTO `question` VALUES (298, 11, '以下哪些食物适合乳糖不耐受人群？（多选）', '{\"A\": \"鲜奶\", \"B\": \"酸奶\", \"C\": \"奶酪\", \"D\": \"豆浆\"}', 2, 'BD', '酸奶中的乳酸菌可分解乳糖，豆浆不含乳糖。鲜奶和奶酪含乳糖，可能引发腹胀、腹泻等症状。', 5, '2025-08-21 22:37:18');
INSERT INTO `question` VALUES (299, 11, '烧伤患者早期营养支持的重点是？', '{\"A\": \"高蛋白饮食\", \"B\": \"高脂肪饮食\", \"C\": \"高碳水化合物饮食\", \"D\": \"低盐饮食\"}', 1, 'C', '烧伤患者早期处于高代谢状态，需通过高碳水化合物饮食（60-70%总能量）提供能量，减少蛋白质分解。', 5, '2025-08-21 22:37:18');
INSERT INTO `question` VALUES (300, 11, '以下哪种营养素缺乏会导致夜盲症？', '{\"A\": \"维生素A\", \"B\": \"维生素B1\", \"C\": \"维生素C\", \"D\": \"维生素D\"}', 1, 'A', '维生素A是视网膜感光物质的前体，缺乏会导致暗适应能力下降，引发夜盲症。动物肝脏、蛋黄和深色蔬菜是优质来源。', 5, '2025-08-21 22:37:18');
INSERT INTO `question` VALUES (301, 11, '痛风患者需限制哪种营养素摄入？', '{\"A\": \"蛋白质\", \"B\": \"嘌呤\", \"C\": \"钙\", \"D\": \"膳食纤维\"}', 1, 'B', '嘌呤代谢产物尿酸是痛风病因，患者需限制高嘌呤食物（如动物内脏、海鲜）。蛋白质摄入应选择低嘌呤的奶类、蛋类。', 5, '2025-08-21 22:37:18');
INSERT INTO `question` VALUES (302, 11, '以下哪些措施可预防孕妇妊娠期糖尿病？（多选）', '{\"A\": \"控制体重增长\", \"B\": \"增加膳食纤维摄入\", \"C\": \"减少运动\", \"D\": \"定期监测血糖\"}', 2, 'ABD', '控制体重增长（孕中期每周增重0.3-0.5公斤）、增加膳食纤维摄入和定期监测血糖可降低妊娠期糖尿病风险。孕妇需保持适度运动（如散步）。', 5, '2025-08-21 22:37:18');
INSERT INTO `question` VALUES (303, 11, '慢性阻塞性肺疾病（COPD）患者需增加哪种营养素摄入？', '{\"A\": \"蛋白质\", \"B\": \"脂肪\", \"C\": \"碳水化合物\", \"D\": \"维生素K\"}', 1, 'A', 'COPD患者因呼吸肌消耗增加，需通过高蛋白饮食（1.2-1.5克/公斤体重/天）维持肌肉质量。蛋白质还可增强免疫力，预防感染。', 5, '2025-08-21 22:37:18');
INSERT INTO `question` VALUES (304, 11, '以下哪种食物是维生素B12的良好来源？', '{\"A\": \"植物油\", \"B\": \"全谷物\", \"C\": \"动物肝脏\", \"D\": \"水果\"}', 1, 'C', '维生素B12仅存在于动物性食物中，动物肝脏含量最高。素食者需通过强化食品或补充剂满足需求。', 5, '2025-08-21 22:37:18');
INSERT INTO `question` VALUES (305, 11, '术后患者营养支持的首选途径是？', '{\"A\": \"口服营养补充\", \"B\": \"肠内营养\", \"C\": \"肠外营养\", \"D\": \"静脉输液\"}', 1, 'B', '肠内营养（如鼻饲管、胃造瘘）可维持肠道黏膜完整性，减少感染风险。仅在肠道功能衰竭时使用肠外营养。', 5, '2025-08-21 22:37:18');
INSERT INTO `question` VALUES (306, 11, '以下哪些措施可改善老年人便秘？（多选）', '{\"A\": \"增加膳食纤维摄入\", \"B\": \"多喝水\", \"C\": \"减少运动\", \"D\": \"定时排便\"}', 2, 'ABD', '膳食纤维可增加粪便体积，水可软化粪便，定时排便可建立规律。减少运动（如久坐）会加重便秘。', 5, '2025-08-21 22:37:18');
INSERT INTO `question` VALUES (307, 11, '慢性心力衰竭患者需限制哪种营养素摄入？', '{\"A\": \"钠\", \"B\": \"钾\", \"C\": \"钙\", \"D\": \"镁\"}', 1, 'A', '钠摄入过多会加重水肿，慢性心力衰竭患者每日钠摄入量应低于2克。钾摄入需根据血钾水平调整，避免高钾或低钾血症。', 5, '2025-08-21 22:37:18');
INSERT INTO `question` VALUES (308, 11, '以下哪种食物是Omega-3脂肪酸的最佳来源？', '{\"A\": \"牛肉\", \"B\": \"深海鱼\", \"C\": \"鸡蛋\", \"D\": \"坚果\"}', 1, 'B', '深海鱼（如三文鱼、沙丁鱼）富含EPA和DHA，可降低心血管疾病风险。植物油（如亚麻籽油）含α-亚麻酸，但转化率低。', 5, '2025-08-21 22:37:18');
INSERT INTO `question` VALUES (309, 11, '癌症患者放化疗期间需增加哪种营养素摄入？', '{\"A\": \"蛋白质\", \"B\": \"脂肪\", \"C\": \"碳水化合物\", \"D\": \"维生素A\"}', 1, 'A', '放化疗会加速蛋白质分解，患者需通过高蛋白饮食（1.2-1.5克/公斤体重/天）维持肌肉质量。蛋白质还可增强免疫力，促进伤口愈合。', 5, '2025-08-21 22:37:18');
INSERT INTO `question` VALUES (310, 11, '以下哪些措施可预防骨质疏松？（多选）', '{\"A\": \"增加钙摄入\", \"B\": \"补充维生素D\", \"C\": \"减少运动\", \"D\": \"避免吸烟\"}', 2, 'ABD', '钙和维生素D是骨健康的基础，运动（如负重训练）可刺激骨形成，吸烟会加速骨量流失。减少运动（如久坐）会增加骨质疏松风险。', 5, '2025-08-21 22:37:18');
INSERT INTO `question` VALUES (311, 11, '慢性肾病患者需限制哪种矿物质摄入？', '{\"A\": \"磷\", \"B\": \"镁\", \"C\": \"钙\", \"D\": \"铁\"}', 1, 'A', '慢性肾病患者因肾功能下降，磷排泄减少，需限制高磷食物（如动物内脏、加工肉类）。高磷血症会导致皮肤瘙痒、骨痛等症状。', 5, '2025-08-21 22:37:18');
INSERT INTO `question` VALUES (312, 11, '以下哪种食物是维生素C的良好来源？', '{\"A\": \"米饭\", \"B\": \"菠菜\", \"C\": \"柑橘\", \"D\": \"牛奶\"}', 1, 'C', '维生素C主要存在于新鲜水果和蔬菜中，柑橘类水果（如橙子、柠檬）含量最高。维生素C可促进胶原蛋白合成，增强免疫力。', 5, '2025-08-21 22:37:18');
INSERT INTO `question` VALUES (313, 11, '术后患者营养支持的目标是？', '{\"A\": \"快速增重\", \"B\": \"维持肌肉质量\", \"C\": \"减少蛋白质分解\", \"D\": \"促进伤口愈合\"}', 2, 'BCD', '术后患者需通过营养支持维持肌肉质量、减少蛋白质分解和促进伤口愈合。快速增重可能导致脂肪堆积，增加代谢负担。', 5, '2025-08-21 22:37:18');
INSERT INTO `question` VALUES (314, 11, '以下哪种营养素缺乏会导致坏血病？', '{\"A\": \"维生素A\", \"B\": \"维生素B1\", \"C\": \"维生素C\", \"D\": \"维生素D\"}', 1, 'C', '维生素C是胶原蛋白合成的前体，缺乏会导致毛细血管脆性增加，引发牙龈出血、关节疼痛等症状。坏血病曾是长期航海者的常见病。', 5, '2025-08-21 22:37:18');
INSERT INTO `question` VALUES (315, 11, '慢性阻塞性肺疾病（COPD）患者需增加哪种营养素摄入以改善呼吸肌功能？', '{\"A\": \"蛋白质\", \"B\": \"脂肪\", \"C\": \"碳水化合物\", \"D\": \"维生素E\"}', 1, 'A', 'COPD患者因呼吸肌消耗增加，需通过高蛋白饮食（1.2-1.5克/公斤体重/天）维持肌肉质量。蛋白质还可增强免疫力，预防感染。', 5, '2025-08-21 22:37:18');
INSERT INTO `question` VALUES (316, 11, '以下哪些措施可改善孕妇便秘？（多选）', '{\"A\": \"增加膳食纤维摄入\", \"B\": \"多喝水\", \"C\": \"减少运动\", \"D\": \"定时排便\"}', 2, 'ABD', '膳食纤维可增加粪便体积，水可软化粪便，定时排便可建立规律。减少运动（如久坐）会加重便秘。孕妇需保持适度运动（如散步）。', 5, '2025-08-21 22:37:18');
INSERT INTO `question` VALUES (317, 11, '慢性心力衰竭患者需限制哪种营养素摄入以减轻水肿？', '{\"A\": \"钠\", \"B\": \"钾\", \"C\": \"钙\", \"D\": \"镁\"}', 1, 'A', '钠摄入过多会加重水肿，慢性心力衰竭患者每日钠摄入量应低于2克。钾摄入需根据血钾水平调整，避免高钾或低钾血症。', 5, '2025-08-21 22:37:18');
INSERT INTO `question` VALUES (318, 11, '以下哪种食物是膳食纤维的最佳来源？', '{\"A\": \"精制米面\", \"B\": \"全谷物\", \"C\": \"肉类\", \"D\": \"奶制品\"}', 1, 'B', '全谷物（如燕麦、糙米）保留了麸皮和胚芽，富含膳食纤维。膳食纤维可促进肠道蠕动，预防便秘和结肠癌。', 5, '2025-08-21 22:37:18');
INSERT INTO `question` VALUES (319, 12, '1. 以下哪种营养素是人体能量的主要来源？', '{\"A\": \"蛋白质\", \"B\": \"脂肪\", \"C\": \"碳水化合物\", \"D\": \"维生素\"}', 1, 'C', '碳水化合物是人体能量的主要来源，它经过消化分解成葡萄糖后被身体吸收利用。', 5, '2025-08-21 22:37:26');
INSERT INTO `question` VALUES (320, 12, '2. 人体必需脂肪酸包括？', '{\"A\": \"软脂酸\", \"B\": \"硬脂酸\", \"C\": \"亚油酸和α-亚麻酸\", \"D\": \"花生四烯酸\"}', 1, 'C', '人体必需脂肪酸是指人体不能自身合成，必须从食物中摄取的脂肪酸，主要包括亚油酸和α-亚麻酸。', 5, '2025-08-21 22:37:26');
INSERT INTO `question` VALUES (321, 12, '3. 以下哪种维生素是脂溶性维生素？', '{\"A\": \"维生素B1\", \"B\": \"维生素C\", \"C\": \"维生素D\", \"D\": \"维生素B2\"}', 1, 'C', '脂溶性维生素包括维生素A、D、E、K，它们能溶于脂肪和有机溶剂中。', 5, '2025-08-21 22:37:26');
INSERT INTO `question` VALUES (322, 12, '4. 膳食中铁的良好来源是？', '{\"A\": \"蔬菜\", \"B\": \"水果\", \"C\": \"动物肝脏\", \"D\": \"谷类\"}', 1, 'C', '动物肝脏富含血红素铁，生物利用率高，是膳食中铁的良好来源。', 5, '2025-08-21 22:37:26');
INSERT INTO `question` VALUES (323, 12, '5. 以下哪种矿物质对维持骨骼健康至关重要？', '{\"A\": \"铁\", \"B\": \"钙\", \"C\": \"锌\", \"D\": \"硒\"}', 1, 'B', '钙是构成骨骼和牙齿的主要成分，对维持骨骼健康起着至关重要的作用。', 5, '2025-08-21 22:37:26');
INSERT INTO `question` VALUES (324, 12, '6. 以下哪种食物富含膳食纤维？', '{\"A\": \"瘦肉\", \"B\": \"鸡蛋\", \"C\": \"全谷物\", \"D\": \"牛奶\"}', 1, 'C', '全谷物保留了谷粒的完整结构，富含膳食纤维，有助于促进肠道蠕动。', 5, '2025-08-21 22:37:26');
INSERT INTO `question` VALUES (325, 12, '7. 以下哪种营养素具有抗氧化作用？', '{\"A\": \"维生素A\", \"B\": \"维生素B1\", \"C\": \"维生素C\", \"D\": \"维生素B2\"}', 1, 'C', '维生素C是一种强抗氧化剂，能清除体内自由基，保护细胞免受氧化损伤。', 5, '2025-08-21 22:37:26');
INSERT INTO `question` VALUES (326, 12, '8. 以下哪种食物是维生素B12的良好来源？', '{\"A\": \"植物油\", \"B\": \"蔬菜\", \"C\": \"动物性食物\", \"D\": \"水果\"}', 1, 'C', '维生素B12主要存在于动物性食物中，如肉类、鱼类、蛋类和奶类等。', 5, '2025-08-21 22:37:26');
INSERT INTO `question` VALUES (327, 12, '9. 以下哪种营养素有助于维持正常视力？', '{\"A\": \"维生素A\", \"B\": \"维生素C\", \"C\": \"维生素D\", \"D\": \"维生素E\"}', 1, 'A', '维生素A是构成视紫红质的成分，对维持正常视力起着重要作用。', 5, '2025-08-21 22:37:26');
INSERT INTO `question` VALUES (328, 12, '10. 以下哪种食物富含不饱和脂肪酸？', '{\"A\": \"猪油\", \"B\": \"牛油\", \"C\": \"鱼油\", \"D\": \"椰子油\"}', 1, 'C', '鱼油富含不饱和脂肪酸，特别是ω-3脂肪酸，对心血管健康有益。', 5, '2025-08-21 22:37:26');
INSERT INTO `question` VALUES (329, 12, '11. 以下哪些因素会影响食物中营养素的吸收？', '{\"A\": \"食物的烹饪方式\", \"B\": \"个体的消化功能\", \"C\": \"食物中其他成分的干扰\", \"D\": \"以上都是\"}', 1, 'D', '食物的烹饪方式、个体的消化功能以及食物中其他成分的干扰都会影响营养素的吸收。', 5, '2025-08-21 22:37:26');
INSERT INTO `question` VALUES (330, 12, '12. 以下哪种营养素在人体内可以转化为维生素A？', '{\"A\": \"胡萝卜素\", \"B\": \"叶黄素\", \"C\": \"番茄红素\", \"D\": \"花青素\"}', 1, 'A', '胡萝卜素在人体内可以转化为维生素A，具有维生素A的生理活性。', 5, '2025-08-21 22:37:26');
INSERT INTO `question` VALUES (331, 12, '13. 以下哪种食物是钙的良好来源且钙的吸收率较高？', '{\"A\": \"菠菜\", \"B\": \"豆腐\", \"C\": \"牛奶\", \"D\": \"虾皮\"}', 1, 'C', '牛奶不仅富含钙，而且钙的吸收率较高，是补钙的优质食物。', 5, '2025-08-21 22:37:26');
INSERT INTO `question` VALUES (332, 12, '14. 以下哪种营养素有助于维持神经系统的正常功能？', '{\"A\": \"维生素B1\", \"B\": \"维生素C\", \"C\": \"维生素D\", \"D\": \"维生素E\"}', 1, 'A', '维生素B1参与神经系统的代谢过程，对维持神经系统的正常功能起着重要作用。', 5, '2025-08-21 22:37:26');
INSERT INTO `question` VALUES (333, 12, '15. 以下哪种食物富含维生素K？', '{\"A\": \"绿叶蔬菜\", \"B\": \"水果\", \"C\": \"肉类\", \"D\": \"谷类\"}', 1, 'A', '绿叶蔬菜是维生素K的良好来源，如菠菜、西兰花等。', 5, '2025-08-21 22:37:26');
INSERT INTO `question` VALUES (334, 12, '16. 以下哪种营养素有助于促进铁的吸收？', '{\"A\": \"维生素C\", \"B\": \"维生素D\", \"C\": \"维生素E\", \"D\": \"维生素K\"}', 1, 'A', '维生素C能将三价铁还原为二价铁，从而促进铁的吸收。', 5, '2025-08-21 22:37:26');
INSERT INTO `question` VALUES (335, 12, '17. 以下哪种食物富含碘？', '{\"A\": \"海带\", \"B\": \"菠菜\", \"C\": \"苹果\", \"D\": \"大米\"}', 1, 'A', '海带等海藻类食物富含碘，是碘的良好来源。', 5, '2025-08-21 22:37:26');
INSERT INTO `question` VALUES (336, 12, '18. 以下哪种营养素在能量代谢中起重要作用？', '{\"A\": \"维生素B1\", \"B\": \"维生素B2\", \"C\": \"烟酸\", \"D\": \"以上都是\"}', 1, 'D', '维生素B1、维生素B2和烟酸都参与能量代谢过程，在能量代谢中起着重要作用。', 5, '2025-08-21 22:37:26');
INSERT INTO `question` VALUES (337, 12, '19. 以下哪种食物富含维生素E？', '{\"A\": \"植物油\", \"B\": \"动物油\", \"C\": \"瘦肉\", \"D\": \"水果\"}', 1, 'A', '植物油富含维生素E，如麦胚油、玉米油等。', 5, '2025-08-21 22:37:26');
INSERT INTO `question` VALUES (338, 12, '20. 以下哪种营养素有助于维持骨骼和牙齿的矿化？', '{\"A\": \"钙\", \"B\": \"磷\", \"C\": \"镁\", \"D\": \"以上都是\"}', 1, 'D', '钙、磷、镁都是构成骨骼和牙齿的重要成分，有助于维持骨骼和牙齿的矿化。', 5, '2025-08-21 22:37:26');
INSERT INTO `question` VALUES (339, 12, '21. 以下哪些食物属于优质蛋白质来源？', '{\"A\": \"大豆及其制品\", \"B\": \"肉类\", \"C\": \"蛋类\", \"D\": \"以上都是\"}', 1, 'D', '大豆及其制品、肉类、蛋类都含有丰富的优质蛋白质，生物利用率高。', 5, '2025-08-21 22:37:26');
INSERT INTO `question` VALUES (340, 12, '22. 以下哪种营养素有助于提高免疫力？', '{\"A\": \"维生素A\", \"B\": \"维生素C\", \"C\": \"锌\", \"D\": \"以上都是\"}', 1, 'D', '维生素A、维生素C和锌都参与免疫系统的功能调节，有助于提高免疫力。', 5, '2025-08-21 22:37:26');
INSERT INTO `question` VALUES (341, 12, '23. 以下哪种食物富含叶酸？', '{\"A\": \"绿叶蔬菜\", \"B\": \"水果\", \"C\": \"肉类\", \"D\": \"谷类\"}', 1, 'A', '绿叶蔬菜是叶酸的良好来源，如菠菜、生菜等。', 5, '2025-08-21 22:37:26');
INSERT INTO `question` VALUES (342, 12, '24. 以下哪种营养素有助于维持正常的血液凝固功能？', '{\"A\": \"维生素K\", \"B\": \"维生素C\", \"C\": \"维生素E\", \"D\": \"维生素A\"}', 1, 'A', '维生素K参与凝血因子的合成，对维持正常的血液凝固功能起着重要作用。', 5, '2025-08-21 22:37:26');
INSERT INTO `question` VALUES (343, 12, '25. 以下哪些因素可能导致营养素缺乏？', '{\"A\": \"膳食摄入不足\", \"B\": \"消化吸收障碍\", \"C\": \"需求量增加\", \"D\": \"以上都是\"}', 1, 'D', '膳食摄入不足、消化吸收障碍以及需求量增加都可能导致营养素缺乏。', 5, '2025-08-21 22:37:26');
INSERT INTO `question` VALUES (344, 12, '26. 以下哪种食物富含硒？', '{\"A\": \"海产品\", \"B\": \"蔬菜\", \"C\": \"水果\", \"D\": \"谷类\"}', 1, 'A', '海产品如虾、蟹、贝类等富含硒，是硒的良好来源。', 5, '2025-08-21 22:37:26');
INSERT INTO `question` VALUES (345, 12, '27. 以下哪种营养素有助于维持皮肤和黏膜的健康？', '{\"A\": \"维生素A\", \"B\": \"维生素B2\", \"C\": \"维生素C\", \"D\": \"以上都是\"}', 1, 'D', '维生素A、维生素B2和维生素C都参与皮肤和黏膜的代谢和修复，有助于维持其健康。', 5, '2025-08-21 22:37:26');
INSERT INTO `question` VALUES (346, 12, '28. 以下哪种食物富含膳食纤维且属于可溶性膳食纤维？', '{\"A\": \"燕麦\", \"B\": \"芹菜\", \"C\": \"苹果\", \"D\": \"以上都是\"}', 2, 'AC', '燕麦中的β-葡聚糖和苹果中的果胶都属于可溶性膳食纤维，芹菜中的膳食纤维主要为不溶性膳食纤维。', 5, '2025-08-21 22:37:26');
INSERT INTO `question` VALUES (347, 12, '29. 以下哪些营养素属于宏量营养素？', '{\"A\": \"碳水化合物\", \"B\": \"脂肪\", \"C\": \"蛋白质\", \"D\": \"以上都是\"}', 2, 'ABC', '宏量营养素包括碳水化合物、脂肪和蛋白质，它们是人体能量的主要来源和构成身体组织的重要成分。', 5, '2025-08-21 22:37:26');
INSERT INTO `question` VALUES (348, 12, '30. 以下哪些食物富含抗氧化物质？', '{\"A\": \"蓝莓\", \"B\": \"绿茶\", \"C\": \"西兰花\", \"D\": \"以上都是\"}', 2, 'ABCD', '蓝莓富含花青素、绿茶富含茶多酚、西兰花富含维生素C和类胡萝卜素等，它们都具有抗氧化作用。', 5, '2025-08-21 22:37:26');
INSERT INTO `question` VALUES (349, 13, '1. 以下哪种营养素是人体主要的能量来源？', '{\"A\": \"蛋白质\", \"B\": \"脂肪\", \"C\": \"碳水化合物\", \"D\": \"维生素\"}', 1, 'C', '碳水化合物是人体最主要的能量来源，尤其是大脑和红细胞的主要供能物质。', 5, '2025-08-21 22:37:33');
INSERT INTO `question` VALUES (350, 13, '2. 以下哪种维生素缺乏可能导致夜盲症？', '{\"A\": \"维生素A\", \"B\": \"维生素B1\", \"C\": \"维生素C\", \"D\": \"维生素D\"}', 1, 'A', '维生素A缺乏会导致视网膜上的视紫红质合成不足，从而引发夜盲症。', 5, '2025-08-21 22:37:33');
INSERT INTO `question` VALUES (351, 13, '3. 以下哪种矿物质对维持骨骼健康最重要？', '{\"A\": \"铁\", \"B\": \"钙\", \"C\": \"锌\", \"D\": \"碘\"}', 1, 'B', '钙是构成骨骼和牙齿的主要成分，对维持骨骼健康至关重要。', 5, '2025-08-21 22:37:33');
INSERT INTO `question` VALUES (352, 13, '4. 以下哪种食物富含膳食纤维？', '{\"A\": \"瘦肉\", \"B\": \"鸡蛋\", \"C\": \"全谷物\", \"D\": \"牛奶\"}', 1, 'C', '全谷物富含膳食纤维，有助于促进肠道蠕动和预防便秘。', 5, '2025-08-21 22:37:33');
INSERT INTO `question` VALUES (353, 13, '5. 以下哪种营养素在体内可以转化为维生素A？', '{\"A\": \"胡萝卜素\", \"B\": \"叶黄素\", \"C\": \"番茄红素\", \"D\": \"玉米黄素\"}', 1, 'A', '胡萝卜素在体内可以转化为维生素A，具有维护视力、皮肤健康等功能。', 5, '2025-08-21 22:37:33');
INSERT INTO `question` VALUES (354, 13, '7. 以下哪种营养素有助于维持正常血压？', '{\"A\": \"钠\", \"B\": \"钾\", \"C\": \"氯\", \"D\": \"钙（此处考虑其对血管平滑肌的作用间接影响血压）\"}', 1, 'B', '钾有助于维持细胞内液的渗透压和酸碱平衡，同时参与心脏的正常节律和血压调节，适量摄入钾有助于降低高血压风险。', 5, '2025-08-21 22:37:33');
INSERT INTO `question` VALUES (355, 13, '8. 以下哪种食物富含不饱和脂肪酸？', '{\"A\": \"猪油\", \"B\": \"牛油\", \"C\": \"鱼油\", \"D\": \"椰子油\"}', 1, 'C', '鱼油富含不饱和脂肪酸，尤其是Omega-3脂肪酸，对心血管健康有益。', 5, '2025-08-21 22:37:33');
INSERT INTO `question` VALUES (356, 13, '9. 以下哪种维生素与血液凝固有关？', '{\"A\": \"维生素K\", \"B\": \"维生素E\", \"C\": \"维生素C\", \"D\": \"维生素B12\"}', 1, 'A', '维生素K是血液凝固过程中必需的辅酶，参与凝血因子的合成。', 5, '2025-08-21 22:37:33');
INSERT INTO `question` VALUES (357, 13, '10. 以下哪种食物是维生素C的良好来源？', '{\"A\": \"柑橘类水果\", \"B\": \"坚果\", \"C\": \"肉类\", \"D\": \"谷物\"}', 1, 'A', '柑橘类水果如橙子、柚子等富含维生素C，具有抗氧化作用，有助于增强免疫力。', 5, '2025-08-21 22:37:33');
INSERT INTO `question` VALUES (358, 13, '11. 以下哪种营养素对维持神经系统正常功能至关重要？', '{\"A\": \"镁\", \"B\": \"铁\", \"C\": \"锌\", \"D\": \"硒\"}', 1, 'A', '镁参与多种酶的激活和神经递质的释放，对维持神经系统正常功能至关重要。', 5, '2025-08-21 22:37:33');
INSERT INTO `question` VALUES (359, 13, '13. 以下哪种营养素有助于促进铁的吸收？', '{\"A\": \"钙\", \"B\": \"维生素C\", \"C\": \"锌\", \"D\": \"磷\"}', 1, 'B', '维生素C可以促进非血红素铁的吸收，提高铁的利用率。', 5, '2025-08-21 22:37:33');
INSERT INTO `question` VALUES (360, 13, '14. 以下哪种食物是钙的良好来源？', '{\"A\": \"菠菜\", \"B\": \"牛奶\", \"C\": \"香蕉\", \"D\": \"鸡肉\"}', 1, 'B', '牛奶是钙的良好来源，且钙的吸收率较高，有助于维持骨骼健康。', 5, '2025-08-21 22:37:33');
INSERT INTO `question` VALUES (361, 13, '15. 以下哪种营养素对维持正常视力有重要作用？', '{\"A\": \"维生素A\", \"B\": \"维生素B1\", \"C\": \"维生素D\", \"D\": \"维生素E\"}', 1, 'A', '维生素A对维持正常视力有重要作用，尤其是暗视觉功能。', 5, '2025-08-21 22:37:33');
INSERT INTO `question` VALUES (362, 13, '16. 以下哪种食物富含Omega-3脂肪酸？', '{\"A\": \"橄榄油\", \"B\": \"亚麻籽油\", \"C\": \"玉米油\", \"D\": \"花生油\"}', 1, 'B', '亚麻籽油富含Omega-3脂肪酸，尤其是α-亚麻酸，可以在体内转化为EPA和DHA。', 5, '2025-08-21 22:37:33');
INSERT INTO `question` VALUES (363, 13, '17. 以下哪种营养素有助于增强免疫力？', '{\"A\": \"维生素A\", \"B\": \"维生素B6\", \"C\": \"维生素C\", \"D\": \"维生素D（考虑其对免疫系统的调节作用）\"}', 1, 'C', '维生素C具有抗氧化作用，可以增强免疫细胞的活性，有助于增强免疫力。', 5, '2025-08-21 22:37:33');
INSERT INTO `question` VALUES (364, 13, '18. 以下哪种食物是铁的良好来源？', '{\"A\": \"红枣\", \"B\": \"牛奶\", \"C\": \"鸡蛋黄\", \"D\": \"豆腐\"}', 1, 'C', '鸡蛋黄是铁的良好来源之一，同时含有其他多种营养素。', 5, '2025-08-21 22:37:33');
INSERT INTO `question` VALUES (365, 13, '19. 以下哪种营养素对维持皮肤健康有重要作用？', '{\"A\": \"维生素A\", \"B\": \"维生素B2\", \"C\": \"维生素C\", \"D\": \"维生素E（考虑其抗氧化和保护皮肤的作用）\"}', 1, 'A', '维生素A对维持皮肤健康有重要作用，可以促进皮肤细胞的更新和修复。', 5, '2025-08-21 22:37:33');
INSERT INTO `question` VALUES (366, 13, '20. 以下哪种食物富含膳食纤维且低热量？', '{\"A\": \"薯片\", \"B\": \"蔬菜\", \"C\": \"糖果\", \"D\": \"炸鸡\"}', 1, 'B', '蔬菜富含膳食纤维且热量较低，有助于促进肠道蠕动和控制体重。', 5, '2025-08-21 22:37:33');
INSERT INTO `question` VALUES (367, 13, '21. 以下哪种营养素有助于降低胆固醇水平？', '{\"A\": \"饱和脂肪酸\", \"B\": \"不饱和脂肪酸\", \"C\": \"胆固醇\", \"D\": \"反式脂肪酸\"}', 1, 'B', '不饱和脂肪酸，尤其是单不饱和脂肪酸和多不饱和脂肪酸，有助于降低胆固醇水平，减少心血管疾病风险。', 5, '2025-08-21 22:37:33');
INSERT INTO `question` VALUES (368, 13, '23. 以下哪种营养素对维持牙齿健康有重要作用？', '{\"A\": \"氟\", \"B\": \"钙\", \"C\": \"磷\", \"D\": \"维生素D（考虑其对钙吸收的促进作用）\"}', 1, 'A', '氟是维持牙齿健康的重要元素，可以增强牙齿的抗酸性，预防龋齿。', 5, '2025-08-21 22:37:33');
INSERT INTO `question` VALUES (369, 13, '24. 以下哪种食物富含维生素D？', '{\"A\": \"蘑菇\", \"B\": \"鱼肝油\", \"C\": \"菠菜\", \"D\": \"苹果\"}', 1, 'B', '鱼肝油是维生素D的丰富来源，有助于促进钙的吸收和利用。', 5, '2025-08-21 22:37:33');
INSERT INTO `question` VALUES (370, 13, '25. 以下哪种营养素对维持电解质平衡有重要作用？', '{\"A\": \"钠\", \"B\": \"钾\", \"C\": \"钙\", \"D\": \"镁（考虑其在细胞内液中的作用）\"}', 1, 'A', '钠是维持细胞外液渗透压和电解质平衡的重要元素，但需注意适量摄入以避免高血压等健康问题。同时钾、钙、镁也参与电解质平衡，但此题选最直接相关的钠。', 5, '2025-08-21 22:37:33');
INSERT INTO `question` VALUES (371, 13, '27. 以下哪种营养素对维持正常血糖水平有重要作用？', '{\"A\": \"膳食纤维\", \"B\": \"脂肪\", \"C\": \"蛋白质\", \"D\": \"维生素\"}', 1, 'A', '膳食纤维可以减缓食物在胃肠道的消化速度，从而延缓葡萄糖的吸收，有助于维持正常血糖水平。', 5, '2025-08-21 22:37:33');
INSERT INTO `question` VALUES (372, 13, '28. 以下哪种食物是维生素E的良好来源？', '{\"A\": \"植物油\", \"B\": \"牛奶\", \"C\": \"鸡蛋\", \"D\": \"瘦肉\"}', 1, 'A', '植物油是维生素E的良好来源，尤其是小麦胚芽油、葵花籽油等。', 5, '2025-08-21 22:37:33');
INSERT INTO `question` VALUES (373, 14, '1. 以下哪项是群体营养管理中的关键步骤？', '{\"A\": \"个人饮食偏好调查\", \"B\": \"制定群体营养改善计划\", \"C\": \"单个营养素摄入量计算\", \"D\": \"个人体重监测\"}', 1, 'B', '群体营养管理需要综合考虑群体的整体需求，制定针对性的营养改善计划，而非仅关注个人或单一营养素。', 5, '2025-08-21 22:37:41');
INSERT INTO `question` VALUES (374, 14, '2. 个体营养评估中，BMI（身体质量指数）的计算公式是？', '{\"A\": \"体重（kg）/身高（m）\", \"B\": \"体重（kg）/身高²（m²）\", \"C\": \"身高（m）/体重（kg）\", \"D\": \"体重（lb）/身高²（in²）\"}', 1, 'B', 'BMI是体重（kg）除以身高（m）的平方，用于评估个体体重是否健康。', 5, '2025-08-21 22:37:41');
INSERT INTO `question` VALUES (375, 14, '3. 以下哪项是群体营养教育的主要目标？', '{\"A\": \"增加个人营养知识\", \"B\": \"改变群体不良饮食习惯\", \"C\": \"诊断个人营养缺乏症\", \"D\": \"制定个人饮食计划\"}', 1, 'B', '群体营养教育旨在通过普及营养知识，改变群体中的不良饮食习惯，提高整体营养水平。', 5, '2025-08-21 22:37:41');
INSERT INTO `question` VALUES (376, 14, '4. 个体化营养建议应基于哪些因素？', '{\"A\": \"年龄、性别\", \"B\": \"年龄、性别、身体活动水平\", \"C\": \"年龄、性别、身体活动水平、健康状况\", \"D\": \"仅基于年龄\"}', 1, 'C', '个体化营养建议需综合考虑年龄、性别、身体活动水平及健康状况等多方面因素。', 5, '2025-08-21 22:37:41');
INSERT INTO `question` VALUES (377, 14, '5. 群体营养监测中，常用的指标不包括？', '{\"A\": \"膳食摄入量\", \"B\": \"血液生化指标\", \"C\": \"个人体重变化\", \"D\": \"群体发病率\"}', 1, 'D', '群体营养监测主要关注膳食摄入、营养状况及健康指标，而发病率虽与营养相关，但不直接作为营养监测指标。', 5, '2025-08-21 22:37:41');
INSERT INTO `question` VALUES (378, 14, '7. 个体营养咨询中，最重要的环节是？', '{\"A\": \"收集个人信息\", \"B\": \"制定饮食计划\", \"C\": \"建立信任关系\", \"D\": \"评估营养状况\"}', 1, 'C', '建立信任关系是个体营养咨询的基础，有助于后续信息的准确收集和计划的顺利实施。', 5, '2025-08-21 22:37:41');
INSERT INTO `question` VALUES (379, 14, '8. 群体营养改善项目中，评估效果的主要指标是？', '{\"A\": \"个人满意度\", \"B\": \"膳食摄入量变化\", \"C\": \"营养知识知晓率\", \"D\": \"群体健康指标改善\"}', 1, 'D', '群体营养改善项目的最终目标是改善群体健康，因此评估效果的主要指标是群体健康指标的改善。', 5, '2025-08-21 22:37:41');
INSERT INTO `question` VALUES (380, 14, '9. 个体营养管理中，对于糖尿病患者，应重点关注哪类营养素的摄入？', '{\"A\": \"脂肪\", \"B\": \"蛋白质\", \"C\": \"碳水化合物\", \"D\": \"维生素\"}', 1, 'C', '糖尿病患者需严格控制碳水化合物的摄入，以维持血糖稳定。', 5, '2025-08-21 22:37:41');
INSERT INTO `question` VALUES (381, 14, '10. 群体营养教育中，哪种方法最有效提高参与度？', '{\"A\": \"发放宣传册\", \"B\": \"举办讲座\", \"C\": \"互动式工作坊\", \"D\": \"电视广告\"}', 1, 'C', '互动式工作坊能激发参与者的兴趣，提高营养教育的参与度和效果。', 5, '2025-08-21 22:37:41');
INSERT INTO `question` VALUES (382, 14, '11. 个体营养评估中，除了BMI，还应考虑？', '{\"A\": \"身高\", \"B\": \"腰围\", \"C\": \"鞋码\", \"D\": \"发型\"}', 1, 'B', '腰围是评估中心性肥胖的重要指标，与BMI结合使用能更全面地评估个体营养状况。', 5, '2025-08-21 22:37:41');
INSERT INTO `question` VALUES (383, 14, '12. 群体营养监测中，膳食调查的方法不包括？', '{\"A\": \"24小时回顾法\", \"B\": \"食物频率问卷\", \"C\": \"称重法\", \"D\": \"血液检测\"}', 1, 'D', '血液检测用于评估营养状况，而非膳食调查，膳食调查主要通过回顾法、问卷法和称重法等方法进行。', 5, '2025-08-21 22:37:41');
INSERT INTO `question` VALUES (384, 14, '13. 个体化营养计划制定时，应考虑个体的？', '{\"A\": \"饮食偏好\", \"B\": \"经济状况\", \"C\": \"文化背景\", \"D\": \"以上都是\"}', 1, 'D', '个体化营养计划需综合考虑个体的饮食偏好、经济状况和文化背景，以确保计划的可行性和有效性。', 5, '2025-08-21 22:37:41');
INSERT INTO `question` VALUES (385, 14, '14. 群体营养改善项目中，如何增强可持续性？', '{\"A\": \"短期集中干预\", \"B\": \"依赖外部资金\", \"C\": \"培养本地营养师资\", \"D\": \"忽视社区参与\"}', 1, 'C', '培养本地营养师资能确保项目的长期运行和持续改进，增强项目的可持续性。', 5, '2025-08-21 22:37:41');
INSERT INTO `question` VALUES (386, 14, '15. 个体营养咨询中，如何帮助客户设定合理的营养目标？', '{\"A\": \"设定高不可攀的目标\", \"B\": \"忽略客户意愿\", \"C\": \"结合客户实际情况\", \"D\": \"仅依赖营养师意见\"}', 1, 'C', '合理的营养目标应结合客户的实际情况，包括健康状况、饮食习惯和生活方式等，以确保目标的可行性和有效性。', 5, '2025-08-21 22:37:41');
INSERT INTO `question` VALUES (387, 14, '16. 群体营养教育中，如何针对不同年龄群体设计内容？', '{\"A\": \"使用相同教材\", \"B\": \"忽略年龄差异\", \"C\": \"根据年龄特点定制\", \"D\": \"仅针对成年人\"}', 1, 'C', '不同年龄群体有不同的营养需求和认知特点，因此应根据年龄特点定制营养教育内容。', 5, '2025-08-21 22:37:41');
INSERT INTO `question` VALUES (388, 14, '17. 个体营养管理中，对于高血压患者，应推荐哪种饮食模式？', '{\"A\": \"高脂饮食\", \"B\": \"高盐饮食\", \"C\": \"DASH饮食\", \"D\": \"高糖饮食\"}', 1, 'C', 'DASH饮食（膳食 approaches to stop hypertension）是一种低盐、低脂、高纤维的饮食模式，有助于降低血压。', 5, '2025-08-21 22:37:41');
INSERT INTO `question` VALUES (389, 14, '18. 群体营养监测中，如何收集准确的数据？', '{\"A\": \"依赖自我报告\", \"B\": \"使用标准化工具\", \"C\": \"忽略数据质量\", \"D\": \"仅收集部分数据\"}', 1, 'B', '使用标准化工具能确保数据的准确性和一致性，提高群体营养监测的质量。', 5, '2025-08-21 22:37:41');
INSERT INTO `question` VALUES (390, 14, '19. 个体营养咨询中，如何评估咨询效果？', '{\"A\": \"仅依赖客户反馈\", \"B\": \"使用标准化评估工具\", \"C\": \"忽略后续跟踪\", \"D\": \"仅关注短期变化\"}', 1, 'B', '使用标准化评估工具能客观、全面地评估咨询效果，包括客户的营养知识、态度和行为变化等。', 5, '2025-08-21 22:37:41');
INSERT INTO `question` VALUES (391, 14, '20. 群体营养改善项目中，如何促进社区参与？', '{\"A\": \"强制参与\", \"B\": \"忽视社区意见\", \"C\": \"建立社区营养小组\", \"D\": \"仅依赖政府支持\"}', 1, 'C', '建立社区营养小组能激发社区居民的积极性和参与度，促进项目的顺利实施和持续改进。', 5, '2025-08-21 22:37:41');
INSERT INTO `question` VALUES (392, 14, '21. 个体营养管理中，对于肥胖患者，应首先关注？', '{\"A\": \"增加运动量\", \"B\": \"减少热量摄入\", \"C\": \"服用减肥药\", \"D\": \"忽略饮食管理\"}', 1, 'B', '对于肥胖患者，减少热量摄入是首要任务，同时结合增加运动量等综合措施进行管理。', 5, '2025-08-21 22:37:41');
INSERT INTO `question` VALUES (393, 14, '22. 群体营养教育中，如何利用社交媒体？', '{\"A\": \"仅发布广告\", \"B\": \"忽略互动\", \"C\": \"发布营养知识，促进互动\", \"D\": \"仅针对年轻人\"}', 1, 'C', '利用社交媒体发布营养知识，促进用户互动，能扩大营养教育的覆盖面和影响力。', 5, '2025-08-21 22:37:41');
INSERT INTO `question` VALUES (394, 14, '23. 个体营养评估中，血液生化指标不包括？', '{\"A\": \"血糖\", \"B\": \"血脂\", \"C\": \"身高\", \"D\": \"血红蛋白\"}', 1, 'C', '血液生化指标包括血糖、血脂、血红蛋白等，用于评估个体的营养状况和健康风险，身高不属于血液生化指标。', 5, '2025-08-21 22:37:41');
INSERT INTO `question` VALUES (395, 14, '24. 群体营养监测中，如何分析数据？', '{\"A\": \"仅依赖直觉\", \"B\": \"使用统计软件\", \"C\": \"忽略异常值\", \"D\": \"仅进行描述性分析\"}', 1, 'B', '使用统计软件能对群体营养监测数据进行深入分析，揭示数据背后的规律和趋势。', 5, '2025-08-21 22:37:41');
INSERT INTO `question` VALUES (396, 14, '25. 个体营养咨询中，如何处理客户的抵触情绪？', '{\"A\": \"强制实施计划\", \"B\": \"忽略客户感受\", \"C\": \"倾听客户意见，调整计划\", \"D\": \"仅依赖营养师意见\"}', 1, 'C', '倾听客户意见，理解其抵触情绪的原因，并调整计划以满足客户需求，是处理抵触情绪的有效方法。', 5, '2025-08-21 22:37:41');
INSERT INTO `question` VALUES (397, 14, '26. 群体营养改善项目中，如何评估项目成本效益？', '{\"A\": \"仅考虑直接成本\", \"B\": \"忽略长期效益\", \"C\": \"综合分析成本与效益\", \"D\": \"仅依赖政府报告\"}', 1, 'C', '综合分析项目成本与效益，包括直接成本和间接成本，以及短期效益和长期效益，是评估项目成本效益的全面方法。', 5, '2025-08-21 22:37:41');
INSERT INTO `question` VALUES (398, 14, '27. 个体营养管理中，对于素食者，应重点关注哪类营养素的摄入？', '{\"A\": \"维生素B12\", \"B\": \"维生素C\", \"C\": \"钙\", \"D\": \"铁\"}', 2, 'AD', '素食者可能缺乏维生素B12和铁等营养素，因此应重点关注这些营养素的摄入，必要时可通过补充剂或强化食品来补充。', 5, '2025-08-21 22:37:41');
INSERT INTO `question` VALUES (399, 14, '28. 群体营养教育中，如何针对不同文化背景设计内容？', '{\"A\": \"使用统一教材\", \"B\": \"忽略文化差异\", \"C\": \"尊重文化差异，定制内容\", \"D\": \"仅针对主流文化\"}', 1, 'C', '尊重不同文化背景，定制符合其文化特点的营养教育内容，能提高教育的针对性和有效性。', 5, '2025-08-21 22:37:41');
INSERT INTO `question` VALUES (400, 14, '29. 个体营养咨询中，如何帮助客户建立健康的饮食习惯？', '{\"A\": \"强制客户改变\", \"B\": \"忽略客户意愿\", \"C\": \"提供个性化建议，逐步引导\", \"D\": \"仅依赖营养师监督\"}', 1, 'C', '提供个性化建议，结合客户的实际情况和意愿，逐步引导客户建立健康的饮食习惯，是更可行和有效的方法。', 5, '2025-08-21 22:37:41');
INSERT INTO `question` VALUES (401, 14, '30. 群体营养监测中，如何确保数据的代表性？', '{\"A\": \"仅选择特定人群\", \"B\": \"忽略抽样方法\", \"C\": \"使用随机抽样方法\", \"D\": \"仅收集部分数据\"}', 1, 'C', '使用随机抽样方法能确保群体营养监测数据的代表性和可靠性，提高监测结果的准确性和可信度。', 5, '2025-08-21 22:37:41');
INSERT INTO `question` VALUES (402, 15, '婴幼儿满 6 月龄添加辅食时，首选的食物类别是？', '{\"A\": \"肉类泥\", \"B\": \"含铁米粉\", \"C\": \"蔬菜泥\", \"D\": \"水果泥\"}', 1, 'B', '婴幼儿满 6 月龄后，从母体获得的铁储备逐渐耗尽，且母乳或配方奶中铁含量较低，易发生缺铁性贫血。含铁米粉强化了铁元素，且质地细腻、易于消化吸收，能有效预防缺铁性贫血，因此是添加辅食的首选。', 5, '2025-08-21 22:37:49');
INSERT INTO `question` VALUES (403, 15, '妊娠期妇女每日额外需要增加的蛋白质摄入量（孕中期 vs 孕晚期）分别是多少？', '{\"A\": \"A.5g vs 10g\", \"B\": \"B.10g vs 15g\", \"C\": \"C.15g vs 20g\", \"D\": \"D.20g vs 25g\"}', 1, 'C', '根据《中国居民膳食指南（2022）》，妊娠期妇女孕中期每日需在非孕基础上额外增加 15g 蛋白质，孕晚期额外增加 20g 蛋白质，以满足胎儿生长发育、母体组织修复及胎盘发育的需求。', 5, '2025-08-21 22:37:49');
INSERT INTO `question` VALUES (404, 15, '对糖尿病患者进行营养管理时，核心原则不包括？', '{\"A\": \"控制总能量摄入，维持健康体重\", \"B\": \"严格限制碳水化合物摄入，每日不超过 100g\", \"C\": \"选择低升糖指数（GI）的食物\", \"D\": \"保证膳食纤维摄入，每日 25-30g\"}', 1, 'B', '糖尿病患者营养管理的核心是控制总能量，而非严格限制碳水化合物。碳水化合物应占总能量的 50%-60%，需选择低 GI 食物（如全谷物、杂豆、蔬菜），并合理分配到各餐，避免血糖大幅波动。严格限制碳水化合物可能导致能量不足、酮症风险增加，故 B 为错误选项。', 5, '2025-08-21 22:37:49');
INSERT INTO `question` VALUES (405, 15, '慢性肾病（CKD）3 期患者，饮食中蛋白质的推荐摄入量是？', '{\"A\": \"A.0.8-1.0g/(kg・d)\", \"B\": \"B.0.6-0.8g/(kg・d)\", \"C\": \"C.0.4-0.6g/(kg・d)\", \"D\": \"D.1.0-1.2g/(kg・d)\"}', 1, 'B', '根据《慢性肾脏病患者膳食指导》，CKD 1-2 期患者蛋白质推荐量为 0.8-1.0g/(kg・d)，CKD 3-5 期（未透析）患者为 0.6-0.8g/(kg・d)，透析患者可适当提高至 1.0-1.2g/(kg・d)，以减少氮质血症，延缓肾功能恶化。', 5, '2025-08-21 22:37:49');
INSERT INTO `question` VALUES (406, 15, '社区营养评估中，用于了解居民膳食结构和摄入量的常用方法是？', '{\"A\": \"体格测量\", \"B\": \"膳食调查（24 小时回顾法、食物频率法）\", \"C\": \"生化指标检测\", \"D\": \"问卷调查（健康知识知晓率）\"}', 1, 'B', '膳食调查是评估居民膳食结构、食物摄入量及营养状况的核心方法，常用 24 小时回顾法（快速了解短期摄入）、食物频率法（了解长期摄入习惯）、称重法（精准度最高）。体格测量（如身高、体重）评估生长发育或肥胖；生化指标（如血红蛋白、血糖）评估营养相关疾病；问卷调查（健康知识）评估营养教育效果，均不直接反映膳食摄入。', 5, '2025-08-21 22:37:49');
INSERT INTO `question` VALUES (407, 15, '学龄儿童（6-12 岁）每日钙的推荐摄入量是多少？', '{\"A\": \"A.600mg\", \"B\": \"B.800mg\", \"C\": \"C.1000mg\", \"D\": \"D.1200mg\"}', 1, 'B', '学龄儿童处于骨骼快速生长阶段，钙是骨骼发育的关键营养素。根据《中国居民膳食营养素参考摄入量（2013 版）》，6-12 岁儿童每日钙推荐摄入量为 800mg，13-17 岁青少年增至 1000mg，以满足骨骼生长需求。', 5, '2025-08-21 22:37:49');
INSERT INTO `question` VALUES (408, 15, '母乳喂养的足月新生儿，出生后多久开始补充维生素 D？', '{\"A\": \"出生后立即补充\", \"B\": \"出生后 1 周内\", \"C\": \"出生后 2 周内\", \"D\": \"出生后 1 个月内\"}', 1, 'C', '母乳中维生素 D 含量较低（约 2-4IU/100ml），无法满足新生儿需求。根据《婴幼儿喂养与营养指南（2022）》，足月新生儿出生后 2 周内开始补充维生素 D，每日 400IU，直至 2 岁，以预防维生素 D 缺乏性佝偻病。', 5, '2025-08-21 22:37:49');
INSERT INTO `question` VALUES (409, 15, '孕早期妇女每日叶酸的推荐摄入量是多少？', '{\"A\": \"A.100μg DFE\", \"B\": \"B.200μg DFE\", \"C\": \"C.400μg DFE\", \"D\": \"D.800μg DFE\"}', 1, 'D', '孕早期是胎儿神经管发育的关键时期，叶酸缺乏易导致胎儿神经管畸形（如脊柱裂、无脑儿）。根据指南，孕早期妇女每日叶酸推荐摄入量为 800μg DFE（膳食叶酸当量），其中 400μg 可通过补充剂获取，剩余从食物（如深绿色蔬菜、动物肝脏）中摄入。', 5, '2025-08-21 22:37:49');
INSERT INTO `question` VALUES (410, 15, '老年人因咀嚼功能下降，饮食调整建议不包括？', '{\"A\": \"将食物切碎、煮软，如将肉类制成肉末\", \"B\": \"多选择流质食物，如粥、汤替代固体食物\", \"C\": \"使用牙托或假牙，改善咀嚼能力\", \"D\": \"保证食物多样性，避免营养单一\"}', 1, 'B', '老年人咀嚼功能下降，可通过切碎食物、使用假牙改善，但长期以流质食物为主会导致咀嚼功能进一步退化，且可能因食物体积大、能量密度低导致能量和营养素摄入不足。应优先选择软食（如软饭、蒸蛋），而非完全替代为流质，故 B 错误。', 5, '2025-08-21 22:37:49');
INSERT INTO `question` VALUES (411, 15, '婴幼儿添加辅食的原则不包括？', '{\"A\": \"从少到多，从稀到稠\", \"B\": \"从一种到多种，每种食物观察 3-5 天\", \"C\": \"从细到粗，如先菜泥后菜末\", \"D\": \"辅食中优先添加盐和糖，提升口感\"}', 1, 'D', '婴幼儿肾脏发育不完善，过早添加盐会增加肾脏负担；糖会影响味觉发育，增加龋齿风险，且无营养价值。根据指南，婴幼儿辅食应保持原味，1 岁内不添加盐、糖、酱油等调味品，故 D 错误。', 5, '2025-08-21 22:37:49');
INSERT INTO `question` VALUES (412, 15, '慢性阻塞性肺疾病（COPD）患者营养管理的核心是？', '{\"A\": \"高能量、高蛋白饮食，改善呼吸肌功能\", \"B\": \"低能量饮食，减轻体重以降低呼吸负担\", \"C\": \"严格限制碳水化合物摄入，减少二氧化碳生成\", \"D\": \"高纤维饮食，预防便秘\"}', 1, 'A', 'COPD 患者因呼吸功能受损，呼吸肌消耗能量增加，易出现营养不良（肌肉流失、呼吸肌乏力），进一步加重呼吸功能障碍。营养管理核心是提供高能量（比普通人群高 10%-20%）、高蛋白（1.2-1.5g/(kg・d)）饮食，以维持呼吸肌功能，改善营养状况，故 A 正确；低能量会加重营养不良，B 错误；碳水化合物是主要能量来源，无需严格限制，C 错误；高纤维是辅助措施，非核心，D 错误。', 5, '2025-08-21 22:37:49');
INSERT INTO `question` VALUES (413, 15, '哺乳期妇女每日额外需要增加的能量摄入量是多少？', '{\"A\": \"A.300kcal\", \"B\": \"B.500kcal\", \"C\": \"C.800kcal\", \"D\": \"D.1000kcal\"}', 1, 'B', '哺乳期妇女需额外能量用于乳汁分泌，根据《中国居民膳食指南（2022）》，每日需在非哺乳期基础上增加 500kcal 能量，其中约 300kcal 来自膳食，200kcal 来自孕期储存的脂肪，以保证乳汁的质和量。', 5, '2025-08-21 22:37:49');
INSERT INTO `question` VALUES (414, 15, '下列哪种食物最适合作为慢性肾病患者的蛋白质来源（优质蛋白且必需氨基酸齐全）？', '{\"A\": \"大米\", \"B\": \"面粉\", \"C\": \"鸡蛋\", \"D\": \"红薯\"}', 1, 'C', '优质蛋白指必需氨基酸种类齐全、数量充足、比例适宜的蛋白质，主要来源于动物性食物（如蛋、奶、肉、鱼）和大豆及其制品。鸡蛋含有人体所需的全部必需氨基酸，生物利用率高（约 94%），是慢性肾病患者优质蛋白的理想来源；大米、面粉、红薯属于植物性食物，蛋白质为非优质蛋白，必需氨基酸不齐全，故 C 正确。', 5, '2025-08-21 22:37:49');
INSERT INTO `question` VALUES (415, 15, '孕晚期妇女出现下肢水肿，营养管理建议不包括？', '{\"A\": \"适当限制食盐摄入，每日不超过 5g\", \"B\": \"增加蛋白质摄入，维持血浆胶体渗透压\", \"C\": \"严格限制饮水，每日不超过 1000ml\", \"D\": \"抬高下肢，促进静脉回流\"}', 1, 'C', '孕晚期下肢水肿多为生理性（子宫压迫下腔静脉，静脉回流受阻），适当限制盐摄入（减少水钠潴留）、增加蛋白质（维持血浆胶体渗透压，减少组织水肿）、抬高下肢均有助于缓解。而严格限制饮水会导致血容量不足，影响胎盘血流和乳汁分泌，且水肿与饮水量无直接关联，每日饮水仍需保证 1500-1700ml，故 C 错误。', 5, '2025-08-21 22:37:49');
INSERT INTO `question` VALUES (416, 15, '糖尿病患者选择主食时，优先推荐的是？', '{\"A\": \"精白米饭\", \"B\": \"白面包\", \"C\": \"全麦面包\", \"D\": \"糯米\"}', 1, 'C', '糖尿病患者主食需选择低 GI、高膳食纤维的食物，以延缓血糖上升。全麦面包保留了麸皮和胚芽，膳食纤维含量高（约 5-8g/100g），GI 值较低（约 50-60）；而精白米饭、白面包、糯米均为精制碳水化合物，膳食纤维少，GI 值高（70 以上），易导致血糖快速升高，故 C 正确。', 5, '2025-08-21 22:37:49');
INSERT INTO `question` VALUES (417, 15, '婴幼儿满 1 岁后，每日奶量的推荐摄入量是多少？', '{\"A\": \"A.300-400ml\", \"B\": \"B.500-600ml\", \"C\": \"C.700-800ml\", \"D\": \"D.900-1000ml\"}', 1, 'A', '1 岁后婴幼儿以固体食物为主，奶为营养补充。根据《婴幼儿喂养与营养指南（2022）》，1-2 岁幼儿每日奶量推荐 300-400ml（包括牛奶、酸奶、奶酪等），过多奶量会占据胃容量，影响其他食物摄入，导致营养不均衡。', 5, '2025-08-21 22:37:49');
INSERT INTO `question` VALUES (418, 15, '下列哪种情况提示婴幼儿可能存在蛋白质 - 能量营养不良（PEM）？', '{\"A\": \"体重增长缓慢或停滞，低于同龄儿童第 3 百分位\", \"B\": \"身高增长正常，体重略低于同龄儿童第 10 百分位\", \"C\": \"皮肤弹性好，皮下脂肪丰满\", \"D\": \"精神状态良好，活动量大\"}', 1, 'A', '蛋白质 - 能量营养不良的核心表现是生长迟缓，尤其是体重增长缓慢或停滞，低于同龄儿童生长标准的第 3 百分位（重度营养不良）或第 10-25 百分位（轻度营养不良）。B 选项体重略低但身高正常，可能为轻度体重不足，不一定是 PEM；C、D 选项均为营养良好的表现，故 A 正确。', 5, '2025-08-21 22:37:49');
INSERT INTO `question` VALUES (419, 16, '在团餐食谱编制中，午餐的能量分配比例通常为多少？', '{\"A\": \"15%-20%\", \"B\": \"25%-30%\", \"C\": \"30%-40%\", \"D\": \"40%-50%\"}', 1, 'C', '团餐能量分配中，午餐的推荐比例为30%-40%。', 5, '2025-08-21 22:37:57');
INSERT INTO `question` VALUES (420, 16, '糖尿病患者个性化食谱中，碳水化合物的计算主要依据什么？', '{\"A\": \"患者的年龄\", \"B\": \"患者的体重和活动量\", \"C\": \"患者的血压水平\", \"D\": \"患者的血脂水平\"}', 1, 'B', '糖尿病患者个性化食谱的碳水化合物计算主要依据患者的体重和活动量来确定。', 5, '2025-08-21 22:37:57');
INSERT INTO `question` VALUES (421, 16, '以下哪种烹饪技术对营养素的保留最有优势？', '{\"A\": \"油炸\", \"B\": \"烤\", \"C\": \"蒸\", \"D\": \"煎\"}', 1, 'C', '蒸是一种能够较好保留食物营养素的烹饪方式。', 5, '2025-08-21 22:37:57');
INSERT INTO `question` VALUES (422, 16, '油炸对油脂的氧化有什么影响？', '{\"A\": \"降低过氧化值\", \"B\": \"提高过氧化值\", \"C\": \"不影响过氧化值\", \"D\": \"使油脂变清澈\"}', 1, 'B', '油炸过程中，油脂的氧化程度增加，导致过氧化值升高。', 5, '2025-08-21 22:37:57');
INSERT INTO `question` VALUES (423, 16, '在食材采购时，冬季应优先选择哪种类型的蔬菜以控制成本？', '{\"A\": \"热带水果\", \"B\": \"本地根茎类蔬菜\", \"C\": \"进口蔬菜\", \"D\": \"反季节蔬菜\"}', 1, 'B', '冬季优先选择本地根茎类蔬菜，既符合季节性，又能有效控制成本。', 5, '2025-08-21 22:37:57');
INSERT INTO `question` VALUES (424, 16, 'HACCP体系中的关键控制点（CCP）主要用于什么？', '{\"A\": \"确定食品的包装材料\", \"B\": \"监控食品生产过程中的安全风险\", \"C\": \"决定食品的定价策略\", \"D\": \"选择食品的广告宣传方式\"}', 1, 'B', 'HACCP体系中的关键控制点（CCP）用于监控食品生产过程中的安全风险，确保食品安全。', 5, '2025-08-21 22:37:57');
INSERT INTO `question` VALUES (425, 16, '冷链食品的温度监控是HACCP体系中的哪个环节？', '{\"A\": \"原料验收\", \"B\": \"加工过程\", \"C\": \"储存和运输\", \"D\": \"成品检验\"}', 1, 'C', '冷链食品的温度监控主要在储存和运输环节进行，以确保食品的质量和安全。', 5, '2025-08-21 22:37:57');
INSERT INTO `question` VALUES (426, 16, '预防食源性疾病时，以下哪项措施是预防交叉污染的关键？', '{\"A\": \"食物充分加热\", \"B\": \"生熟分开、工具专用\", \"C\": \"使用防腐剂\", \"D\": \"食物快速冷冻\"}', 1, 'B', '预防交叉污染的关键措施是生熟分开、工具专用，以避免不同食物间的污染。', 5, '2025-08-21 22:37:57');
INSERT INTO `question` VALUES (427, 16, '治疗膳食中的低嘌呤饮食主要适用于哪类人群？', '{\"A\": \"高血压患者\", \"B\": \"糖尿病患者\", \"C\": \"痛风患者\", \"D\": \"肥胖症患者\"}', 1, 'C', '低嘌呤饮食主要用于痛风患者，以减少尿酸的产生和沉积。', 5, '2025-08-21 22:37:57');
INSERT INTO `question` VALUES (428, 16, '学生餐的营养均衡标准中，蛋白质的供能比应为多少？', '{\"A\": \"5%-10%\", \"B\": \"12%-15%\", \"C\": \"20%-25%\", \"D\": \"30%-35%\"}', 1, 'B', '学生餐的营养均衡标准中，蛋白质的供能比应为12%-15%。', 5, '2025-08-21 22:37:57');
INSERT INTO `question` VALUES (429, 16, '在食谱编制中，早餐的能量分配比例通常为多少？', '{\"A\": \"10%-15%\", \"B\": \"25%-30%\", \"C\": \"35%-40%\", \"D\": \"45%-50%\"}', 1, 'B', '团餐能量分配中，早餐的推荐比例为25%-30%。', 5, '2025-08-21 22:37:57');
INSERT INTO `question` VALUES (430, 16, '以下哪种烹饪方式可能导致油脂的氧化程度增加？', '{\"A\": \"煮\", \"B\": \"炖\", \"C\": \"油炸\", \"D\": \"蒸\"}', 1, 'C', '油炸过程中，油脂与高温和空气接触，导致氧化程度增加。', 5, '2025-08-21 22:37:57');
INSERT INTO `question` VALUES (431, 16, '在食材成本控制中，选择本地季节性食材的主要优势是什么？', '{\"A\": \"提高食材的营养价值\", \"B\": \"降低运输和储存成本\", \"C\": \"增加食材的多样性\", \"D\": \"提高食材的口感\"}', 1, 'B', '选择本地季节性食材可以降低运输和储存成本，从而有效控制食材成本。', 5, '2025-08-21 22:37:57');
INSERT INTO `question` VALUES (432, 16, 'HACCP体系中的CCP确定主要基于什么？', '{\"A\": \"食品的口感和风味\", \"B\": \"食品生产过程中的潜在危害分析\", \"C\": \"食品的包装设计\", \"D\": \"食品的市场需求\"}', 1, 'B', 'HACCP体系中的CCP确定主要基于食品生产过程中的潜在危害分析。', 5, '2025-08-21 22:37:57');
INSERT INTO `question` VALUES (433, 16, '以下哪项是预防食源性疾病的重要措施？', '{\"A\": \"食物长时间存放\", \"B\": \"食物充分加热并保持清洁\", \"C\": \"使用过期食材\", \"D\": \"食物不分类存放\"}', 1, 'B', '预防食源性疾病的重要措施包括食物充分加热并保持清洁，以避免细菌和其他病原体的污染。', 5, '2025-08-21 22:37:57');
INSERT INTO `question` VALUES (434, 16, '低嘌呤饮食在医疗机构膳食中主要用于治疗哪种疾病？', '{\"A\": \"贫血\", \"B\": \"骨质疏松\", \"C\": \"痛风\", \"D\": \"糖尿病\"}', 1, 'C', '低嘌呤饮食主要用于痛风的治疗，以减少尿酸的产生。', 5, '2025-08-21 22:37:57');
INSERT INTO `question` VALUES (435, 16, '学生餐中，以下哪种营养素的供能比应控制在12%-15%？', '{\"A\": \"碳水化合物\", \"B\": \"脂肪\", \"C\": \"蛋白质\", \"D\": \"维生素\"}', 1, 'C', '学生餐中，蛋白质的供能比应控制在12%-15%。', 5, '2025-08-21 22:37:57');
INSERT INTO `question` VALUES (436, 16, '在食谱编制中，午餐的能量分配比例下限是多少？', '{\"A\": \"20%\", \"B\": \"25%\", \"C\": \"30%\", \"D\": \"35%\"}', 1, 'C', '在食谱编制中，午餐的能量分配比例下限是30%。', 5, '2025-08-21 22:37:57');
INSERT INTO `question` VALUES (437, 16, '糖尿病患者个性化食谱中，碳水化合物的摄入量主要根据什么调整？', '{\"A\": \"患者的身高\", \"B\": \"患者的体重和日常活动量\", \"C\": \"患者的血型\", \"D\": \"患者的职业\"}', 1, 'B', '糖尿病患者个性化食谱中，碳水化合物的摄入量主要根据患者的体重和日常活动量来调整。', 5, '2025-08-21 22:37:57');
INSERT INTO `question` VALUES (438, 16, '以下哪种烹饪方式对营养素的破坏最小？', '{\"A\": \"油炸\", \"B\": \"烤\", \"C\": \"蒸\", \"D\": \"煎\"}', 1, 'C', '蒸是一种对营养素破坏最小的烹饪方式，因为它不需要高温和油脂。', 5, '2025-08-21 22:37:57');
INSERT INTO `question` VALUES (439, 16, '在食材采购中，选择冬季本地根茎类蔬菜的主要原因是？', '{\"A\": \"口感更好\", \"B\": \"营养价值更高\", \"C\": \"成本更低且供应稳定\", \"D\": \"颜色更鲜艳\"}', 1, 'C', '选择冬季本地根茎类蔬菜的主要原因是成本更低且供应稳定。', 5, '2025-08-21 22:37:57');
INSERT INTO `question` VALUES (440, 16, 'HACCP体系中的关键控制点（CCP）监控通常包括哪些内容？', '{\"A\": \"食品的包装设计\", \"B\": \"食品生产过程中的温度、时间等参数\", \"C\": \"食品的广告宣传\", \"D\": \"食品的定价策略\"}', 1, 'B', 'HACCP体系中的关键控制点（CCP）监控通常包括食品生产过程中的温度、时间等关键参数。', 5, '2025-08-21 22:37:57');
INSERT INTO `question` VALUES (441, 16, '预防食源性疾病时，以下哪项措施是错误的？', '{\"A\": \"食物充分加热\", \"B\": \"生熟食物混放\", \"C\": \"保持厨房清洁\", \"D\": \"使用清洁的水和食材\"}', 1, 'B', '预防食源性疾病时，生熟食物应分开存放，以避免交叉污染。', 5, '2025-08-21 22:37:57');
INSERT INTO `question` VALUES (442, 16, '治疗膳食中的低盐饮食主要适用于哪类人群？', '{\"A\": \"肥胖症患者\", \"B\": \"高血压患者\", \"C\": \"糖尿病患者\", \"D\": \"痛风患者\"}', 1, 'B', '低盐饮食主要用于高血压患者，以控制血压水平。', 5, '2025-08-21 22:37:57');
INSERT INTO `question` VALUES (443, 16, '学生餐的营养均衡中，以下哪种营养素的重要性不容忽视？', '{\"A\": \"膳食纤维\", \"B\": \"酒精\", \"C\": \"咖啡因\", \"D\": \"添加剂\"}', 1, 'A', '学生餐的营养均衡中，膳食纤维的重要性不容忽视，它有助于促进消化和预防便秘。', 5, '2025-08-21 22:37:57');
INSERT INTO `question` VALUES (444, 16, '在食谱编制中，早餐的能量分配比例上限是多少？', '{\"A\": \"20%\", \"B\": \"25%\", \"C\": \"30%\", \"D\": \"35%\"}', 1, 'C', '在食谱编制中，早餐的能量分配比例上限是30%。', 5, '2025-08-21 22:37:57');
INSERT INTO `question` VALUES (445, 16, '糖尿病患者个性化食谱制定时，需要考虑哪些因素？（多选）', '{\"A\": \"患者的体重\", \"B\": \"患者的日常活动量\", \"C\": \"患者的血糖水平\", \"D\": \"患者的喜好\"}', 2, 'ABC', '糖尿病患者个性化食谱制定时，需要考虑患者的体重、日常活动量和血糖水平等因素。', 5, '2025-08-21 22:37:57');
INSERT INTO `question` VALUES (446, 16, 'HACCP体系在食品行业中的应用主要目的是什么？（多选）', '{\"A\": \"提高食品的口感\", \"B\": \"确保食品安全\", \"C\": \"降低生产成本\", \"D\": \"预防食源性疾病\"}', 2, 'BD', 'HACCP体系在食品行业中的应用主要目的是确保食品安全和预防食源性疾病。', 5, '2025-08-21 22:37:57');
INSERT INTO `question` VALUES (447, 16, '以下哪些措施可以有效预防食源性疾病？（多选）', '{\"A\": \"食物充分加热\", \"B\": \"生熟食物分开存放\", \"C\": \"使用过期食材\", \"D\": \"保持厨房清洁\"}', 2, 'ABD', '有效预防食源性疾病的措施包括食物充分加热、生熟食物分开存放和保持厨房清洁。', 5, '2025-08-21 22:37:57');
INSERT INTO `question` VALUES (448, 16, '医疗机构膳食中，治疗膳食的种类包括哪些？（多选）', '{\"A\": \"低盐饮食\", \"B\": \"低脂饮食\", \"C\": \"高蛋白饮食\", \"D\": \"普通饮食\"}', 2, 'ABC', '医疗机构膳食中，治疗膳食的种类包括低盐饮食、低脂饮食和高蛋白饮食等，以适应不同疾病的治疗需求。', 5, '2025-08-21 22:37:57');
INSERT INTO `question` VALUES (449, 17, '1. 胃排空时间对哪种营养素的吸收影响最大？', '{\"A\": \"脂肪\", \"B\": \"碳水化合物\", \"C\": \"蛋白质\", \"D\": \"水溶性维生素\"}', 1, 'B', '胃排空时间主要影响碳水化合物的吸收速度，尤其是快速消化的简单碳水化合物。', 5, '2025-08-21 22:38:06');
INSERT INTO `question` VALUES (450, 17, '2. 胰岛素的主要生理功能是？', '{\"A\": \"升高血糖\", \"B\": \"促进脂肪分解\", \"C\": \"降低血糖\", \"D\": \"抑制蛋白质合成\"}', 1, 'C', '胰岛素通过促进细胞摄取葡萄糖和抑制肝糖原分解来降低血糖水平。', 5, '2025-08-21 22:38:06');
INSERT INTO `question` VALUES (451, 17, '3. BMI 计算公式为？', '{\"A\": \"体重（kg）/身高（m）²\", \"B\": \"体重（kg）/身高（cm）²\", \"C\": \"身高（m）/体重（kg）²\", \"D\": \"身高（cm）/体重（kg）²\"}', 1, 'A', 'BMI 是体重（kg）除以身高（m）的平方，用于评估体重是否在健康范围内。', 5, '2025-08-21 22:38:06');
INSERT INTO `question` VALUES (452, 17, '4. 血清前白蛋白和转铁蛋白主要用于筛查哪种情况？', '{\"A\": \"贫血\", \"B\": \"营养不良\", \"C\": \"肝病\", \"D\": \"肾病\"}', 1, 'B', '血清前白蛋白和转铁蛋白是反映蛋白质营养状况的敏感指标，常用于营养不良筛查。', 5, '2025-08-21 22:38:06');
INSERT INTO `question` VALUES (453, 17, '5. 孕妇每日叶酸补充量应为多少？', '{\"A\": \"100-200μg/d\", \"B\": \"200-400μg/d\", \"C\": \"400-800μg/d\", \"D\": \"800-1000μg/d\"}', 1, 'C', '孕妇每日叶酸补充量为 400-800μg/d，以预防胎儿神经管缺陷。', 5, '2025-08-21 22:38:06');
INSERT INTO `question` VALUES (454, 17, '6. 老年人肌肉衰减综合征的营养干预措施包括哪些？', '{\"A\": \"优质蛋白 + 维生素 D\", \"B\": \"高脂肪饮食\", \"C\": \"低盐饮食\", \"D\": \"高糖饮食\"}', 1, 'A', '优质蛋白和维生素 D 的摄入有助于维持老年人肌肉质量和功能。', 5, '2025-08-21 22:38:06');
INSERT INTO `question` VALUES (455, 17, '7. 糖尿病患者的碳水化合物分配中，每餐占比应为多少？', '{\"A\": \"10%-15%\", \"B\": \"20%-30%\", \"C\": \"30%-40%\", \"D\": \"40%-50%\"}', 1, 'B', '糖尿病患者的碳水化合物分配应均衡，每餐占比 20%-30% 以控制血糖波动。', 5, '2025-08-21 22:38:06');
INSERT INTO `question` VALUES (456, 17, '8. DASH 饮食中，钾/钠比值应达到多少？', '{\"A\": \"≥1:1\", \"B\": \"≥2:1\", \"C\": \"≥3:1\", \"D\": \"≥4:1\"}', 1, 'C', 'DASH 饮食强调高钾低钠，钾/钠比值应≥3:1 以帮助降低高血压风险。', 5, '2025-08-21 22:38:06');
INSERT INTO `question` VALUES (457, 17, '9. 肾病患者肠内营养制剂应选择哪种配方？', '{\"A\": \"高蛋白配方\", \"B\": \"低蛋白配方\", \"C\": \"高脂肪配方\", \"D\": \"高糖配方\"}', 1, 'B', '肾病患者需限制蛋白质摄入，应选择低蛋白配方的肠内营养制剂。', 5, '2025-08-21 22:38:06');
INSERT INTO `question` VALUES (458, 17, '10. 肠外营养的常见并发症包括以下哪项？', '{\"A\": \"腹泻\", \"B\": \"导管感染\", \"C\": \"便秘\", \"D\": \"胃排空延迟\"}', 1, 'B', '导管感染是肠外营养的常见并发症之一，需严格无菌操作以预防。', 5, '2025-08-21 22:38:06');
INSERT INTO `question` VALUES (459, 17, '11. 肿瘤患者专用配方的能量密度应为多少？', '{\"A\": \"0.5-1.0kcal/mL\", \"B\": \"1.0-1.5kcal/mL\", \"C\": \"1.5-2.0kcal/mL\", \"D\": \"2.0-2.5kcal/mL\"}', 1, 'C', '肿瘤患者专用配方的能量密度通常为 1.5-2.0kcal/mL，以满足高能量需求。', 5, '2025-08-21 22:38:06');
INSERT INTO `question` VALUES (460, 17, '12. 术后康复期患者的蛋白质需求为多少？', '{\"A\": \"0.8-1.0g/kg/d\", \"B\": \"1.0-1.2g/kg/d\", \"C\": \"1.2-2.0g/kg/d\", \"D\": \"2.0-2.5g/kg/d\"}', 1, 'C', '术后康复期患者需增加蛋白质摄入以促进伤口愈合，推荐量为 1.2-2.0g/kg/d。', 5, '2025-08-21 22:38:06');
INSERT INTO `question` VALUES (461, 17, '13. 耐力项目运动员赛前碳水化合物补充策略是什么？', '{\"A\": \"低糖饮食\", \"B\": \"糖原负荷法\", \"C\": \"高脂肪饮食\", \"D\": \"高蛋白饮食\"}', 1, 'B', '糖原负荷法通过赛前高碳水化合物饮食增加肌糖原储备，提高耐力表现。', 5, '2025-08-21 22:38:06');
INSERT INTO `question` VALUES (462, 17, '14. 力量训练运动员的蛋白质摄入标准为多少？', '{\"A\": \"0.8-1.0g/kg/d\", \"B\": \"1.0-1.2g/kg/d\", \"C\": \"1.2-1.6g/kg/d\", \"D\": \"1.6-2.2g/kg/d\"}', 1, 'D', '力量训练运动员需更高蛋白质摄入以支持肌肉合成，推荐量为 1.6-2.2g/kg/d。', 5, '2025-08-21 22:38:06');
INSERT INTO `question` VALUES (463, 17, '15. 高温作业者每小时应补充多少水分？', '{\"A\": \"100-200mL\", \"B\": \"300-500mL\", \"C\": \"500-1000mL\", \"D\": \"1000-1500mL\"}', 1, 'C', '高温作业者每小时需补水 500-1000mL 以维持体液平衡。', 5, '2025-08-21 22:38:06');
INSERT INTO `question` VALUES (464, 17, '16. 高温作业者每小时应补充多少钠？', '{\"A\": \"0.1-0.5g\", \"B\": \"0.5-1g\", \"C\": \"1-2g\", \"D\": \"2-3g\"}', 1, 'C', '高温作业者每小时需补充钠 1-2g 以补偿汗液丢失。', 5, '2025-08-21 22:38:06');
INSERT INTO `question` VALUES (465, 17, '17. 以下哪项是消化系统的主要功能？', '{\"A\": \"调节血糖\", \"B\": \"吸收营养素\", \"C\": \"合成蛋白质\", \"D\": \"运输氧气\"}', 1, 'B', '消化系统的主要功能是分解食物并吸收营养素。', 5, '2025-08-21 22:38:06');
INSERT INTO `question` VALUES (466, 17, '18. 胰岛素分泌不足会导致哪种疾病？', '{\"A\": \"高血压\", \"B\": \"糖尿病\", \"C\": \"贫血\", \"D\": \"骨质疏松\"}', 1, 'B', '胰岛素分泌不足会导致血糖升高，进而引发糖尿病。', 5, '2025-08-21 22:38:06');
INSERT INTO `question` VALUES (467, 17, '19. BMI 正常范围是多少？', '{\"A\": \"<18.5\", \"B\": \"18.5-23.9\", \"C\": \"24-27.9\", \"D\": \"≥28\"}', 1, 'B', 'BMI 正常范围为 18.5-23.9，低于或高于此范围可能提示体重异常。', 5, '2025-08-21 22:38:06');
INSERT INTO `question` VALUES (468, 17, '20. 腰围/臀围比（WHR）主要用于评估哪种健康风险？', '{\"A\": \"心血管疾病\", \"B\": \"贫血\", \"C\": \"肝病\", \"D\": \"肾病\"}', 1, 'A', 'WHR 是评估中心性肥胖的指标，与心血管疾病风险密切相关。', 5, '2025-08-21 22:38:06');
INSERT INTO `question` VALUES (469, 17, '21. 孕妇叶酸缺乏可能导致哪种胎儿畸形？', '{\"A\": \"唇腭裂\", \"B\": \"神经管缺陷\", \"C\": \"先天性心脏病\", \"D\": \"肢体畸形\"}', 1, 'B', '孕妇叶酸缺乏会增加胎儿神经管缺陷的风险，如脊柱裂。', 5, '2025-08-21 22:38:06');
INSERT INTO `question` VALUES (470, 17, '22. 老年人肌肉衰减综合征的主要表现是什么？', '{\"A\": \"体重增加\", \"B\": \"肌肉质量减少\", \"C\": \"骨密度升高\", \"D\": \"代谢率升高\"}', 1, 'B', '老年人肌肉衰减综合征的主要表现是肌肉质量减少和功能下降。', 5, '2025-08-21 22:38:06');
INSERT INTO `question` VALUES (471, 17, '23. 糖尿病患者的碳水化合物选择应优先选择哪种类型？', '{\"A\": \"简单碳水化合物\", \"B\": \"复杂碳水化合物\", \"C\": \"高糖食物\", \"D\": \"低纤维食物\"}', 1, 'B', '糖尿病患者应优先选择复杂碳水化合物（如全谷物）以控制血糖波动。', 5, '2025-08-21 22:38:06');
INSERT INTO `question` VALUES (472, 17, '24. DASH 饮食的核心原则是什么？', '{\"A\": \"高脂肪低纤维\", \"B\": \"高蛋白低碳水\", \"C\": \"高钾低钠\", \"D\": \"高糖低蛋白\"}', 1, 'C', 'DASH 饮食的核心原则是高钾低钠，以帮助降低高血压风险。', 5, '2025-08-21 22:38:06');
INSERT INTO `question` VALUES (473, 17, '25. 肠内营养制剂的选择原则应根据什么？', '{\"A\": \"患者口味偏好\", \"B\": \"疾病类型和营养需求\", \"C\": \"制剂价格\", \"D\": \"医生个人经验\"}', 1, 'B', '肠内营养制剂的选择应根据疾病类型和营养需求进行个性化调整。', 5, '2025-08-21 22:38:06');
INSERT INTO `question` VALUES (474, 17, '26. 肠外营养的适应症不包括以下哪项？', '{\"A\": \"短肠综合征\", \"B\": \"严重腹泻\", \"C\": \"完全性肠梗阻\", \"D\": \"轻度营养不良\"}', 1, 'D', '肠外营养适用于无法通过肠道摄入营养的患者，轻度营养不良通常不需肠外营养。', 5, '2025-08-21 22:38:06');
INSERT INTO `question` VALUES (475, 17, '27. 肿瘤患者专用配方的特点是什么？', '{\"A\": \"高脂肪低能量\", \"B\": \"高蛋白低能量\", \"C\": \"高能量高蛋白\", \"D\": \"低能量低蛋白\"}', 1, 'C', '肿瘤患者专用配方通常为高能量高蛋白，以满足代谢需求和维持体重。', 5, '2025-08-21 22:38:06');
INSERT INTO `question` VALUES (476, 17, '28. 术后康复期患者增加蛋白质摄入的目的是什么？', '{\"A\": \"促进脂肪合成\", \"B\": \"促进伤口愈合\", \"C\": \"降低血糖\", \"D\": \"减少肌肉流失\"}', 2, 'BD', '术后康复期患者增加蛋白质摄入可促进伤口愈合并减少肌肉流失。', 5, '2025-08-21 22:38:06');
INSERT INTO `question` VALUES (477, 17, '29. 耐力项目运动员赛前糖原负荷法的实施时间通常为多久？', '{\"A\": \"赛前1天\", \"B\": \"赛前3天\", \"C\": \"赛前1周\", \"D\": \"赛前2周\"}', 1, 'B', '糖原负荷法通常在赛前3天进行，通过高碳水化合物饮食增加肌糖原储备。', 5, '2025-08-21 22:38:06');
INSERT INTO `question` VALUES (478, 17, '30. 高温作业者水盐补充的注意事项包括哪些？', '{\"A\": \"仅补充水分\", \"B\": \"仅补充钠\", \"C\": \"同时补充水分和电解质\", \"D\": \"避免补充钾\"}', 1, 'C', '高温作业者需同时补充水分和电解质（如钠）以维持体液平衡。', 5, '2025-08-21 22:38:06');
INSERT INTO `question` VALUES (479, 18, '1.根据《中国居民膳食指南（2025版）》，植物性蛋白推荐占比为多少？', '{\"A\": \"A.30%-35%\", \"B\": \"B.45%-50%\", \"C\": \"C.55%-60%\", \"D\": \"D.25%-30%\"}', 1, 'B', '《中国居民膳食指南（2025版）》明确新增植物性蛋白推荐占比内容，推荐占比为45%-50%，该比例有助于平衡膳食结构，提升膳食营养质量，故答案选B。', 5, '2025-08-21 22:38:15');
INSERT INTO `question` VALUES (480, 18, '2.下列哪项不属于DRIs（膳食营养素参考摄入量）中的核心指标？', '{\"A\": \"A.EAR\", \"B\": \"B.RNI\", \"C\": \"C.UL\", \"D\": \"D.BMI\"}', 1, 'D', 'DRIs的核心指标包括EAR（平均需要量）、RNI（推荐摄入量）、UL（可耐受最高摄入量）等；BMI（身体质量指数）是评估人体体重与身高关系的指标，不属于DRIs范畴，故答案选D。', 5, '2025-08-21 22:38:15');
INSERT INTO `question` VALUES (481, 18, '3.女性铁的RNI（推荐摄入量）为多少？', '{\"A\": \"A.15mg/d\", \"B\": \"B.20mg/d\", \"C\": \"C.25mg/d\", \"D\": \"D.18mg/d\"}', 1, 'B', '根据DRIs应用标准，女性因生理特点（如月经周期失血）对铁的需求高于男性，其铁的RNI明确为20mg/d，故答案选B。', 5, '2025-08-21 22:38:15');
INSERT INTO `question` VALUES (482, 18, '《食品安全法》规定，食品营养标签的强制标示内容不包括以下哪项？', '{\"A\": \"A.能量\", \"B\": \"B.维生素C\", \"C\": \"C.蛋白质\", \"D\": \"D.钠\"}', 1, 'B', '《食品安全法》明确食品营养标签强制标示内容为能量、蛋白质、脂肪、碳水化合物、钠，维生素C不属于强制标示内容，故答案选B。', 5, '2025-08-21 22:38:15');
INSERT INTO `question` VALUES (483, 18, '4.24小时膳食回顾法的实施步骤不包括以下哪项？', '{\"A\": \"A.确定调查对象\", \"B\": \"B.记录调查对象未来24小时膳食计划\", \"C\": \"C.询问调查对象过去24小时进食情况\", \"D\": \"D.整理与分析膳食数据\"}', 1, 'B', '24小时膳食回顾法是通过询问调查对象过去24小时实际进食情况来获取膳食信息，核心是“回顾过去”，而非记录“未来膳食计划”，故答案选B。', 5, '2025-08-21 22:38:15');
INSERT INTO `question` VALUES (484, 18, '5.称重法在实施过程中，为控制误差，不需要重点关注的是哪项？', '{\"A\": \"A.食物称重工具的准确性\", \"B\": \"B.调查对象的饮食习惯偏好\", \"C\": \"C.食物烹饪前后的重量变化\", \"D\": \"D.食物废弃部分的重量记录\"}', 1, 'B', '称重法误差控制重点在于重量数据的准确性，包括称重工具精度、烹饪前后重量变化、废弃部分重量记录等；调查对象饮食习惯偏好与重量数据误差无关，故答案选B。', 5, '2025-08-21 22:38:15');
INSERT INTO `question` VALUES (485, 18, '6.智能膳食评估工具中，AI图像识别技术的核心应用是以下哪项？', '{\"A\": \"A.分析调查对象的运动频率\", \"B\": \"B.识别食物种类并估算食物重量\", \"C\": \"C.评估调查对象的健康风险\", \"D\": \"D.制定个性化运动方案\"}', 1, 'B', 'AI图像识别技术在智能膳食评估工具中的核心作用是通过拍摄食物图像，自动识别食物种类，并结合图像特征估算食物重量，为膳食评估提供基础数据，故答案选B。', 5, '2025-08-21 22:38:15');
INSERT INTO `question` VALUES (486, 18, '7.老年人骨质疏松干预项目的效果评估指标不包括以下哪项？', '{\"A\": \"A.骨密度变化\", \"B\": \"B.骨折发生率\", \"C\": \"C.身高变化\", \"D\": \"D.血糖水平\"}', 1, 'D', '老年人骨质疏松干预效果评估需围绕骨骼健康相关指标，如骨密度（直接反映骨骼强度）、骨折发生率（干预有效性的关键结局）、身高变化（间接反映椎体压缩情况）；血糖水平与骨质疏松无直接关联，不属于评估指标，故答案选D。', 5, '2025-08-21 22:38:15');
INSERT INTO `question` VALUES (487, 18, '8.营养教育短视频的制作要点中，关于时长的推荐要求是？', '{\"A\": \"A.时长≤5分钟\", \"B\": \"B.时长≤3分钟\", \"C\": \"C.时长≥10分钟\", \"D\": \"D.时长5-8分钟\"}', 1, 'B', '营养教育短视频需适应受众碎片化信息获取习惯，核心是快速传递核心信息，故制作要点明确时长应≤3分钟，避免因时长过长导致受众注意力流失，故答案选B。', 5, '2025-08-21 22:38:15');
INSERT INTO `question` VALUES (488, 18, '9.营养教育短视频制作中，以下哪项不符合“突出核心信息”的要点？', '{\"A\": \"A.开篇明确本次科普的营养主题\", \"B\": \"B.围绕主题展开1-2个关键知识点\", \"C\": \"C.同时讲解5个以上不同营养话题\", \"D\": \"D.结尾总结核心内容并给出行动建议\"}', 1, 'C', '“突出核心信息”要求营养教育短视频聚焦单一或少数关键主题，避免信息过载；同时讲解5个以上不同营养话题会导致重点不突出，不符合该要点，故答案选C。', 5, '2025-08-21 22:38:15');
INSERT INTO `question` VALUES (489, 18, '10.可持续膳食中，植物基饮食对碳排放的影响是以下哪项？', '{\"A\": \"A.显著增加碳排放\", \"B\": \"B.与动物性饮食碳排放无差异\", \"C\": \"C.显著降低碳排放\", \"D\": \"D.会导致碳排放波动无规律\"}', 1, 'C', '植物基饮食的生产过程（如种植谷物、蔬菜）相比动物性饮食（如养殖畜禽）消耗更少的能源、水资源，且减少畜禽养殖产生的甲烷排放，因此能显著降低碳排放，故答案选C。', 5, '2025-08-21 22:38:15');
INSERT INTO `question` VALUES (490, 18, '11.评估食物系统的资源利用效率时，不需要考虑的指标是以下哪项？', '{\"A\": \"A.单位食物产量的水资源消耗量\", \"B\": \"B.食物生产过程中的能源利用率\", \"C\": \"C.食物的销售价格\", \"D\": \"D.食物生产过程中的土地利用率\"}', 1, 'C', '食物系统资源利用效率评估围绕“资源投入-食物产出”关系，包括水资源消耗、能源利用率、土地利用率等；食物销售价格受市场供需、成本等多种因素影响，与资源利用效率无直接关联，故答案选C。', 5, '2025-08-21 22:38:15');
INSERT INTO `question` VALUES (491, 18, '12.以下哪种重金属会抑制铁的吸收？', '{\"A\": \"A.锌\", \"B\": \"B.铅\", \"C\": \"C.铜\", \"D\": \"D.硒\"}', 1, 'B', '重金属对营养素吸收存在拮抗作用，其中铅会与铁竞争肠道吸收通道，显著抑制铁的吸收，可能导致缺铁性贫血等问题，故答案选B。', 5, '2025-08-21 22:38:15');
INSERT INTO `question` VALUES (492, 18, '13.《中国居民膳食指南（2025版）》推荐每日摄入多少种食物？', '{\"A\": \"A.8种\", \"B\": \"B.10种\", \"C\": \"C.12种\", \"D\": \"D.15种\"}', 1, 'C', '《中国居民膳食指南（2025版）》新增每日食物搭配要求，明确推荐每日摄入12种食物，以实现膳食多样化，满足身体对多种营养素的需求，故答案选C。', 5, '2025-08-21 22:38:15');
INSERT INTO `question` VALUES (493, 18, '14.EAR（平均需要量）的定义是以下哪项？', '{\"A\": \"A.满足某一特定性别、年龄及生理状况群体中50%个体需要量的营养素摄入水平\", \"B\": \"B.满足某一特定性别、年龄及生理状况群体中97%-98%个体需要量的营养素摄入水平\", \"C\": \"C.某一特定性别、年龄及生理状况群体中个体可耐受的营养素最高摄入水平\", \"D\": \"D.为预防慢性病而推荐的营养素摄入水平\"}', 1, 'A', 'EAR（平均需要量）是指满足某一特定性别、年龄及生理状况群体中50%个体需要量的营养素摄入水平，是制定RNI的基础；B选项是RNI的定义，C选项是UL的定义，故答案选A。', 5, '2025-08-21 22:38:15');
INSERT INTO `question` VALUES (494, 18, '15.在膳食计划制定中，UL（可耐受最高摄入量）的作用是以下哪项？', '{\"A\": \"A.作为膳食营养素摄入的推荐目标\", \"B\": \"B.判断个体营养素摄入是否不足\", \"C\": \"C.避免个体营养素摄入过量导致不良反应\", \"D\": \"D.评估群体营养素摄入的平均水平\"}', 1, 'C', 'UL（可耐受最高摄入量）是指某一特定性别、年龄及生理状况群体中个体可耐受的营养素最高摄入水平，超过该水平可能增加不良反应风险，其核心作用是避免摄入过量，故答案选C。', 5, '2025-08-21 22:38:15');
INSERT INTO `question` VALUES (495, 18, '17.24小时膳食回顾法实施时，为提高数据准确性，可采取的措施有（多选）？', '{\"A\": \"A.引导调查对象按餐次顺序回忆\", \"B\": \"B.使用食物模型帮助调查对象估算食物量\", \"C\": \"C.调查前告知调查对象需回忆的时间范围\", \"D\": \"D.仅询问调查对象主食摄入情况，忽略辅食\"}', 2, 'ABC', '提高24小时膳食回顾法数据准确性的措施包括按餐次顺序引导回忆（避免遗漏）、使用食物模型辅助估算（减少量的误差）、提前明确回忆时间范围（确保回忆聚焦）；忽略辅食会导致数据不完整，降低准确性，故答案选A、B、C。', 5, '2025-08-21 22:38:15');
INSERT INTO `question` VALUES (496, 18, '18.称重法误差控制的关键环节包括（多选）？', '{\"A\": \"A.定期校准食物称重仪器\", \"B\": \"B.准确记录食物烹饪前的生重和烹饪后的熟重\", \"C\": \"C.记录食物在储存过程中的重量损失\", \"D\": \"D.忽略调查对象剩余食物的重量\"}', 2, 'ABC', '称重法误差控制需确保重量数据全程准确，包括校准仪器（保证测量精度）、记录烹饪前后重量（评估烹饪损失）、记录储存重量损失（避免数据偏差）；剩余食物重量会影响实际摄入量计算，不可忽略，故答案选A、B、C。', 5, '2025-08-21 22:38:15');
INSERT INTO `question` VALUES (497, 18, '19.智能膳食评估工具在社区营养干预中的优势包括（多选）？', '{\"A\": \"A.快速获取膳食数据，提高评估效率\", \"B\": \"B.减少人工记录误差，提升数据准确性\", \"C\": \"C.可实时反馈膳食建议，增强干预互动性\", \"D\": \"D.仅适用于年轻群体，无法覆盖老年人\"}', 2, 'ABC', '智能膳食评估工具的优势体现在效率（快速获取数据）、准确性（减少人工误差）、互动性（实时反馈建议）；目前部分工具已优化适老化设计，可覆盖老年人，D选项表述错误，故答案选A、B、C。', 5, '2025-08-21 22:38:15');
INSERT INTO `question` VALUES (498, 18, '20.老年人骨质疏松干预项目效果评估的核心指标包括（多选）？', '{\"A\": \"A.腰椎、髋部骨密度检测值变化\", \"B\": \"B.干预期间骨折发生次数\", \"C\": \"C.老年人钙和维生素D的膳食摄入量变化\", \"D\": \"D.老年人的体重变化\"}', 2, 'ABC', '老年人骨质疏松干预效果评估需围绕“骨骼健康-营养摄入”核心，骨密度变化（直接反映骨骼强度）、骨折次数（关键结局指标）、钙与维生素D摄入量（干预相关营养素）均为核心指标；体重变化与骨质疏松无直接关联，故答案选A、B、C。', 5, '2025-08-21 22:38:15');
INSERT INTO `question` VALUES (499, 18, '21.营养教育短视频制作中，符合“突出核心信息”要点的做法有（多选）？', '{\"A\": \"A.每个视频聚焦1个营养主题，如“如何补充膳食纤维”\", \"B\": \"B.用醒目字幕标注核心知识点，如“每日食盐摄入≤5g”\", \"C\": \"C.围绕主题展开3-5个延伸知识点，丰富内容\", \"D\": \"D.结尾用口诀总结核心信息，帮助记忆\"}', 2, 'ABD', '“突出核心信息”要求聚焦重点、强化记忆，包括单一主题（避免分散）、醒目字幕（突出关键）、口诀总结（辅助记忆）；展开3-5个延伸知识点会导致信息过载，偏离核心，故答案选A、B、D。', 5, '2025-08-21 22:38:15');
INSERT INTO `question` VALUES (500, 18, '22.可持续膳食中，评估食物系统资源利用效率的指标包括（多选）？', '{\"A\": \"A.单位食物产量的耕地面积占用\", \"B\": \"B.单位食物产量的化肥和农药使用量\", \"C\": \"C.食物从生产到消费环节的损耗率\", \"D\": \"D.食物的包装材料成本\"}', 2, 'ABC', '食物系统资源利用效率评估围绕“资源投入-损耗”，包括耕地占用（土地资源）、化肥农药使用（生产资源）、损耗率（资源浪费程度）；包装材料成本属于经济成本范畴，与资源利用效率无关，故答案选A、B、C。', 5, '2025-08-21 22:38:15');
INSERT INTO `question` VALUES (501, 18, '23.以下关于植物基饮食对环境影响的表述正确的有（多选）？', '{\"A\": \"A.植物基饮食生产过程中水资源消耗低于动物性饮食\", \"B\": \"B.植物基饮食可减少畜禽养殖产生的甲烷排放\", \"C\": \"C.植物基饮食对土壤肥力的破坏大于动物性饮食\", \"D\": \"D.植物基饮食生产过程中能源消耗更低\"}', 2, 'ABD', '植物基饮食相比动物性饮食，生产过程中水资源消耗更少（如种植蔬菜 vs 养殖牛）、甲烷排放更低（畜禽是甲烷主要来源）、能源消耗更低（减少饲料加工、畜禽养殖环节能耗）；合理的植物种植（如轮作）可维持土壤肥力，其对土壤破坏小于过度放牧的动物性饮食，故答案选A、B、D。', 5, '2025-08-21 22:38:15');
INSERT INTO `question` VALUES (502, 18, '24.重金属对营养素吸收的拮抗作用包括（多选）？', '{\"A\": \"A.铅抑制铁的吸收\", \"B\": \"B.汞抑制锌的吸收\", \"C\": \"C.铜促进铁的吸收\", \"D\": \"D.镉抑制钙的吸收\"}', 2, 'ABD', '重金属对营养素吸收多为拮抗作用（抑制吸收），如铅抑制铁、汞抑制锌、镉抑制钙；铜促进铁吸收属于协同作用，而非拮抗作用，故答案选A、B、D。', 5, '2025-08-21 22:38:15');
INSERT INTO `question` VALUES (503, 18, '25.在制定个体膳食计划时，DRIs中的RNI可作为以下哪项依据？', '{\"A\": \"A.判断个体营养素摄入是否过量的上限\", \"B\": \"B.满足个体97%-98%营养素需求的目标摄入量\", \"C\": \"C.满足群体50%个体营养素需求的平均摄入量\", \"D\": \"D.评估个体营养素缺乏风险的下限\"}', 1, 'B', 'RNI（推荐摄入量）是指满足某一特定性别、年龄及生理状况群体中97%-98%个体需要量的营养素摄入水平，可作为个体膳食计划中营养素摄入的目标值；A是UL的作用，C是EAR的定义，D是EAR的应用，故答案选B。', 5, '2025-08-21 22:38:15');
INSERT INTO `question` VALUES (504, 18, '26.食品营养标签中，“能量”的标示单位通常不包括以下哪项？', '{\"A\": \"A.千焦（kJ）\", \"B\": \"B.千卡（kcal）\", \"C\": \"C.瓦特（W）\", \"D\": \"D.千焦/100克（kJ/100g）\"}', 1, 'C', '食品营养标签中能量的常用标示单位为千焦（kJ）、千卡（kcal），或结合食物重量标注（如kJ/100g）；瓦特（W）是功率单位，与能量单位无关，故答案选C。', 5, '2025-08-21 22:38:15');
INSERT INTO `question` VALUES (505, 18, '27.社区营养调查中，若需快速了解大规模人群的膳食情况，最适宜采用的方法是？', '{\"A\": \"A.称重法\", \"B\": \"B.24小时膳食回顾法\", \"C\": \"C.食物频率法\", \"D\": \"D.记账法\"}', 1, 'C', '食物频率法通过询问调查对象过去一段时间内（如1个月、3个月）各类食物的摄入频率，可快速获取大规模人群的膳食结构特征，调查效率高；称重法精度高但耗时，24小时膳食回顾法适合个体，记账法适合家庭，故答案选C。', 5, '2025-08-21 22:38:15');
INSERT INTO `question` VALUES (506, 18, '28.以下哪项不属于AI图像识别技术在智能膳食评估中的局限性？', '{\"A\": \"A.对形态相似食物（如大米和小米）识别准确率较低\", \"B\": \"B.无法识别食物的烹饪方式（如清蒸 vs 油炸）\", \"C\": \"C.可快速估算食物重量，减少人工误差\", \"D\": \"D.对复杂混合食物（如炒饭）中食材的区分难度大\"}', 1, 'C', 'AI图像识别技术的局限性包括相似食物识别难、烹饪方式无法识别、混合食物食材区分难；“快速估算重量、减少人工误差”是其优势，而非局限性，故答案选C。', 5, '2025-08-21 22:38:15');
INSERT INTO `question` VALUES (507, 18, '29.营养教育中，短视频相比传统讲座的优势不包括以下哪项？', '{\"A\": \"A.传播范围更广，可通过网络快速扩散\", \"B\": \"B.信息传递更深入，可系统讲解复杂理论\", \"C\": \"C.观看时间灵活，适应碎片化学习需求\", \"D\": \"D.形式更生动，可结合动画、实景提升吸引力\"}', 1, 'B', '短视频的优势在于传播广、时间灵活、形式生动，但受时长限制（≤3分钟），难以系统讲解复杂理论；传统讲座更适合深入、系统地传递复杂知识，故答案选B。', 5, '2025-08-21 22:38:15');
INSERT INTO `question` VALUES (508, 18, '30.以下关于环境污染与营养素吸收关系的表述，错误的是？', '{\"A\": \"A.铅污染会降低肠道对铁的吸收效率\", \"B\": \"B.汞污染可能影响锌的肠道转运过程\", \"C\": \"C.重金属污染仅影响宏量营养素吸收，对微量营养素无影响\", \"D\": \"D.镉污染可能干扰钙在骨骼中的沉积\"}', 1, 'C', '重金属污染对营养素吸收的影响涵盖微量营养素（如铅影响铁、汞影响锌、镉影响钙），对宏量营养素吸收也可能存在间接影响（如肠道黏膜损伤导致整体吸收功能下降）；C选项“仅影响宏量营养素，对微量营养素无影响”的表述错误，故答案选C。', 5, '2025-08-21 22:38:15');
INSERT INTO `question` VALUES (509, 19, '1. 以下哪项不属于注册营养师的职业守则？', '{\"A\": \"诚实守信\", \"B\": \"保护客户隐私\", \"C\": \"追求个人利益最大化\", \"D\": \"避免利益冲突\"}', 1, 'C', '注册营养师的职业守则包括诚实守信、保护客户隐私、避免利益冲突，不包括追求个人利益最大化。', 5, '2025-08-21 22:38:23');
INSERT INTO `question` VALUES (510, 19, '2. 在营养补剂推广中，注册营养师应承担的专业责任不包括以下哪一项？', '{\"A\": \"确保推广信息的真实性\", \"B\": \"夸大补剂效果以吸引客户\", \"C\": \"根据客户情况提供合理建议\", \"D\": \"了解补剂的成分和功效\"}', 1, 'B', '在营养补剂推广中，注册营养师应确保推广信息的真实性，根据客户情况提供合理建议，并了解补剂的成分和功效，而不应夸大补剂效果。', 5, '2025-08-21 22:38:23');
INSERT INTO `question` VALUES (511, 19, '3. 跨学科协作中，注册营养师应遵循的沟通规范不包括以下哪一项？', '{\"A\": \"尊重其他学科的专业意见\", \"B\": \"清晰、准确地传达营养相关信息\", \"C\": \"忽视其他学科的贡献，独自决策\", \"D\": \"与其他学科保持有效沟通\"}', 1, 'C', '跨学科协作中，注册营养师应尊重其他学科的专业意见，清晰、准确地传达营养相关信息，并与其他学科保持有效沟通，而不应忽视其他学科的贡献，独自决策。', 5, '2025-08-21 22:38:23');
INSERT INTO `question` VALUES (512, 19, '4. 精准营养主要研究的是哪方面的内容？', '{\"A\": \"营养素的化学结构\", \"B\": \"基因与营养的互作关系\", \"C\": \"营养素的生理功能\", \"D\": \"营养素的缺乏症\"}', 1, 'B', '精准营养主要研究基因与营养的互作关系，如MTHFR基因多态性与叶酸代谢等。', 5, '2025-08-21 22:38:23');
INSERT INTO `question` VALUES (513, 19, '5. MTHFR基因多态性主要影响哪种营养素的代谢？', '{\"A\": \"维生素C\", \"B\": \"叶酸\", \"C\": \"维生素D\", \"D\": \"钙\"}', 1, 'B', 'MTHFR基因多态性主要影响叶酸的代谢。', 5, '2025-08-21 22:38:23');
INSERT INTO `question` VALUES (514, 19, '6. 个性化营养干预方案设计是基于什么理念？', '{\"A\": \"所有人都需要相同的营养摄入\", \"B\": \"每个人的营养需求都是独特的\", \"C\": \"营养素的需求与年龄无关\", \"D\": \"营养素的需求与性别无关\"}', 1, 'B', '个性化营养干预方案设计是基于每个人的营养需求都是独特的这一理念。', 5, '2025-08-21 22:38:23');
INSERT INTO `question` VALUES (515, 19, '7. 肠道菌群产生的短链脂肪酸中，哪一种对代谢性疾病有调控作用？', '{\"A\": \"乙酸\", \"B\": \"丙酸\", \"C\": \"丁酸\", \"D\": \"戊酸\"}', 1, 'C', '肠道菌群产生的短链脂肪酸中，丁酸对代谢性疾病有调控作用。', 5, '2025-08-21 22:38:23');
INSERT INTO `question` VALUES (516, 19, '8. 注册营养师在职业活动中应遵循的首要原则是？', '{\"A\": \"追求经济效益\", \"B\": \"诚实守信\", \"C\": \"迎合客户需求\", \"D\": \"推广热门产品\"}', 1, 'B', '诚实守信是注册营养师在职业活动中应遵循的首要原则。', 5, '2025-08-21 22:38:23');
INSERT INTO `question` VALUES (517, 19, '9. 在推广营养补剂时，注册营养师应确保？', '{\"A\": \"补剂价格最低\", \"B\": \"补剂效果立竿见影\", \"C\": \"推广信息真实可靠\", \"D\": \"补剂包装最精美\"}', 1, 'C', '在推广营养补剂时，注册营养师应确保推广信息真实可靠。', 5, '2025-08-21 22:38:23');
INSERT INTO `question` VALUES (518, 19, '10. 跨学科协作中，注册营养师与其他学科专业人员沟通的主要目的是？', '{\"A\": \"展示自己的专业知识\", \"B\": \"独自做出决策\", \"C\": \"共同为客户制定最佳方案\", \"D\": \"忽视其他学科的意见\"}', 1, 'C', '跨学科协作中，注册营养师与其他学科专业人员沟通的主要目的是共同为客户制定最佳方案。', 5, '2025-08-21 22:38:23');
INSERT INTO `question` VALUES (519, 19, '11. 精准营养的研究有助于实现什么目标？', '{\"A\": \"所有人采用相同的饮食模式\", \"B\": \"根据个人基因特征制定营养方案\", \"C\": \"消除所有营养相关疾病\", \"D\": \"降低营养素的生产成本\"}', 1, 'B', '精准营养的研究有助于根据个人基因特征制定营养方案。', 5, '2025-08-21 22:38:23');
INSERT INTO `question` VALUES (520, 19, '12. MTHFR基因突变可能导致什么问题？', '{\"A\": \"维生素C吸收增加\", \"B\": \"叶酸代谢障碍\", \"C\": \"钙吸收减少\", \"D\": \"维生素D合成增加\"}', 1, 'B', 'MTHFR基因突变可能导致叶酸代谢障碍。', 5, '2025-08-21 22:38:23');
INSERT INTO `question` VALUES (521, 19, '13. 个性化营养干预方案需要考虑哪些因素？（多选）', '{\"A\": \"个人的基因特征\", \"B\": \"个人的生活习惯\", \"C\": \"个人的健康状况\", \"D\": \"当前的流行饮食趋势\"}', 2, 'ABC', '个性化营养干预方案需要考虑个人的基因特征、生活习惯和健康状况，而不是当前的流行饮食趋势。', 5, '2025-08-21 22:38:23');
INSERT INTO `question` VALUES (522, 19, '14. 肠道菌群通过产生短链脂肪酸对宿主健康产生积极影响，以下哪项不是短链脂肪酸的作用？（多选）', '{\"A\": \"提供能量\", \"B\": \"调节免疫系统\", \"C\": \"促进脂肪合成\", \"D\": \"改善肠道屏障功能\"}', 2, 'C', '短链脂肪酸的作用包括提供能量、调节免疫系统和改善肠道屏障功能，但不包括促进脂肪合成。', 5, '2025-08-21 22:38:23');
INSERT INTO `question` VALUES (523, 19, '15. 注册营养师在保护客户隐私方面应做到？', '{\"A\": \"随意分享客户信息\", \"B\": \"只在必要时分享客户信息，并征得客户同意\", \"C\": \"将客户信息用于商业推广\", \"D\": \"不保存客户任何信息\"}', 1, 'B', '注册营养师在保护客户隐私方面应做到只在必要时分享客户信息，并征得客户同意。', 5, '2025-08-21 22:38:23');
INSERT INTO `question` VALUES (524, 19, '16. 在营养补剂推广中，以下哪种行为是符合专业责任的？', '{\"A\": \"隐瞒补剂的副作用\", \"B\": \"夸大补剂的功效\", \"C\": \"根据客户情况推荐合适的补剂\", \"D\": \"推荐未经证实的补剂\"}', 1, 'C', '在营养补剂推广中，根据客户情况推荐合适的补剂是符合专业责任的。', 5, '2025-08-21 22:38:23');
INSERT INTO `question` VALUES (525, 19, '17. 跨学科协作中，以下哪种沟通方式是不恰当的？', '{\"A\": \"尊重其他学科的专业意见\", \"B\": \"清晰表达自己的观点\", \"C\": \"忽视其他学科的贡献\", \"D\": \"寻求共同解决方案\"}', 1, 'C', '跨学科协作中，忽视其他学科的贡献是不恰当的沟通方式。', 5, '2025-08-21 22:38:23');
INSERT INTO `question` VALUES (526, 19, '18. 精准营养的实施需要依赖哪些技术？（多选）', '{\"A\": \"基因测序技术\", \"B\": \"营养素检测技术\", \"C\": \"大数据分析技术\", \"D\": \"传统的饮食调查方法\"}', 2, 'ABC', '精准营养的实施需要依赖基因测序技术、营养素检测技术和大数据分析技术，而传统的饮食调查方法虽然有一定价值，但不是精准营养实施的核心技术。', 5, '2025-08-21 22:38:23');
INSERT INTO `question` VALUES (527, 19, '19. MTHFR基因多态性对叶酸代谢的影响主要表现在哪个方面？', '{\"A\": \"增加叶酸的吸收\", \"B\": \"减少叶酸的利用\", \"C\": \"提高叶酸的储存能力\", \"D\": \"不影响叶酸的代谢\"}', 1, 'B', 'MTHFR基因多态性对叶酸代谢的影响主要表现在减少叶酸的利用。', 5, '2025-08-21 22:38:23');
INSERT INTO `question` VALUES (528, 19, '20. 个性化营养干预方案的设计应基于哪些信息？（多选）', '{\"A\": \"个人的遗传信息\", \"B\": \"个人的饮食习惯\", \"C\": \"个人的身体活动水平\", \"D\": \"个人的社会经济状况\"}', 2, 'ABC', '个性化营养干预方案的设计应基于个人的遗传信息、饮食习惯和身体活动水平，虽然社会经济状况可能影响营养摄入，但不是设计个性化方案的核心信息。', 5, '2025-08-21 22:38:23');
INSERT INTO `question` VALUES (529, 19, '21. 肠道菌群产生的丁酸对代谢性疾病的调控机制主要包括哪些方面？（多选）', '{\"A\": \"改善胰岛素敏感性\", \"B\": \"减少炎症反应\", \"C\": \"促进脂肪堆积\", \"D\": \"增强肠道屏障功能\"}', 2, 'ABD', '肠道菌群产生的丁酸对代谢性疾病的调控机制主要包括改善胰岛素敏感性、减少炎症反应和增强肠道屏障功能，而不包括促进脂肪堆积。', 5, '2025-08-21 22:38:23');
INSERT INTO `question` VALUES (530, 19, '22. 注册营养师在职业活动中应如何处理利益冲突？', '{\"A\": \"优先考虑个人利益\", \"B\": \"隐瞒利益冲突\", \"C\": \"公开利益冲突并采取措施避免影响专业判断\", \"D\": \"忽视利益冲突的存在\"}', 1, 'C', '注册营养师在职业活动中应公开利益冲突并采取措施避免影响专业判断。', 5, '2025-08-21 22:38:23');
INSERT INTO `question` VALUES (531, 19, '23. 在推广营养补剂时，注册营养师应避免哪种行为？', '{\"A\": \"提供科学的使用建议\", \"B\": \"夸大补剂的适应症\", \"C\": \"告知客户可能的副作用\", \"D\": \"根据客户情况调整剂量\"}', 1, 'B', '在推广营养补剂时，注册营养师应避免夸大补剂的适应症。', 5, '2025-08-21 22:38:23');
INSERT INTO `question` VALUES (532, 19, '24. 跨学科协作中，注册营养师应如何与其他学科专业人员合作？', '{\"A\": \"主导所有决策\", \"B\": \"忽视其他学科的意见\", \"C\": \"积极参与并贡献自己的专业知识\", \"D\": \"拒绝与其他学科合作\"}', 1, 'C', '跨学科协作中，注册营养师应积极参与并贡献自己的专业知识。', 5, '2025-08-21 22:38:23');
INSERT INTO `question` VALUES (533, 19, '25. 精准营养的研究对于预防哪种疾病具有重要意义？', '{\"A\": \"传染病\", \"B\": \"慢性代谢性疾病\", \"C\": \"外伤\", \"D\": \"遗传性疾病（非营养相关）\"}', 1, 'B', '精准营养的研究对于预防慢性代谢性疾病具有重要意义。', 5, '2025-08-21 22:38:23');
INSERT INTO `question` VALUES (534, 19, '26. MTHFR基因突变与哪种疾病的风险增加有关？', '{\"A\": \"糖尿病\", \"B\": \"神经管缺陷\", \"C\": \"哮喘\", \"D\": \"骨折\"}', 1, 'B', 'MTHFR基因突变与神经管缺陷的风险增加有关。', 5, '2025-08-21 22:38:23');
INSERT INTO `question` VALUES (535, 19, '27. 个性化营养干预方案的效果评估应包括哪些方面？（多选）', '{\"A\": \"生理指标的变化\", \"B\": \"心理状态的变化\", \"C\": \"饮食习惯的改善\", \"D\": \"社会经济状况的改善\"}', 2, 'ABC', '个性化营养干预方案的效果评估应包括生理指标的变化、心理状态的变化和饮食习惯的改善，而社会经济状况的改善不是直接的营养干预效果。', 5, '2025-08-21 22:38:23');
INSERT INTO `question` VALUES (536, 19, '28. 肠道菌群通过产生短链脂肪酸对宿主有哪些益处？（多选）', '{\"A\": \"提供能量来源\", \"B\": \"促进肠道蠕动\", \"C\": \"调节免疫系统\", \"D\": \"降低血液胆固醇水平\"}', 2, 'ACD', '肠道菌群通过产生短链脂肪酸对宿主的益处包括提供能量来源、调节免疫系统和降低血液胆固醇水平，而促进肠道蠕动不是短链脂肪酸的主要作用。', 5, '2025-08-21 22:38:23');
INSERT INTO `question` VALUES (537, 19, '29. 注册营养师在职业活动中应如何维护职业声誉？', '{\"A\": \"通过不正当手段获取客户\", \"B\": \"提供虚假的信息或建议\", \"C\": \"持续学习，提升专业能力\", \"D\": \"忽视客户的反馈\"}', 1, 'C', '注册营养师在职业活动中应通过持续学习，提升专业能力来维护职业声誉。', 5, '2025-08-21 22:38:23');
INSERT INTO `question` VALUES (538, 19, '30. 在推广营养补剂时，以下哪种做法是符合伦理的？', '{\"A\": \"强制客户购买\", \"B\": \"提供虚假宣传材料\", \"C\": \"根据客户健康状况推荐合适的补剂\", \"D\": \"忽视客户的疑问和担忧\"}', 1, 'C', '在推广营养补剂时，根据客户健康状况推荐合适的补剂是符合伦理的做法。', 5, '2025-08-21 22:38:23');
INSERT INTO `question` VALUES (539, 20, '1. 成人必需氨基酸共有几种？', '{\"A\": \"7种\", \"B\": \"8种\", \"C\": \"9种\", \"D\": \"10种\"}', 1, 'B', '成人必需氨基酸包括异亮氨酸、亮氨酸、赖氨酸、蛋氨酸、苯丙氨酸、苏氨酸、色氨酸、缬氨酸共8种。', 5, '2025-08-21 22:38:30');
INSERT INTO `question` VALUES (540, 20, '2. 以下哪种脂肪酸是必需脂肪酸？', '{\"A\": \"油酸\", \"B\": \"亚油酸\", \"C\": \"硬脂酸\", \"D\": \"棕榈酸\"}', 1, 'B', '亚油酸和α-亚麻酸是人体必需脂肪酸，必须从食物中获取。', 5, '2025-08-21 22:38:30');
INSERT INTO `question` VALUES (541, 20, '3. 计算食物血糖生成指数（GI）时，通常以哪种碳水化合物作为参考标准？', '{\"A\": \"果糖\", \"B\": \"葡萄糖\", \"C\": \"蔗糖\", \"D\": \"麦芽糖\"}', 1, 'B', '血糖生成指数（GI）是以葡萄糖为参考标准（GI=100）进行计算的。', 5, '2025-08-21 22:38:30');
INSERT INTO `question` VALUES (542, 20, '4. 根据Harris-Benedict公式，基础代谢率（BMR）估算与以下哪项无关？', '{\"A\": \"年龄\", \"B\": \"身高\", \"C\": \"体重\", \"D\": \"运动量\"}', 1, 'D', 'Harris-Benedict公式主要考虑年龄、性别、身高和体重，运动量影响总能量消耗，但不直接影响基础代谢率估算。', 5, '2025-08-21 22:38:30');
INSERT INTO `question` VALUES (543, 20, '5. 三大产能营养素中，碳水化合物的供能比例通常为多少？', '{\"A\": \"10%-15%\", \"B\": \"20%-30%\", \"C\": \"55%-65%\", \"D\": \"70%-80%\"}', 1, 'C', '碳水化合物是主要的能量来源，其供能比例通常为55%-65%。', 5, '2025-08-21 22:38:30');
INSERT INTO `question` VALUES (544, 20, '6. 以下哪种维生素属于脂溶性维生素？', '{\"A\": \"维生素B1\", \"B\": \"维生素C\", \"C\": \"维生素D\", \"D\": \"维生素B12\"}', 1, 'C', '脂溶性维生素包括维生素A、D、E、K，维生素B1、C、B12属于水溶性维生素。', 5, '2025-08-21 22:38:30');
INSERT INTO `question` VALUES (545, 20, '7. 维生素D缺乏可能导致哪种疾病？', '{\"A\": \"坏血病\", \"B\": \"佝偻病\", \"C\": \"脚气病\", \"D\": \"夜盲症\"}', 1, 'B', '维生素D缺乏会导致儿童佝偻病和成人骨软化症。', 5, '2025-08-21 22:38:30');
INSERT INTO `question` VALUES (546, 20, '8. 钙的主要膳食来源不包括以下哪项？', '{\"A\": \"牛奶\", \"B\": \"豆腐\", \"C\": \"绿叶蔬菜\", \"D\": \"动物肝脏\"}', 1, 'D', '动物肝脏富含铁和维生素A，但不是钙的主要膳食来源。', 5, '2025-08-21 22:38:30');
INSERT INTO `question` VALUES (547, 20, '9. 2025版GB14880中，维生素D强化剂量的上限调整为多少？', '{\"A\": \"5μg/100g\", \"B\": \"10μg/100g\", \"C\": \"15μg/100g\", \"D\": \"20μg/100g\"}', 1, 'C', '2025版GB14880将维生素D强化剂量上限调整为15μg/100g。', 5, '2025-08-21 22:38:30');
INSERT INTO `question` VALUES (548, 20, '10. 蛋白质变性主要影响其哪方面特性？', '{\"A\": \"一级结构\", \"B\": \"二级结构\", \"C\": \"三级结构\", \"D\": \"生物学功能\"}', 2, 'CD', '蛋白质变性主要破坏其高级结构（二级、三级、四级结构），导致生物学功能丧失。', 5, '2025-08-21 22:38:30');
INSERT INTO `question` VALUES (549, 20, '11. 糖酵解的关键酶不包括以下哪项？', '{\"A\": \"己糖激酶\", \"B\": \"磷酸果糖激酶-1\", \"C\": \"丙酮酸激酶\", \"D\": \"柠檬酸合酶\"}', 1, 'D', '柠檬酸合酶是三羧酸循环的关键酶，不属于糖酵解途径。', 5, '2025-08-21 22:38:30');
INSERT INTO `question` VALUES (550, 20, '12. 脂肪酸β-氧化的主要场所是哪里？', '{\"A\": \"细胞质\", \"B\": \"线粒体\", \"C\": \"内质网\", \"D\": \"高尔基体\"}', 1, 'B', '脂肪酸β-氧化主要在线粒体中进行，生成乙酰辅酶A。', 5, '2025-08-21 22:38:30');
INSERT INTO `question` VALUES (551, 20, '13. 鸟氨酸循环的主要生理意义是什么？', '{\"A\": \"合成尿素\", \"B\": \"合成氨基酸\", \"C\": \"分解葡萄糖\", \"D\": \"合成脂肪酸\"}', 1, 'A', '鸟氨酸循环（尿素循环）的主要功能是将氨转化为尿素，从而解毒。', 5, '2025-08-21 22:38:30');
INSERT INTO `question` VALUES (552, 20, '14. 硫胺素（维生素B1）作为辅酶参与以下哪种代谢？', '{\"A\": \"糖酵解\", \"B\": \"三羧酸循环\", \"C\": \"脂肪酸合成\", \"D\": \"磷酸戊糖途径\"}', 1, 'B', '硫胺素以焦磷酸硫胺素（TPP）形式作为辅酶，参与三羧酸循环中的丙酮酸脱氢酶复合体反应。', 5, '2025-08-21 22:38:30');
INSERT INTO `question` VALUES (553, 20, '15. 核黄素（维生素B2）的活性形式是哪种辅酶？', '{\"A\": \"NAD+\", \"B\": \"FAD\", \"C\": \"CoA\", \"D\": \"FMN\"}', 2, 'BD', '核黄素的活性形式为FAD（黄素腺嘌呤二核苷酸）和FMN（黄素单核苷酸），均参与能量代谢。', 5, '2025-08-21 22:38:30');
INSERT INTO `question` VALUES (554, 20, '16. 查询食物营养成分最常用的工具书是哪本？', '{\"A\": \"《中国居民膳食指南》\", \"B\": \"《中国食物成分表》\", \"C\": \"《营养与食品卫生学》\", \"D\": \"《食品添加剂使用标准》\"}', 1, 'B', '《中国食物成分表》是查询食物营养成分的标准工具书。', 5, '2025-08-21 22:38:30');
INSERT INTO `question` VALUES (555, 20, '17. 铁强化酱油的主要适用人群是？', '{\"A\": \"孕妇\", \"B\": \"老年人\", \"C\": \"缺铁性贫血患者\", \"D\": \"所有人群\"}', 1, 'C', '铁强化酱油主要用于预防和控制缺铁性贫血，尤其适用于铁缺乏高风险人群。', 5, '2025-08-21 22:38:30');
INSERT INTO `question` VALUES (556, 20, '18. 以下哪种疾病属于食源性疾病？', '{\"A\": \"高血压\", \"B\": \"糖尿病\", \"C\": \"沙门氏菌感染\", \"D\": \"骨质疏松症\"}', 1, 'C', '沙门氏菌感染是通过食物传播的细菌性食源性疾病。', 5, '2025-08-21 22:38:30');
INSERT INTO `question` VALUES (557, 20, '19. 食品添加剂中山梨酸钾的主要作用是什么？', '{\"A\": \"增味\", \"B\": \"防腐\", \"C\": \"着色\", \"D\": \"营养强化\"}', 1, 'B', '山梨酸钾是一种常用的防腐剂，可抑制霉菌和酵母菌生长。', 5, '2025-08-21 22:38:30');
INSERT INTO `question` VALUES (558, 20, '20. 高温油炸过程中可能生成哪种有害物质？', '{\"A\": \"反式脂肪酸\", \"B\": \"膳食纤维\", \"C\": \"维生素C\", \"D\": \"矿物质\"}', 1, 'A', '高温油炸会导致不饱和脂肪酸部分氢化，生成反式脂肪酸。', 5, '2025-08-21 22:38:30');
INSERT INTO `question` VALUES (559, 20, '21. 巴氏杀菌对维生素C的保留率通常为多少？', '{\"A\": \"30%-50%\", \"B\": \"50%-70%\", \"C\": \"70%-90%\", \"D\": \"90%以上\"}', 1, 'C', '巴氏杀菌（低温长时间或高温短时间）对维生素C的保留率较高，通常为70%-90%。', 5, '2025-08-21 22:38:30');
INSERT INTO `question` VALUES (560, 20, '22. 以下哪种食物属于营养强化食品？', '{\"A\": \"鲜牛奶\", \"B\": \"铁强化面粉\", \"C\": \"天然蜂蜜\", \"D\": \"新鲜水果\"}', 1, 'B', '铁强化面粉是通过人工添加铁元素制成的营养强化食品。', 5, '2025-08-21 22:38:30');
INSERT INTO `question` VALUES (561, 20, '23. 必需氨基酸中，哪种氨基酸对婴幼儿尤为重要？', '{\"A\": \"赖氨酸\", \"B\": \"蛋氨酸\", \"C\": \"组氨酸\", \"D\": \"苯丙氨酸\"}', 1, 'C', '组氨酸是婴幼儿必需氨基酸（成人非必需），因其合成能力不足。', 5, '2025-08-21 22:38:30');
INSERT INTO `question` VALUES (562, 20, '24. 维生素K缺乏可能导致哪种症状？', '{\"A\": \"出血倾向\", \"B\": \"皮肤干燥\", \"C\": \"肌肉无力\", \"D\": \"视力下降\"}', 1, 'A', '维生素K是凝血因子合成的必需辅酶，缺乏会导致凝血障碍和出血倾向。', 5, '2025-08-21 22:38:30');
INSERT INTO `question` VALUES (563, 20, '25. 以下哪种矿物质与血红蛋白合成密切相关？', '{\"A\": \"钙\", \"B\": \"铁\", \"C\": \"锌\", \"D\": \"硒\"}', 1, 'B', '铁是血红蛋白的组成成分，参与氧运输。', 5, '2025-08-21 22:38:30');
INSERT INTO `question` VALUES (564, 20, '26. 糖酵解的终产物是？', '{\"A\": \"丙酮酸\", \"B\": \"乙酰辅酶A\", \"C\": \"乳酸\", \"D\": \"葡萄糖\"}', 1, 'A', '糖酵解在有氧条件下生成丙酮酸，无氧条件下进一步转化为乳酸。', 5, '2025-08-21 22:38:30');
INSERT INTO `question` VALUES (565, 20, '27. 以下哪种维生素与钙吸收直接相关？', '{\"A\": \"维生素A\", \"B\": \"维生素D\", \"C\": \"维生素K\", \"D\": \"维生素C\"}', 1, 'B', '维生素D通过促进肠道钙吸收和骨钙沉积维持血钙平衡。', 5, '2025-08-21 22:38:30');
INSERT INTO `question` VALUES (566, 20, '28. 蛋白质一级结构的主要化学键是？', '{\"A\": \"氢键\", \"B\": \"疏水键\", \"C\": \"肽键\", \"D\": \"盐键\"}', 1, 'C', '蛋白质一级结构由氨基酸通过肽键连接形成。', 5, '2025-08-21 22:38:30');
INSERT INTO `question` VALUES (567, 20, '29. 以下哪种食物富含ω-3多不饱和脂肪酸？', '{\"A\": \"玉米油\", \"B\": \"大豆油\", \"C\": \"亚麻籽油\", \"D\": \"椰子油\"}', 1, 'C', '亚麻籽油富含α-亚麻酸（ω-3系列），可转化为EPA和DHA。', 5, '2025-08-21 22:38:30');
INSERT INTO `question` VALUES (568, 20, '30. 食品添加剂使用时应遵循的原则不包括以下哪项？', '{\"A\": \"必要性原则\", \"B\": \"安全性原则\", \"C\": \"随意添加原则\", \"D\": \"最低用量原则\"}', 1, 'C', '食品添加剂使用需遵循必要性、安全性和最低用量原则，严禁随意添加。', 5, '2025-08-21 22:38:30');
